import { useState, useEffect } from 'react';
import { getReport } from '../services/api';
import { downloadPDF } from '../utils/downloadPDF';
import { T } from '../i18n';

const initials = n => (n||'').split(' ').slice(0,2).map(w=>w[0]||'').join('').toUpperCase()||'?';
const GRADS = [
  ['#1c3370','#3b5bdb'],['#0f766e','#14b8a6'],['#7e22ce','#a855f7'],
  ['#b45309','#f59e0b'],['#be123c','#fb7185'],['#0369a1','#38bdf8'],
  ['#166534','#22c55e'],['#7c2d12','#f97316'],
];
const grad = n => GRADS[(n?.charCodeAt(0)||0)%GRADS.length];
const wIcon = c => {
  const s=(c||'').toLowerCase();
  return s.includes('sun')||s.includes('clear')?'â˜€ï¸':s.includes('cloud')?'â›…':s.includes('rain')?'ðŸŒ§ï¸':s.includes('storm')?'â›ˆï¸':s.includes('snow')?'â„ï¸':'ðŸŒ¤ï¸';
};

const Divider = ({ title, subtitle }) => (
  <div style={{ background:'linear-gradient(150deg,#0a1628 0%,#1c3370 55%,#1e4a9a 100%)', borderRadius:16, padding:'36px 32px', margin:'32px 0 0', position:'relative', overflow:'hidden', display:'flex', flexDirection:'column', alignItems:'center', justifyContent:'center', textAlign:'center', minHeight:140 }}>
    <div style={{ position:'absolute',top:-50,right:-50,width:200,height:200,borderRadius:'50%',background:'rgba(255,255,255,0.04)' }}/>
    <div style={{ color:'white', fontWeight:900, fontSize:22, marginBottom:10, position:'relative' }}>{title}</div>
    <div style={{ width:56, height:3, background:'#b8932a', borderRadius:4, marginBottom:10, position:'relative' }}/>
    {subtitle&&<div style={{ color:'rgba(255,255,255,0.65)', fontSize:13.5, position:'relative' }}>{subtitle}</div>}
  </div>
);

const SecBar = ({ icon, title, accent='#1c3370' }) => (
  <div style={{ background:accent, color:'white', padding:'10px 18px', borderRadius:8, marginBottom:16, display:'flex', alignItems:'center', gap:8, fontSize:13.5, fontWeight:800 }}>
    <span style={{ fontSize:16 }}>{icon}</span>{title}
  </div>
);

const PageTitle = ({ icon, title }) => (
  <div style={{ fontSize:18, fontWeight:900, color:'#1c3370', borderBottom:'2.5px solid #1c3370', paddingBottom:10, marginBottom:20, display:'flex', alignItems:'center', gap:8 }}>
    <span>{icon}</span>{title}
  </div>
);

const Chip = ({ label, value, accent='#1c3370' }) => (
  <div style={{ background:'#f8fafc', border:'1px solid #e2e8f0', borderRadius:10, padding:'11px 14px', borderTop:`3px solid ${accent}` }}>
    <div style={{ fontSize:9.5, fontWeight:700, color:'#94a3b8', textTransform:'uppercase', letterSpacing:.8, marginBottom:5 }}>{label}</div>
    <div style={{ fontSize:13, fontWeight:700, color:'#0f172a', lineHeight:1.4 }}>{value||'â€”'}</div>
  </div>
);

const InfoBox = ({ children, accent='#1c3370', bg='#f0f4ff' }) => (
  <div style={{ background:bg, borderRight:`4px solid ${accent}`, padding:'12px 16px', borderRadius:'0 8px 8px 0', fontSize:13, color:'#334155', lineHeight:1.8, marginBottom:16 }}>{children}</div>
);

const TblHead = ({ cols, bg='#1c3370' }) => (
  <thead><tr style={{ background:bg }}>
    {cols.map(h => <th key={h} style={{ padding:'9px 14px', color:'white', fontWeight:700, textAlign:'right', fontSize:12 }}>{h}</th>)}
  </tr></thead>
);

export default function ReportDetail({ reportId, onBack, onPDFOpen, lang='ar' }) {
  const [report, setReport] = useState(null);
  const [loading, setLoading] = useState(true);
  const t = T[lang];
  const isAr = lang === 'ar';
  const dir = isAr ? 'rtl' : 'ltr';

  useEffect(() => {
    setLoading(true);
    getReport(reportId).then(r => { setReport(r.data); setLoading(false); }).catch(() => setLoading(false));
  }, [reportId]);

  if (loading) return (
    <div style={{ display:'flex', alignItems:'center', justifyContent:'center', height:'60vh', flexDirection:'column', gap:14 }}>
      <div style={{ width:44, height:44, border:'4px solid #e2e8f0', borderTopColor:'#1c3370', borderRadius:'50%', animation:'spin 0.8s linear infinite' }}/>
      <div style={{ color:'#64748b', fontSize:14 }}>{isAr ? 'Ø¬Ø§Ø±ÙŠ ØªØ­Ù…ÙŠÙ„ Ø§Ù„ØªÙ‚Ø±ÙŠØ±...' : 'Loading report...'}</div>
      <style>{`@keyframes spin{to{transform:rotate(360deg)}}`}</style>
    </div>
  );
  if (!report) return null;

  const riskClr = { low:'#22c55e', medium:'#f59e0b', high:'#ef4444' }[report.risk_level||'low'];
  const ci = report.country_info || {};
  const cd = report.conference_data || {};
  const sess = report.sessions || {};

  const card = { background:'white', border:'1px solid #e2e8f0', borderRadius:14, padding:24, marginBottom:0, boxShadow:'0 1px 4px rgba(0,0,0,0.05)' };
  const rowStyle = (i) => ({ background: i%2===0?'white':'#f8fafc', borderBottom:'1px solid #f1f5f9' });

  return (
    <div style={{ background:'#f1f5f9', minHeight:'100vh', fontFamily: isAr ? "'Cairo',sans-serif" : 'inherit', direction:dir }}>

      {/* â”€â”€â”€ STICKY HEADER â”€â”€â”€ */}
      <div style={{ background:'linear-gradient(150deg,#0a1628 0%,#1c3370 55%,#1e4a9a 100%)', padding:'20px 32px', position:'sticky', top:0, zIndex:100, boxShadow:'0 4px 20px rgba(0,0,0,0.3)' }}>
        <div style={{ display:'flex', alignItems:'center', gap:12, marginBottom:14, flexWrap:'wrap', direction:dir }}>
          <button onClick={onBack} style={{ background:'rgba(255,255,255,0.1)', border:'1px solid rgba(255,255,255,0.2)', color:'white', borderRadius:8, padding:'6px 14px', cursor:'pointer', fontSize:13, fontWeight:600, fontFamily:'inherit' }}>
            {t.backToReports}
          </button>
          <span style={{ color:'rgba(255,255,255,0.35)' }}>â–¸</span>
          <span style={{ color:'rgba(255,255,255,0.55)', fontSize:12, flex:1, overflow:'hidden', textOverflow:'ellipsis', whiteSpace:'nowrap' }}>{report.event_name}</span>
          <div style={{ display:'flex', gap:7, flexWrap:'wrap' }}>
            {[
              { bg:riskClr+'25', cl:riskClr, lb:`${(report.risk_level||'low').toUpperCase()} RISK` },
              { bg:report.status==='ready'?'#22c55e22':'#f59e0b22', cl:report.status==='ready'?'#22c55e':'#f59e0b', lb:`â— ${isAr ? (report.status==='ready'?t.ready:t.draft) : (report.status?.charAt(0).toUpperCase()+report.status?.slice(1))}` },
              { bg:'rgba(255,255,255,0.12)', cl:'white', lb:`ðŸ“‹ ${t.official}` },
            ].map(tag => (
              <span key={tag.lb} style={{ background:tag.bg, color:tag.cl, borderRadius:20, padding:'3px 10px', fontSize:11, fontWeight:700, border:`1px solid ${tag.cl}22` }}>{tag.lb}</span>
            ))}
          </div>
        </div>
        <div style={{ display:'flex', alignItems:'flex-start', justifyContent:'space-between', gap:20, direction:dir }}>
          <div>
            <h1 style={{ color:'white', fontWeight:900, fontSize:20, margin:'0 0 5px', lineHeight:1.3 }}>{report.event_name}</h1>
            {report.report_subtitle && <div style={{ color:'rgba(255,255,255,0.6)', fontSize:12.5 }}>{report.report_subtitle}</div>}
            <div style={{ display:'flex', flexWrap:'wrap', gap:7, marginTop:10 }}>
              {[
                { i:'ðŸ“…', v:`${report.start_date}${report.end_date?' â€“ '+report.end_date:''}` },
                { i:'ðŸ“', v:`${report.venue||''}${report.venue?', ':''}${report.city}, ${report.country}` },
                { i:'ðŸ‘¤', v:report.traveler_name },
              ].map(m => (
                <div key={m.v} style={{ display:'flex', alignItems:'center', gap:5, background:'rgba(255,255,255,0.1)', borderRadius:7, padding:'4px 10px', color:'rgba(255,255,255,0.85)', fontSize:12, fontWeight:600, border:'1px solid rgba(255,255,255,0.1)' }}>
                  {m.i} {m.v}
                </div>
              ))}
            </div>
          </div>
          <div style={{ display:'flex', gap:8, flexShrink:0 }}>
            <button onClick={() => onPDFOpen(reportId, report.event_name, 'html')}
              style={{ padding:'9px 16px', background:'rgba(255,255,255,0.1)', color:'white', border:'1px solid rgba(255,255,255,0.2)', borderRadius:9, fontSize:12.5, fontWeight:700, cursor:'pointer', fontFamily:'inherit' }}>
              {t.preview}
            </button>
            <button onClick={() => downloadPDF(reportId, `SFDA_${report.event_name}.pdf`)}
              style={{ padding:'9px 18px', background:'white', color:'#1c3370', border:'none', borderRadius:9, fontSize:12.5, fontWeight:800, cursor:'pointer', fontFamily:'inherit', boxShadow:'0 4px 14px rgba(0,0,0,0.25)' }}>
              {t.exportPDF}
            </button>
          </div>
        </div>
      </div>

      {/* â”€â”€â”€ CONTENT â”€â”€â”€ */}
      <div style={{ padding:'28px 48px 60px', maxWidth:1100, margin:'0 auto' }}>

        {/* 1. Visit Overview */}
        <div style={card}>
          <PageTitle icon="ðŸŽ¯" title={t.visitOverview}/>
          <div style={{ display:'grid', gridTemplateColumns:'repeat(3,1fr)', gap:10, marginBottom:20 }}>
            <Chip label={t.event}       value={report.event_name}     accent="#1c3370"/>
            <Chip label={t.cityCountry} value={`${report.city}, ${report.country}`} accent="#2563eb"/>
            <Chip label={t.dates}       value={`${report.start_date} â€“ ${report.end_date||''}`} accent="#7c3aed"/>
            <Chip label={t.venue}       value={report.venue}          accent="#0f766e"/>
            <Chip label={t.responsible} value={report.traveler_name}  accent="#b45309"/>
            <Chip label={t.position}    value={report.traveler_title} accent="#be123c"/>
          </div>
          <SecBar icon="ðŸŽ¯" title={t.visitObjectives}/>
          {(report.visit_objectives||[]).map((o,i) => (
            <div key={i} style={{ display:'flex', gap:12, alignItems:'flex-start', padding:'9px 0', borderBottom:'1px solid #f1f5f9' }}>
              <div style={{ width:26, height:26, borderRadius:8, background:'linear-gradient(135deg,#1c3370,#2563eb)', color:'white', fontWeight:800, fontSize:11.5, textAlign:'center', lineHeight:'26px', flexShrink:0 }}>{i+1}</div>
              <span style={{ fontSize:13, color:'#334155', lineHeight:1.7, flex:1 }}>{o}</span>
            </div>
          ))}
        </div>

        {/* 2. Country Info */}
        <div style={{ ...card, marginTop:16 }}>
          <PageTitle icon="ðŸŒ" title={`${t.countryInfo} ${report.country}`}/>
          {ci.overview && <InfoBox accent="#1c3370">{ci.overview}</InfoBox>}
          <div style={{ display:'grid', gridTemplateColumns:'repeat(3,1fr)', gap:10, marginBottom:20 }}>
            {[
              [t.population, ci.population],
              [t.area, ci.area],
              [t.gdp, ci.gdp],
            ].map(([label, val]) => (
              <div key={label} style={{ background:'#f0f4ff', border:'1px solid #d0d8f0', borderRadius:9, padding:'12px', textAlign:'center' }}>
                <div style={{ fontSize:15, fontWeight:900, color:'#1c3370', lineHeight:1.2 }}>{val||'â€”'}</div>
                <div style={{ fontSize:9.5, color:'#666', marginTop:4 }}>{label}</div>
              </div>
            ))}
          </div>
          <table style={{ width:'100%', borderCollapse:'collapse', fontSize:13 }}>
            <TblHead cols={[t.countryInfo.split('â€”')[0]||'Ø§Ù„Ø¨Ù†Ø¯', 'Ø§Ù„Ù…Ø¹Ù„ÙˆÙ…Ø© / Info']}/>
            <tbody>
              {[
                [t.capital, ci.capital],
                [t.headOfState, `${ci.head_of_state_title||''} ${ci.head_of_state||''}`.trim()],
                [t.currency, ci.currency],
                [t.language, ci.official_language],
                [t.religion, ci.religion],
                [t.timezone, ci.timezone],
              ].filter(([,v]) => v && v !== 'â€”').map(([k,v], i) => (
                <tr key={k} style={rowStyle(i)}>
                  <td style={{ padding:'10px 14px', color:'#475569', width:'40%' }}>{k}</td>
                  <td style={{ padding:'10px 14px', fontWeight:700, color:'#0f172a' }}>{v}</td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>

        {/* 3. Agenda */}
        <div style={{ ...card, marginTop:16 }}>
          <PageTitle icon="ðŸ“…" title={t.agendaTitle}/>
          {(report.agenda||[]).map((day,di) => (
            <div key={di} style={{ marginBottom:24 }}>
              <div style={{ display:'flex', alignItems:'center', justifyContent:'space-between', background:'#1c3370', color:'white', padding:'9px 14px', borderRadius:8, marginBottom:10 }}>
                <span style={{ fontWeight:800, fontSize:13.5 }}>{day.day_label}</span>
                <span style={{ background:'#b8932a', padding:'2px 10px', borderRadius:10, fontSize:10.5, fontWeight:700 }}>{day.day_label_en}</span>
              </div>
              <table style={{ width:'100%', borderCollapse:'collapse', fontSize:13 }}>
                <TblHead cols={[t.time, t.activity, t.locationCol]} bg="#f8fafc"/>
                <tbody>
                  {(day.items||[]).map((item,ii) => (
                    <tr key={ii} style={rowStyle(ii)}>
                      <td style={{ padding:'10px 12px', fontWeight:800, color:'#1c3370', whiteSpace:'nowrap', fontSize:12, width:'10%' }}>{item.time}</td>
                      <td style={{ padding:'10px 12px', color:'#334155', lineHeight:1.5 }}>{item.activity}</td>
                      <td style={{ padding:'10px 12px', color:'#64748b', fontSize:12, width:'22%' }}>{item.location}</td>
                    </tr>
                  ))}
                </tbody>
              </table>
            </div>
          ))}
        </div>

        {/* 4. Delegation */}
        <div style={{ ...card, marginTop:16 }}>
          <PageTitle icon="ðŸ‘¥" title={t.delegationTitle}/>
          <table style={{ width:'100%', borderCollapse:'collapse', fontSize:13 }}>
            <TblHead cols={['#', t.name, t.title, isAr?'Ø§Ù„Ø¬Ù‡Ø© / Ø§Ù„Ø¥Ø¯Ø§Ø±Ø©':'Department']}/>
            <tbody>
              {(report.delegation||[]).map((m,i) => (
                <tr key={i} style={rowStyle(i)}>
                  <td style={{ padding:'11px 14px', color:'#1c3370', fontWeight:800, width:40, textAlign:'center' }}>{i+1}</td>
                  <td style={{ padding:'11px 14px', fontWeight:700, color:'#0f172a' }}>{m.name}</td>
                  <td style={{ padding:'11px 14px', color:'#334155' }}>{m.title}</td>
                  <td style={{ padding:'11px 14px', color:'#64748b', fontSize:12 }}>{m.department}</td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>

        {/* DIVIDER */}
        <Divider title={report.event_name} subtitle={isAr ? 'Ù…Ø¹Ù„ÙˆÙ…Ø§Øª Ø§Ù„ÙØ¹Ø§Ù„ÙŠØ© ÙˆØ§Ù„Ù…Ø¤ØªÙ…Ø±' : 'Conference Information'}/>

        {/* 5. Conference Data */}
        <div style={{ ...card, marginTop:16 }}>
          <PageTitle icon="ðŸ“Š" title={t.conferenceData}/>
          {cd.slogan && (
            <div style={{ textAlign:'center', background:'linear-gradient(135deg,#1c3370,#1e40af)', color:'white', padding:'13px 24px', borderRadius:10, fontSize:15, fontWeight:800, marginBottom:16, fontStyle:'italic' }}>
              "{cd.slogan}"
            </div>
          )}
          {cd.overview && <InfoBox accent="#b8932a" bg="#fffbf0">{cd.overview}</InfoBox>}
          <table style={{ width:'100%', borderCollapse:'collapse', fontSize:13 }}>
            <TblHead cols={[isAr?'Ø§Ù„Ø¨Ù†Ø¯':'Field', isAr?'Ø§Ù„ØªÙØ§ØµÙŠÙ„':'Details']}/>
            <tbody>
              {[
                [t.organizer, cd.organizer],
                [t.dates, cd.dates],
                [t.location, cd.location],
                [t.expectedParticipants, cd.expected_participants],
                [t.ksaParticipation, cd.ksa_participation],
              ].map(([k,v],i) => (
                <tr key={k} style={rowStyle(i)}>
                  <td style={{ padding:'10px 14px', color:'#475569', width:'35%' }}>{k}</td>
                  <td style={{ padding:'10px 14px', fontWeight:700, color:'#0f172a' }}>{v||'â€”'}</td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>

        {/* 6. Conference Tracks */}
        <div style={{ ...card, marginTop:16 }}>
          <PageTitle icon="ðŸ”·" title={t.conferenceTracks}/>
          <div style={{ display:'grid', gridTemplateColumns:'1fr 1fr', gap:8 }}>
            {(report.conference_tracks||[]).map((track,i) => (
              <div key={i} style={{ display:'flex', alignItems:'center', gap:10, background:'#f5f7ff', border:'1px solid #e0e4f4', borderRadius:9, padding:'9px 12px' }}>
                <div style={{ width:22, height:22, borderRadius:6, background:'#b8932a', color:'white', fontWeight:800, fontSize:10, textAlign:'center', lineHeight:'22px', flexShrink:0 }}>{i+1}</div>
                <span style={{ fontSize:12.5, color:'#334155', fontWeight:600 }}>{track}</span>
              </div>
            ))}
          </div>
        </div>

        {/* 7. Previous Outcomes */}
        {(report.previous_outcomes||[]).length > 0 && (
          <div style={{ ...card, marginTop:16 }}>
            <PageTitle icon="ðŸ“ˆ" title={t.previousOutcomes}/>
            {(report.previous_outcomes||[]).map((o,i) => (
              <div key={i} style={{ background:'#f8fafc', border:'1px solid #e2e8f0', borderRight:'4px solid #1c3370', borderRadius:'0 12px 12px 0', padding:'13px 16px', marginBottom:12 }}>
                <div style={{ fontWeight:800, fontSize:14, color:'#1c3370', marginBottom:6 }}>{t.edition} {o.year}</div>
                <div style={{ fontSize:13, color:'#475569', lineHeight:1.7 }}>{o.summary}</div>
              </div>
            ))}
          </div>
        )}

        {/* DIVIDER */}
        <Divider title={isAr ? 'Ø¬Ù„Ø³Ø§Øª Ø§Ù„ÙØ¹Ø§Ù„ÙŠØ©' : 'Conference Sessions'} subtitle={isAr ? 'Ø§Ù„Ø¨Ø±Ù†Ø§Ù…Ø¬ Ø§Ù„ØªÙØµÙŠÙ„ÙŠ Ù„Ù„Ø¬Ù„Ø³Ø§Øª' : 'Detailed Session Program'}/>

        {/* 8. Sessions */}
        <div style={{ ...card, marginTop:16 }}>
          <PageTitle icon="ðŸ“‹" title={t.sessionsTitle}/>
          {(['day1','day2','day3']).map((dk, di) => {
            const ds = sess[dk]; if (!ds?.length) return null;
            const lbl = isAr
              ? ['Ø§Ù„ÙŠÙˆÙ… Ø§Ù„Ø£ÙˆÙ„','Ø§Ù„ÙŠÙˆÙ… Ø§Ù„Ø«Ø§Ù†ÙŠ','Ø§Ù„ÙŠÙˆÙ… Ø§Ù„Ø«Ø§Ù„Ø«'][di]
              : ['Day 1','Day 2','Day 3'][di];
            return (
              <div key={dk} style={{ marginBottom:24 }}>
                <div style={{ background:'linear-gradient(135deg,#1c3370,#2563eb)', color:'white', padding:'8px 14px', borderRadius:8, fontWeight:800, fontSize:13.5, marginBottom:10 }}>{lbl}</div>
                {ds.map((s,si) => (
                  <div key={si} style={{ background:'white', borderRight:'4px solid #1c3370', border:'1px solid #e0e4f4', borderRadius:'0 8px 8px 0', padding:'11px 14px', marginBottom:8 }}>
                    <div style={{ fontSize:11, color:'#888', fontWeight:700, marginBottom:4 }}>ðŸ• {s.time}</div>
                    <div style={{ fontWeight:800, fontSize:13.5, color:'#1c3370', marginBottom:4, lineHeight:1.4 }}>{s.title}</div>
                    {s.speakers && <div style={{ fontSize:12, color:'#2563eb', marginBottom:4, fontWeight:600 }}>ðŸ‘¤ {s.speakers}</div>}
                    {s.description && <div style={{ fontSize:12, color:'#64748b', lineHeight:1.55 }}>{s.description}</div>}
