import { useState, useEffect } from 'react';
import { T, getLang, setLang as persistLang } from '../i18n';

const NAV = [
  { id: 'dashboard', icon: '‚äû', labelKey: 'dashboard'   },
  { id: 'reports',   icon: '‚ò∞', labelKey: 'reports'     },
  { id: 'new',       icon: '‚äï', labelKey: 'newReport'   },
  { id: 'ai',        icon: '‚ú¶', labelKey: 'aiAssistant' },
];
const NAV_SECTION = {
  dashboard: 'navigation', reports: 'navigation',
  new: 'actions', ai: 'aiTools',
};

export default function Sidebar({ page, setPage, lang: parentLang, setLang: notifyParent }) {
  const [lang, setLangState] = useState(() => getLang());

  useEffect(() => {
    if (parentLang && parentLang !== lang) setLangState(parentLang);
  }, [parentLang]);

  const switchLang = (l) => {
    if (l === lang) return;
    persistLang(l);
    setLangState(l);
    notifyParent?.(l);
  };

  const t    = T[lang];
  const isAr = lang === 'ar';
  const dir  = isAr ? 'rtl' : 'ltr';
  const font = isAr ? "'Cairo','Segoe UI',sans-serif" : "'DM Sans',-apple-system,sans-serif";

  const SECTIONS = [
    { key: 'navigation', items: NAV.filter(n => NAV_SECTION[n.id] === 'navigation') },
    { key: 'actions',    items: NAV.filter(n => NAV_SECTION[n.id] === 'actions')    },
    { key: 'aiTools',    items: NAV.filter(n => NAV_SECTION[n.id] === 'aiTools')    },
  ];

  return (
    <aside style={{
      width: 216, minWidth: 216, height: '100vh',
      background: 'linear-gradient(180deg,#0c1527 0%,#0f1c2e 60%,#0a1520 100%)',
      display: 'flex', flexDirection: 'column',
      // Shadow always faces inward toward main content
      boxShadow: isAr ? '-3px 0 24px rgba(0,0,0,0.4)' : '3px 0 24px rgba(0,0,0,0.4)',
      position: 'relative', zIndex: 10,
      fontFamily: font, direction: dir,
      // NO order, NO transform, NO animation.
      // The outer App container has direction:dir so the sidebar (first in DOM)
      // naturally sits LEFT in LTR and RIGHT in RTL ‚Äî no tricks needed.
    }}>

      {/* ‚îÄ‚îÄ HEADER ‚îÄ‚îÄ */}
      <div style={{
        padding: '16px 14px 14px',
        borderBottom: '1px solid rgba(255,255,255,0.07)',
        display: 'flex', alignItems: 'center', justifyContent: 'space-between', gap: 8,
      }}>
        {/* Brand ‚Äî always LTR */}
        <div style={{ display:'flex', alignItems:'center', gap:9, direction:'ltr', flex:1, minWidth:0 }}>
          <div style={{
            width:36, height:36, borderRadius:10, flexShrink:0,
            background:'linear-gradient(135deg,rgba(59,130,246,0.25),rgba(99,102,241,0.2))',
            border:'1px solid rgba(99,102,241,0.3)',
            display:'flex', alignItems:'center', justifyContent:'center', fontSize:18,
          }}>üåê</div>
          <div style={{ minWidth:0 }}>
            <div style={{ color:'#f1f5f9', fontWeight:800, fontSize:13.5, letterSpacing:'-0.02em', fontFamily:"'DM Sans',sans-serif" }}>
              SFDA Travel
            </div>
            <div style={{ display:'flex', alignItems:'center', gap:4, marginTop:2 }}>
              <span style={{ width:6, height:6, borderRadius:'50%', background:'#22c55e', display:'inline-block', boxShadow:'0 0 6px #22c55e' }}/>
              <span style={{ color:'#475569', fontSize:10, fontFamily:"'DM Sans',sans-serif" }}>AI Online</span>
            </div>
          </div>
        </div>

        {/* Language toggle */}
        <div style={{
          display:'flex', background:'rgba(0,0,0,0.35)', borderRadius:8,
          overflow:'hidden', border:'1px solid rgba(255,255,255,0.1)', flexShrink:0,
        }}>
          {['ar','en'].map(l => (
            <button key={l} onClick={() => switchLang(l)}
              style={{
                padding:'5px 10px', border:'none', cursor:'pointer',
                fontSize: l === 'ar' ? 13 : 10.5, fontWeight:700,
                fontFamily: l === 'ar' ? "'Cairo',sans-serif" : "'DM Sans',sans-serif",
                letterSpacing: l === 'en' ? '0.04em' : 0,
                background: lang === l
                  ? 'linear-gradient(135deg,rgba(59,130,246,0.55),rgba(99,102,241,0.45))'
                  : 'transparent',
                color: lang === l ? '#bfdbfe' : '#3d5571',
                transition: 'all 0.15s',
              }}>
              {l === 'ar' ? 'ÿπ' : 'EN'}
            </button>
          ))}
        </div>
      </div>

      {/* ‚îÄ‚îÄ NAV ‚îÄ‚îÄ */}
      <nav style={{ flex:1, padding:'10px 10px', overflowY:'auto' }}>
        {SECTIONS.map(({ key, items }) => items.length === 0 ? null : (
          <div key={key}>
            <div style={{
              fontSize:9.5, fontWeight:700, color:'#1e3a52',
              letterSpacing:'0.11em', margin:'14px 0 5px',
              paddingInlineStart:6, textAlign: isAr ? 'right' : 'left',
              fontFamily:"'DM Sans',sans-serif",
            }}>
              {t[key]}
            </div>
            {items.map(item => {
              const active = page === item.id;
              return (
                <button key={item.id} onClick={() => setPage(item.id)}
                  style={{
                    width:'100%', display:'flex', alignItems:'center',
                    gap:9, padding:'9px 10px',
                    borderRadius:8, border:'none', marginBottom:2, cursor:'pointer',
                    textAlign: isAr ? 'right' : 'left', direction: dir,
                    fontSize:13.5, fontWeight: active ? 700 : 400, fontFamily: font,
                    background: active
                      ? 'linear-gradient(135deg,rgba(59,130,246,0.18),rgba(99,102,241,0.1))'
                      : 'transparent',
                    color: active ? '#93c5fd' : '#6b8aaa',
                    borderInlineStart: active ? '3px solid #3b82f6' : '3px solid transparent',
                    boxShadow: active ? 'inset 0 0 0 1px rgba(59,130,246,0.12)' : 'none',
                    transition: 'all 0.15s',
                  }}
                  onMouseEnter={e => { if (!active) { e.currentTarget.style.background='rgba(255,255,255,0.05)'; e.currentTarget.style.color='#a0bcd8'; }}}
                  onMouseLeave={e => { if (!active) { e.currentTarget.style.background='transparent'; e.currentTarget.style.color='#6b8aaa'; }}}>
                  <span style={{
                    width:28, height:28, borderRadius:7, flexShrink:0,
                    display:'flex', alignItems:'center', justifyContent:'center',
                    fontSize: item.id === 'ai' ? 11 : 14,
                    background: active ? 'rgba(59,130,246,0.22)' : 'rgba(255,255,255,0.04)',
                    color: active ? '#60a5fa' : '#3d5571', transition:'all 0.15s',
                  }}>{item.icon}</span>
                  <span style={{ flex:1 }}>{t[item.labelKey]}</span>
                  {item.id === 'ai' && (
                    <span style={{
                      fontSize:9, fontWeight:700,
                      background:'linear-gradient(135deg,#7c3aed,#4f46e5)',
                      color:'white', borderRadius:4, padding:'2px 5px',
                      fontFamily:"'DM Sans',sans-serif", letterSpacing:'0.03em',
                    }}>AI</span>
                  )}
                </button>
              );
            })}
          </div>
        ))}
      </nav>

      {/* ‚îÄ‚îÄ DIVIDER ‚îÄ‚îÄ */}
      <div style={{ height:1, background:'linear-gradient(90deg,transparent,rgba(255,255,255,0.07),transparent)', margin:'0 10px' }}/>

      {/* ‚îÄ‚îÄ USER ‚îÄ‚îÄ */}
      <div style={{ padding:'12px 14px', display:'flex', alignItems:'center', gap:10, direction:dir }}>
        <div style={{
          width:34, height:34, borderRadius:'50%', flexShrink:0,
          background:'linear-gradient(135deg,#1c3370,#3b5bdb)',
          display:'flex', alignItems:'center', justifyContent:'center',
          fontSize: isAr ? 14 : 11, fontWeight:800, color:'white',
          position:'relative', fontFamily:"'Cairo',sans-serif",
        }}>
          {isAr ? 'ŸáŸÄ' : 'US'}
          <span style={{ position:'absolute', bottom:1, insetInlineEnd:1, width:8, height:8, background:'#22c55e', borderRadius:'50%', border:'1.5px solid #0c1527' }}/>
        </div>
        <div style={{ flex:1, minWidth:0 }}>
          <div style={{ color:'#dde4ee', fontSize:12, fontWeight:600, overflow:'hidden', textOverflow:'ellipsis', whiteSpace:'nowrap' }}>{t.sysUser}</div>
          <div style={{ color:'#2d4158', fontSize:10 }}>SFDA</div>
        </div>
        <button
          style={{ background:'none', border:'none', color:'#2d4a66', cursor:'pointer', fontSize:14, padding:'2px 4px', borderRadius:5, transition:'color 0.15s' }}
          onMouseEnter={e => e.currentTarget.style.color='#64748b'}
          onMouseLeave={e => e.currentTarget.style.color='#2d4a66'}>
          {isAr ? '‚Üê' : '‚Üí'}
        </button>
      </div>

    </aside>
  );
}
