import { useState, useEffect } from 'react';
import ReactDOM from 'react-dom';
import Sidebar from './components/Sidebar';
import WorldMap from './components/WorldMap';
import NewReportForm from './components/NewReportForm';
import ReportDetail from './components/ReportDetail';
import AIAssistant from './components/AIAssistant';
import { getReports, getStats, deleteReport as apiDelete, getInlinePDFUrl, getPreviewUrl } from './services/api';
import { downloadPDF } from './utils/downloadPDF';
import { T, getLang, setLang as saveLang } from './i18n';

// â”€â”€â”€ PreviewModal â€” receives lang so its buttons translate â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€
function PreviewModal({ modal, onClose, t }) {
  if (!modal) return null;
  return ReactDOM.createPortal(
    <div style={{ position:'fixed', inset:0, zIndex:99999, display:'flex', flexDirection:'column', background:'rgba(0,0,0,0.85)' }}>
      <div style={{ background:'#0d1829', padding:'12px 20px', display:'flex', alignItems:'center', justifyContent:'space-between', flexShrink:0, borderBottom:'1px solid rgba(255,255,255,0.1)' }}>
        <span style={{ color:'white', fontSize:14, fontWeight:700 }}>
          {modal.type === 'pdf' ? 'ðŸ“„' : 'ðŸ‘ï¸'} {modal.title}
        </span>
        <div style={{ display:'flex', gap:10 }}>
          <button onClick={() => downloadPDF(modal.reportId, `SFDA_${modal.title || modal.reportId}.pdf`)}
            style={{ padding:'8px 18px', background:'#3b82f6', color:'white', borderRadius:8, fontSize:12, fontWeight:700, border:'none', cursor:'pointer', display:'flex', alignItems:'center', gap:6 }}>
            {t.downloadPDF}
          </button>
          <button onClick={onClose}
            style={{ padding:'8px 18px', background:'rgba(255,255,255,0.12)', color:'white', border:'1px solid rgba(255,255,255,0.2)', borderRadius:8, fontSize:12, fontWeight:700, cursor:'pointer' }}>
            {t.closeBtn}
          </button>
        </div>
      </div>
      <div style={{ flex:1, overflow:'hidden', background:'white' }}>
        <iframe src={modal.url} title={t.preview} style={{ width:'100%', height:'100%', border:'none', display:'block' }} />
      </div>
    </div>,
    document.body
  );
}

function Toast({ toast }) {
  if (!toast) return null;
  return ReactDOM.createPortal(
    <div style={{ position:'fixed', top:20, right:24, zIndex:99998, background:toast.color, color:'white', borderRadius:10, padding:'12px 20px', fontSize:13, fontWeight:600, boxShadow:'0 6px 24px rgba(0,0,0,0.25)', animation:'toastIn 0.25s ease', maxWidth:400 }}>
      {toast.msg}
    </div>,
    document.body
  );
}

// â”€â”€â”€ Reports page extracted as proper component (hooks can't be inside IIFE) â”€
function ReportsPage({ reports, lang, t, isAr, dir, setPage, openPreview, handleDelete, openDetail }) {
  const [search,       setSearch]       = useState('');
  const [statusFilter, setStatusFilter] = useState('all');

  const filtered = reports.filter(r => {
    const q = search.toLowerCase();
    const matchSearch = !q
      || (r.title || r.event_name || '').toLowerCase().includes(q)
      || (r.city  || '').toLowerCase().includes(q)
      || (r.country || '').toLowerCase().includes(q);
    const matchStatus = statusFilter === 'all' || r.status === statusFilter;
    return matchSearch && matchStatus;
  });

  const counts = {
    all:   reports.length,
    ready: reports.filter(r => r.status === 'ready').length,
    draft: reports.filter(r => r.status === 'draft').length,
  };

  const filterPills = [
    { key:'all',   label:`${t.filterAll} (${counts.all})` },
    { key:'ready', label:`${t.ready} (${counts.ready})`   },
    { key:'draft', label:`${t.draft} (${counts.draft})`   },
  ];

  const resultText = lang === 'ar'
    ? `${filtered.length} ${t.results}`
    : `${filtered.length} ${t.results}${filtered.length !== 1 ? 's' : ''}`;

  const font = isAr ? "'Cairo','Segoe UI',sans-serif" : "'DM Sans',-apple-system,sans-serif";

  return (
    <>
      {/* â”€â”€ Header â”€â”€ */}
      <div style={{ display:'flex', justifyContent:'space-between', alignItems:'flex-start', marginBottom:22, direction:dir }}>
        <div>
          <h2 style={{ margin:0, fontSize:22, fontWeight:800, color:'#0d1829', fontFamily:font }}>{t.reportsTitle}</h2>
          <p style={{ margin:'4px 0 0', fontSize:13, color:'#94a3b8', fontFamily:font }}>{t.reportsSubtitle}</p>
        </div>
        <button onClick={() => setPage('new')}
          style={{ display:'flex', alignItems:'center', gap:8, padding:'10px 22px', background:'linear-gradient(135deg,#1c3370,#2d4fa6)', color:'white', border:'none', borderRadius:10, fontSize:13.5, fontWeight:700, cursor:'pointer', boxShadow:'0 4px 14px rgba(28,51,112,0.35)', whiteSpace:'nowrap', fontFamily:font }}>
          + {t.newReport}
        </button>
      </div>

      {/* â”€â”€ Search + filter bar â”€â”€ */}
      <div style={{ background:'white', borderRadius:14, border:'1px solid #e8edf4', padding:'16px 18px', marginBottom:20, boxShadow:'0 1px 6px rgba(0,0,0,0.04)' }}>
        <div style={{ position:'relative', marginBottom:14 }}>
          <span style={{ position:'absolute', [isAr?'right':'left']:14, top:'50%', transform:'translateY(-50%)', color:'#94a3b8', fontSize:16, pointerEvents:'none' }}>ðŸ”</span>
          <input
            value={search}
            onChange={e => setSearch(e.target.value)}
            placeholder={t.searchPlaceholder}
            style={{
              width:'100%', padding:`11px ${isAr?'44px':'16px'} 11px ${isAr?'16px':'44px'}`,
              border:'1.5px solid #e2e8f0', borderRadius:10,
              fontSize:13.5, color:'#1e293b', background:'#f8fafc',
              outline:'none', boxSizing:'border-box',
              direction:dir, fontFamily:font, transition:'border-color 0.15s',
            }}
            onFocus={e => e.target.style.borderColor = '#3b82f6'}
            onBlur={e  => e.target.style.borderColor = '#e2e8f0'}
          />
          {search && (
            <button onClick={() => setSearch('')}
              style={{ position:'absolute', [isAr?'left':'right']:12, top:'50%', transform:'translateY(-50%)', background:'#e2e8f0', border:'none', borderRadius:'50%', width:20, height:20, cursor:'pointer', display:'flex', alignItems:'center', justifyContent:'center', fontSize:11, color:'#64748b' }}>
              âœ•
            </button>
          )}
        </div>

        <div style={{ display:'flex', alignItems:'center', gap:8, direction:dir, flexWrap:'wrap' }}>
          <span style={{ fontSize:12, color:'#94a3b8', fontWeight:600, marginInlineEnd:4, fontFamily:font }}>
            {t.filterLabel}
          </span>
          {filterPills.map(pill => {
            const active = statusFilter === pill.key;
            const activeBg    = pill.key==='ready' ? 'linear-gradient(135deg,#dcfce7,#bbf7d0)' : pill.key==='draft' ? 'linear-gradient(135deg,#fef9c3,#fde68a)' : 'linear-gradient(135deg,#eff6ff,#dbeafe)';
            const activeColor = pill.key==='ready' ? '#15803d'  : pill.key==='draft' ? '#a16207'  : '#1d4ed8';
            const activeBdr   = pill.key==='ready' ? '#86efac'  : pill.key==='draft' ? '#fde68a'  : '#93c5fd';
            return (
              <button key={pill.key} onClick={() => setStatusFilter(pill.key)}
                style={{ padding:'5px 14px', borderRadius:20, cursor:'pointer', fontSize:12.5, fontFamily:font,
                  fontWeight: active ? 700 : 500,
                  background: active ? activeBg    : '#f1f5f9',
                  color:      active ? activeColor : '#64748b',
                  border:     active ? `1.5px solid ${activeBdr}` : '1.5px solid transparent',
                  transition:'all 0.15s',
                }}>
                {pill.label}
              </button>
            );
          })}
          <span style={{ marginInlineStart:'auto', fontSize:12, color:'#94a3b8', fontFamily:font }}>
            {resultText}
          </span>
        </div>
      </div>

      {/* â”€â”€ Cards â”€â”€ */}
      {filtered.length === 0 ? (
        <div style={{ textAlign:'center', padding:'72px 20px', color:'#94a3b8', background:'white', borderRadius:16, border:'1px solid #e2e8f0', direction:dir }}>
          <div style={{ fontSize:42, marginBottom:14 }}>ðŸ“‹</div>
          <div style={{ fontSize:15, fontWeight:600, marginBottom:12, color:'#64748b', fontFamily:font }}>
            {reports.length === 0 ? t.noReports : t.noMatchingReports}
          </div>
          {reports.length === 0 ? (
            <button onClick={() => setPage('new')} style={{ background:'none', border:'none', color:'#3b82f6', cursor:'pointer', fontWeight:700, fontSize:14, fontFamily:font }}>
              {t.createFirst}
            </button>
          ) : (
            <button onClick={() => { setSearch(''); setStatusFilter('all'); }}
              style={{ background:'none', border:'none', color:'#3b82f6', cursor:'pointer', fontWeight:700, fontSize:13, fontFamily:font }}>
              {t.clearFilters}
            </button>
          )}
        </div>
      ) : (
        <div style={{ display:'grid', gridTemplateColumns:'repeat(auto-fill, minmax(310px,1fr))', gap:16 }}>
          {filtered.map(r => (
            <div key={r.id} onClick={() => openDetail(r.id)}
              style={{ background:'white', borderRadius:16, border:'1px solid #e8edf4', padding:'20px', cursor:'pointer', position:'relative', boxShadow:'0 1px 4px rgba(0,0,0,0.05)', transition:'box-shadow 0.18s,transform 0.18s', direction:dir }}
              onMouseEnter={e => { e.currentTarget.style.boxShadow='0 8px 24px rgba(0,0,0,0.11)'; e.currentTarget.style.transform='translateY(-3px)'; }}
              onMouseLeave={e => { e.currentTarget.style.boxShadow='0 1px 4px rgba(0,0,0,0.05)'; e.currentTarget.style.transform='translateY(0)'; }}>

              {/* Accent bar */}
              <div style={{ position:'absolute', top:0, insetInlineStart:0, insetInlineEnd:0, height:3, borderRadius:'16px 16px 0 0', background: r.status==='ready' ? 'linear-gradient(90deg,#22c55e,#16a34a)' : 'linear-gradient(90deg,#f59e0b,#d97706)' }} />

              {/* Top row */}
              <div style={{ display:'flex', alignItems:'flex-start', justifyContent:'space-between', marginBottom:14 }}>
                <div style={{ width:44, height:44, background:'linear-gradient(135deg,#eff6ff,#dbeafe)', borderRadius:12, display:'flex', alignItems:'center', justifyContent:'center', fontSize:22, border:'1px solid #bfdbfe' }}>ðŸ“„</div>
                <div style={{ display:'flex', alignItems:'center', gap:7 }}>
                  <span style={{ background:r.status==='ready'?'#f0fdf4':'#fef9c3', color:r.status==='ready'?'#15803d':'#a16207', border:`1px solid ${r.status==='ready'?'#86efac':'#fde68a'}`, borderRadius:20, padding:'3px 12px', fontSize:11, fontWeight:700, fontFamily:font }}>
                    {r.status === 'ready' ? t.ready : t.draft}
                  </span>
                  <button onClick={e => handleDelete(r.id, e)}
                    style={{ background:'none', border:'none', cursor:'pointer', color:'#cbd5e1', fontSize:16, lineHeight:1, padding:'2px 5px', borderRadius:5, transition:'color 0.15s' }}
                    onMouseEnter={e => e.currentTarget.style.color='#ef4444'}
                    onMouseLeave={e => e.currentTarget.style.color='#cbd5e1'}>âœ•</button>
                </div>
              </div>

              {/* Title */}
              <div style={{ fontWeight:800, fontSize:14.5, color:'#0f172a', marginBottom:10, lineHeight:1.4, fontFamily:font }}>
                {r.ai_generated && (
                  <span style={{ fontSize:9.5, background:'linear-gradient(135deg,#eff6ff,#dbeafe)', color:'#3b82f6', borderRadius:5, padding:'2px 7px', fontWeight:700, marginInlineEnd:7, fontFamily:"'DM Sans',sans-serif" }}>
                    {t.aiGenLabel}
                  </span>
                )}
                {r.title || r.event_name}
              </div>

              {/* Meta */}
              <div style={{ display:'flex', flexDirection:'column', gap:6, marginBottom:16 }}>
                <div style={{ display:'flex', alignItems:'center', gap:7, fontSize:12.5, color:'#2563eb', fontWeight:500 }}>
                  <span>ðŸ“</span>
                  <span style={{ fontFamily:"'DM Sans',sans-serif" }}>{r.city}{r.country ? `, ${r.country}` : ''}</span>
                </div>
                <div style={{ display:'flex', alignItems:'center', gap:7, fontSize:12.5, color:'#64748b' }}>
                  <span>ðŸ“…</span>
                  <span style={{ fontFamily:"'DM Sans',sans-serif" }}>{r.start_date}{r.end_date && r.end_date !== r.start_date ? ` â†’ ${r.end_date}` : ''}</span>
                </div>
              </div>

              {/* Footer */}
              <div style={{ borderTop:'1px solid #f1f5f9', paddingTop:12, display:'flex', alignItems:'center', justifyContent:'space-between' }}>
                <span style={{ fontSize:11, color:'#94a3b8', fontFamily:"'DM Sans',sans-serif" }}>
                  {r.created_at ? new Date(r.created_at).toLocaleDateString(lang === 'ar' ? 'ar-SA' : 'en-US', { month:'short', day:'numeric', year:'numeric' }) : 'â€”'}
                </span>
                <div style={{ display:'flex', gap:6 }} onClick={e => e.stopPropagation()}>
                  <button
                    onClick={e => { e.stopPropagation(); openPreview(r.id, r.title||r.event_name, 'html'); }}
                    style={{ padding:'5px 11px', background:'#f8fafc', color:'#475569', border:'1px solid #e2e8f0', borderRadius:7, fontSize:11.5, fontWeight:600, cursor:'pointer', transition:'all 0.15s' }}
                    onMouseEnter={e => { e.currentTarget.style.background='#f1f5f9'; e.currentTarget.style.borderColor='#cbd5e1'; }}
                    onMouseLeave={e => { e.currentTarget.style.background='#f8fafc'; e.currentTarget.style.borderColor='#e2e8f0'; }}>
                    {t.preview}
                  </button>
                  <button
                    onClick={e => { e.stopPropagation(); downloadPDF(r.id, `SFDA_${r.event_name || r.id}.pdf`); }}
                    style={{ padding:'5px 11px', background:'#eff6ff', color:'#2563eb', border:'1px solid #bfdbfe', borderRadius:7, fontSize:11.5, fontWeight:700, cursor:'pointer', transition:'all 0.15s' }}
                    onMouseEnter={e => { e.currentTarget.style.background='#dbeafe'; }}
                    onMouseLeave={e => { e.currentTarget.style.background='#eff6ff'; }}>
                    {t.exportPDF}
                  </button>
                </div>
              </div>
            </div>
          ))}
        </div>
      )}
    </>
  );
}

// â”€â”€â”€ Main App â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€
export default function App() {
  const [page,         setPageRaw]      = useState('dashboard');
  const [selectedReport, setSelected]   = useState(null);
  const [reports,      setReports]      = useState([]);
  const [stats,        setStats]        = useState({ total:0, ready:0, draft:0 });
  const [toast,        setToast]        = useState(null);
  const [loading,      setLoading]      = useState(true);
  const [previewModal, setPreviewModal] = useState(null);
  const [lang,         setLangState]    = useState(getLang());

  const setLang = (l) => { saveLang(l); setLangState(l); };
  const setPage = (p) => { setPageRaw(p); };
  const t    = T[lang];
  const isAr = lang === 'ar';
  const dir  = isAr ? 'rtl' : 'ltr';
  const font = isAr ? "'Cairo','Segoe UI',sans-serif" : "'DM Sans',-apple-system,sans-serif";

  const showToast = (msg, color='#22c55e') => {
    setToast({ msg, color });
    setTimeout(() => setToast(null), 3500);
  };

  const openPreview = (reportId, title, type) => {
    const url = type === 'pdf' ? getInlinePDFUrl(reportId) : getPreviewUrl(reportId);
    setPreviewModal({ url, reportId, title, type });
  };

  const loadData = async () => {
    try {
      const [rRes, sRes] = await Promise.all([getReports(), getStats()]);
      setReports(rRes.data);
      setStats(sRes.data);
    } catch (e) { console.error(e); }
    finally { setLoading(false); }
  };

  useEffect(() => { loadData(); }, []);

  const handleNewReport = (report) => {
    loadData();
    showToast(`âœ“ "${report.title || report.event_name}" â€” ${t.reportCreated}`);
    setPage('reports');
  };

  const handleDelete = async (id, e) => {
    e?.stopPropagation();
    if (!window.confirm(t.deleteConfirm)) return;
    try {
      await apiDelete(id);
      setReports(prev => prev.filter(r => r.id !== id));
      setStats(prev => ({ ...prev, total: Math.max(0, prev.total - 1) }));
      if (selectedReport === id) { setSelected(null); setPage('reports'); }
      showToast(t.reportDeleted, '#f59e0b');
    } catch { showToast(t.deleteFailed, '#ef4444'); }
  };

  const openDetail = (id) => { setSelected(id); setPage('detail'); };

  const upcoming   = reports.filter(r => r.status === 'ready');
  const statCards  = [
    { val:stats.total, label:t.totalReports, bg:'#eff6ff', ib:'#dbeafe', ic:'#3b82f6', t:'doc'   },
    { val:stats.ready, label:t.readyReports,  bg:'#f0fdf4', ib:'#dcfce7', ic:'#22c55e', t:'check' },
    { val:stats.draft, label:t.draftReports,  bg:'#fffbeb', ib:'#fef9c3', ic:'#f59e0b', t:'clock' },
  ];

  const pageTitle = {
    new:       t.pageTitleNew,
    reports:   t.pageTitleReports,
    detail:    t.pageTitleDetail,
    dashboard: t.pageTitleDashboard,
  };
  const pageSubtitle = {
    new:       t.pageSubNew,
    reports:   `${reports.length} ${lang==='ar' ? t.results : `${t.results}${reports.length!==1?'s':''}`}`,
    detail:    '',
    dashboard: t.pageSubDashboard,
  };

  // t.feed and t.who are already translated arrays in i18n.js
  const FEED = t.feed;
  const WHO  = t.who;

  return (
    <div style={{ display:'flex', height:'100vh', fontFamily:font, background:'#f1f5f9', overflow:'hidden' }}>
      <style>{`
        @keyframes toastIn { from{opacity:0;transform:translateY(-10px)} to{opacity:1;transform:none} }
        @keyframes spin    { to{transform:rotate(360deg)} }
        * { box-sizing:border-box }
        ::-webkit-scrollbar{width:5px} ::-webkit-scrollbar-track{background:transparent} ::-webkit-scrollbar-thumb{background:#e2e8f0;border-radius:3px}
      `}</style>

      <Toast toast={toast} />
      <PreviewModal modal={previewModal} onClose={() => setPreviewModal(null)} t={t} />
      <Sidebar page={page} setPage={p => { setPage(p); setSelected(null); }} lang={lang} setLang={setLang} />

      <div style={{ flex:1, display:'flex', flexDirection:'column', overflow:'hidden' }}>

        {/* â”€â”€ Top header bar â”€â”€ */}
        {page !== 'ai' && (
          <div style={{ background:'white', borderBottom:'1px solid #e9eef5', padding:'17px 28px 15px', display:'flex', justifyContent:'space-between', alignItems:'flex-start', flexShrink:0, direction:dir, fontFamily:font }}>
            <div>
              <h1 style={{ margin:0, fontSize:24, fontWeight:700, color:'#0d1829', letterSpacing:isAr?0:'-0.025em' }}>{pageTitle[page]}</h1>
              <p style={{ margin:'4px 0 0', fontSize:12.5, color:'#94a3b8' }}>{pageSubtitle[page]}</p>
            </div>
            {page !== 'new' && (
              <button onClick={() => setPage('new')}
                style={{ display:'flex', alignItems:'center', gap:7, padding:'9px 18px', background:'#0d1829', color:'white', border:'none', borderRadius:8, fontSize:13, fontWeight:600, cursor:'pointer', whiteSpace:'nowrap', fontFamily:font }}
                onMouseEnter={e => e.currentTarget.style.background='#1e293b'}
                onMouseLeave={e => e.currentTarget.style.background='#0d1829'}>
                + {t.newReport}
              </button>
            )}
          </div>
        )}

        {/* â”€â”€ AI page â”€â”€ */}
        {page === 'ai' && (
          <div style={{ flex:1, display:'flex', flexDirection:'column', overflow:'hidden' }}>
            <AIAssistant reports={reports} />
          </div>
        )}

        {/* â”€â”€ Main scroll area â”€â”€ */}
        {page !== 'ai' && (
          <div style={{ flex:1, overflowY:'auto', padding: page==='new' ? '28px 32px 50px' : '20px 28px 32px' }}>

            {/* â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â• DASHBOARD â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â• */}
            {page === 'dashboard' && (
              <>
                {/* Stat cards */}
                <div style={{ display:'grid', gridTemplateColumns:'repeat(3,1fr)', gap:14, marginBottom:18, direction:dir }}>
                  {statCards.map((s, i) => (
                    <div key={i} style={{ background:s.bg, borderRadius:12, padding:'16px 20px', display:'flex', alignItems:'center', gap:15, border:'1px solid rgba(0,0,0,0.04)', transition:'transform 0.15s,box-shadow 0.15s', cursor:'default' }}
                      onMouseEnter={e => { e.currentTarget.style.transform='translateY(-1px)'; e.currentTarget.style.boxShadow='0 4px 12px rgba(0,0,0,0.08)'; }}
                      onMouseLeave={e => { e.currentTarget.style.transform='none'; e.currentTarget.style.boxShadow='none'; }}>
                      <div style={{ width:44, height:44, borderRadius:11, background:s.ib, display:'flex', alignItems:'center', justifyContent:'center', flexShrink:0 }}>
                        {s.t==='doc'   && <svg width="20" height="20" viewBox="0 0 24 24" fill="none"><rect x="3" y="4" width="18" height="16" rx="2" stroke={s.ic} strokeWidth="2"/><line x1="8" y1="9" x2="16" y2="9" stroke={s.ic} strokeWidth="2" strokeLinecap="round"/><line x1="8" y1="13" x2="13" y2="13" stroke={s.ic} strokeWidth="2" strokeLinecap="round"/></svg>}
                        {s.t==='check' && <svg width="20" height="20" viewBox="0 0 24 24" fill="none"><circle cx="12" cy="12" r="9" stroke={s.ic} strokeWidth="2"/><path d="M8 12l3 3 5-5" stroke={s.ic} strokeWidth="2" strokeLinecap="round" strokeLinejoin="round"/></svg>}
                        {s.t==='clock' && <svg width="20" height="20" viewBox="0 0 24 24" fill="none"><circle cx="12" cy="12" r="9" stroke={s.ic} strokeWidth="2"/><path d="M12 7v5l3 2" stroke={s.ic} strokeWidth="2" strokeLinecap="round"/></svg>}
                      </div>
                      <div>
                        <div style={{ fontSize:30, fontWeight:700, color:'#0d1829', lineHeight:1 }}>{loading ? 'â€¦' : s.val}</div>
                        <div style={{ fontSize:12, color:'#64748b', marginTop:4, fontFamily:font }}>{s.label}</div>
                      </div>
                    </div>
                  ))}
                </div>

                {/* Map */}
                <div style={{ background:'white', borderRadius:14, boxShadow:'0 1px 4px rgba(0,0,0,0.06)', overflow:'hidden', border:'1px solid #edf2f7', marginBottom:18 }}>
                  <div style={{ padding:'13px 20px', display:'flex', justifyContent:'space-between', alignItems:'center', borderBottom:'1px solid #f1f5f9', direction:dir }}>
                    <div style={{ display:'flex', alignItems:'center', gap:10 }}>
                      <div style={{ width:30, height:30, borderRadius:8, background:'#eff6ff', display:'flex', alignItems:'center', justifyContent:'center' }}>
                        <svg width="16" height="16" viewBox="0 0 24 24" fill="none"><circle cx="12" cy="12" r="9" stroke="#3b82f6" strokeWidth="2"/><ellipse cx="12" cy="12" rx="4" ry="9" stroke="#3b82f6" strokeWidth="1.5"/><line x1="3" y1="9" x2="21" y2="9" stroke="#3b82f6" strokeWidth="1.5"/><line x1="3" y1="15" x2="21" y2="15" stroke="#3b82f6" strokeWidth="1.5"/></svg>
                      </div>
                      <div>
                        <div style={{ fontWeight:700, fontSize:14.5, color:'#0d1829', fontFamily:font }}>{t.mapTitle}</div>
                        <div style={{ fontSize:11, color:'#94a3b8', marginTop:1, fontFamily:font }}>{t.mapSubtitle}</div>
                      </div>
                    </div>
                    <div style={{ display:'flex', alignItems:'center', gap:14 }}>
                      {[['#22c55e', t.riskLow], ['#f59e0b', t.riskMedium], ['#ef4444', t.riskHigh]].map(([c, l]) => (
                        <span key={l} style={{ display:'flex', alignItems:'center', gap:5, fontSize:12, color:'#64748b', fontFamily:font }}>
                          <span style={{ width:8, height:8, borderRadius:'50%', background:c, display:'inline-block' }}/>{l}
                        </span>
                      ))}
                    </div>
                  </div>
                  <WorldMap upcomingReports={upcoming} />
                </div>

                {/* Intel Feed + WHO */}
                <div style={{ display:'grid', gridTemplateColumns:'1fr 1fr', gap:16, marginBottom:20, direction:dir }}>
                  {/* Intel feed */}
                  <div style={{ background:'white', borderRadius:14, padding:'18px 20px', border:'1px solid #edf2f7' }}>
                    <div style={{ display:'flex', alignItems:'center', gap:10, marginBottom:14 }}>
                      <div style={{ width:30, height:30, borderRadius:8, background:'#eff6ff', display:'flex', alignItems:'center', justifyContent:'center', fontSize:14 }}>ðŸ“°</div>
                      <span style={{ fontWeight:700, fontSize:15, color:'#0d1829', fontFamily:font }}>{t.intelFeed}</span>
                    </div>
                    {FEED.map((item, i) => (
                      <div key={i} style={{ display:'flex', gap:12, padding:'12px 0', borderBottom: i < FEED.length-1 ? '1px solid #f1f5f9' : 'none', alignItems:'flex-start' }}>
                        <div style={{ width:36, height:36, borderRadius:9, background:item.iconBg, display:'flex', alignItems:'center', justifyContent:'center', fontSize:16, flexShrink:0 }}>{item.icon}</div>
                        <div style={{ flex:1, minWidth:0 }}>
                          <div style={{ fontWeight:600, fontSize:13, color:'#0d1829', marginBottom:2, lineHeight:1.35, fontFamily:font }}>{item.title}</div>
                          <div style={{ fontSize:12, color:'#64748b', marginBottom:4, fontFamily:font }}>{item.summary}</div>
                          <div style={{ fontSize:11, color:'#94a3b8', fontFamily:"'DM Sans',sans-serif" }}>{item.source} Â· {item.time}</div>
                        </div>
                        {item.impact && (
                          <span style={{ border:'1.5px solid #22c55e', color:'#16a34a', borderRadius:20, padding:'3px 9px', fontSize:10, fontWeight:700, whiteSpace:'nowrap', flexShrink:0, fontFamily:font }}>
                            {item.impact}
                          </span>
                        )}
                      </div>
                    ))}
                  </div>

                  {/* WHO */}
                  <div style={{ background:'white', borderRadius:14, padding:'18px 20px', border:'1px solid #edf2f7' }}>
                    <div style={{ display:'flex', alignItems:'center', gap:10, marginBottom:14 }}>
                      <div style={{ width:30, height:30, borderRadius:8, background:'#fffbeb', display:'flex', alignItems:'center', justifyContent:'center', fontSize:14 }}>âš ï¸</div>
                      <span style={{ fontWeight:700, fontSize:15, color:'#0d1829', fontFamily:font }}>{t.whoAlerts}</span>
                    </div>
                    {WHO.map((item, i) => (
                      <div key={i} style={{ paddingBottom:16, borderBottom: i < WHO.length-1 ? '1px solid #f1f5f9' : 'none', marginBottom: i < WHO.length-1 ? 16 : 0 }}>
                        <span style={{ background:item.typeColor, color:'white', borderRadius:6, padding:'2px 10px', fontSize:11, fontWeight:700, fontFamily:font }}>{item.type}</span>
                        <div style={{ fontWeight:700, fontSize:14, color:'#0d1829', margin:'7px 0 4px', fontFamily:font }}>{item.title}</div>
                        <div style={{ fontSize:12, color:'#64748b', marginBottom:7, fontFamily:font }}>{item.summary}</div>
                        <div style={{ display:'flex', flexWrap:'wrap', gap:5 }}>
                          {item.tags.map(tag => (
                            <span key={tag} style={{ background:'#f1f5f9', color:'#475569', borderRadius:6, padding:'2px 9px', fontSize:11, fontFamily:font }}>{tag}</span>
                          ))}
                        </div>
                      </div>
                    ))}
                  </div>
                </div>

                {/* Recent reports */}
                <div>
                  <div style={{ display:'flex', justifyContent:'space-between', alignItems:'center', marginBottom:14, direction:dir }}>
                    <div style={{ fontWeight:700, fontSize:17, color:'#0d1829', fontFamily:font }}>{t.recentReports}</div>
                    <button onClick={() => setPage('reports')} style={{ background:'none', border:'none', cursor:'pointer', color:'#3b82f6', fontSize:13, fontWeight:600, fontFamily:font }}>{t.viewAll}</button>
                  </div>
                  {reports.slice(0, 5).map(r => (
                    <div key={r.id} onClick={() => openDetail(r.id)}
                      style={{ background:'white', borderRadius:12, padding:'14px 18px', display:'flex', alignItems:'center', gap:14, border:'1px solid #edf2f7', cursor:'pointer', transition:'all 0.15s', marginBottom:10, direction:dir }}
                      onMouseEnter={e => { e.currentTarget.style.boxShadow='0 4px 14px rgba(0,0,0,0.09)'; e.currentTarget.style.transform=isAr?'translateX(-2px)':'translateX(2px)'; }}
                      onMouseLeave={e => { e.currentTarget.style.boxShadow='none'; e.currentTarget.style.transform='none'; }}>
                      <div style={{ width:38, height:38, borderRadius:9, background:'#eff6ff', display:'flex', alignItems:'center', justifyContent:'center', flexShrink:0, fontSize:18 }}>ðŸ“„</div>
                      <div style={{ flex:1, minWidth:0 }}>
                        <div style={{ fontWeight:600, fontSize:14, color:'#0d1829', marginBottom:3, fontFamily:font }}>{r.title || r.event_name}</div>
                        <div style={{ display:'flex', gap:14, fontSize:12, color:'#94a3b8', fontFamily:"'DM Sans',sans-serif" }}>
                          <span>ðŸ“ {r.city}, {r.country}</span>
                          <span>ðŸ“… {r.start_date}</span>
                          {r.ai_generated && <span style={{ color:'#3b82f6', fontWeight:600, fontSize:11 }}>{t.aiGenLabel}</span>}
                        </div>
                      </div>
                      <div style={{ display:'flex', gap:8, alignItems:'center', flexShrink:0 }}>
                        <span style={{ padding:'3px 11px', borderRadius:20, fontSize:11, fontWeight:700, background:r.status==='ready'?'#dcfce7':'#fef9c3', color:r.status==='ready'?'#15803d':'#a16207', fontFamily:font }}>
                          {r.status === 'ready' ? t.ready : t.draft}
                        </span>
                        <span style={{ color:'#94a3b8', fontSize:12 }}>{isAr ? 'â†' : 'â†’'}</span>
                      </div>
                    </div>
                  ))}
                  {reports.length === 0 && !loading && (
                    <div style={{ background:'white', borderRadius:12, padding:'36px 18px', textAlign:'center', color:'#94a3b8', border:'1px solid #edf2f7', fontSize:14, fontFamily:font }}>
                      {t.noReportsYet}
                      <button onClick={() => setPage('new')} style={{ display:'block', margin:'12px auto 0', padding:'8px 16px', background:'#0d1829', color:'white', border:'none', borderRadius:7, cursor:'pointer', fontSize:12, fontWeight:600, fontFamily:font }}>
                        {t.createFirstBtn}
                      </button>
                    </div>
                  )}
                </div>
              </>
            )}

            {/* â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â• REPORTS PAGE â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â• */}
            {page === 'reports' && (
              <ReportsPage
                reports={reports}
                lang={lang}
                t={t}
                isAr={isAr}
                dir={dir}
                setPage={setPage}
                openPreview={openPreview}
                handleDelete={handleDelete}
                openDetail={openDetail}
              />
            )}

            {/* â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â• REPORT DETAIL â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â• */}
            {page === 'detail' && selectedReport && (
              <ReportDetail
                lang={lang}
                reportId={selectedReport}
                onBack={() => setPage('reports')}
                onPDFOpen={(id, title, type) => openPreview(id, title, type)}
              />
            )}

            {/* â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â• NEW REPORT â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â• */}
            {page === 'new' && (
              <NewReportForm onSuccess={handleNewReport} onCancel={() => setPage('dashboard')} />
            )}

          </div>
        )}
      </div>
    </div>
  );
}
