import { useState, useEffect } from 'react';
import Sidebar from './components/Sidebar';
import WorldMap from './components/WorldMap';
import NewReportForm from './components/NewReportForm';
import AIAssistant from './components/AIAssistant';
import { getReports, getStats, deleteReport as apiDelete, getPDFUrl, getPreviewUrl } from './services/api';

const RISK_COLORS = { low:'#22c55e', medium:'#f59e0b', high:'#ef4444' };

const FEED = [
  { id:1, icon:'ðŸ“‹', iconBg:'#dbeafe', title:'US FDA Announces Expedited Drug Approval Process', summary:'New fast-track pathway for critical medications to enter market.', source:'Bloomberg', time:'3 hours ago', impact:null },
  { id:2, icon:'ðŸŒ', iconBg:'#fed7aa', title:'G20 Leaders Summit Addresses Global Trade Tensions', summary:'New agreements on tariff reductions expected to impact pharmaceutical trade.', source:'Reuters', time:'Just now', impact:'HIGH IMPACT' },
  { id:3, icon:'ðŸ“ˆ', iconBg:'#fce7f3', title:'Saudi-Japan Bilateral Summit Strengthens Healthcare Ties', summary:'Joint investment in pharmaceutical research and development announced.', source:'Arab News', time:'2 hours ago', impact:'HIGH IMPACT' },
];

const WHO = [
  { id:1, type:'Outbreak', typeColor:'#ef4444', title:'H5N1 Avian Influenza Monitoring', summary:'Enhanced surveillance for avian influenza cases in poultry workers.', region:'Southeast Asia', date:'2/17/2026', tags:['Vietnam','Thailand','Indonesia'], link:'WHO Disease Outbreak News' },
  { id:2, type:'Update', typeColor:'#f59e0b', title:'Middle East Respiratory Syndrome Update', summary:'Sporadic cases of MERS-CoV continue to be reported.', region:'Middle East', date:'2/17/2026', tags:['Saudi Arabia','UAE','Qatar'], link:'WHO EMRO' },
];

function Toast({ toast }) {
  if (!toast) return null;
  return (
    <div style={{ position:'fixed', top:20, right:24, zIndex:9999, background:toast.color, color:'white', borderRadius:10, padding:'12px 20px', fontSize:13, fontWeight:600, boxShadow:'0 6px 24px rgba(0,0,0,0.18)', animation:'toastIn 0.25s ease', maxWidth:400 }}>
      {toast.msg}
    </div>
  );
}

export default function App() {
  const [page, setPage] = useState('dashboard');
  const [reports, setReports] = useState([]);
  const [stats, setStats] = useState({ total:0, ready:0, draft:0 });
  const [toast, setToast] = useState(null);
  const [loading, setLoading] = useState(true);
  const [previewModal, setPreviewModal] = useState(null); // { url, title, downloadUrl }

  const showToast = (msg, color='#22c55e') => {
    setToast({ msg, color });
    setTimeout(() => setToast(null), 3500);
  };

  const loadData = async () => {
    try {
      const [rRes, sRes] = await Promise.all([getReports(), getStats()]);
      setReports(rRes.data);
      setStats(sRes.data);
    } catch (e) {
      console.error(e);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => { loadData(); }, []);

  const handleNewReport = (report) => {
    setReports(prev => [report, ...prev]);
    setStats(prev => ({ ...prev, total: prev.total+1, ready: prev.ready+1 }));
    showToast(`âœ“ "${report.title||report.event_name}" report created!`);
    setPage('dashboard');
  };

  const handleDelete = async (id) => {
    if (!window.confirm('Delete this report?')) return;
    try {
      await apiDelete(id);
      setReports(prev => prev.filter(r => r.id!==id));
      setStats(prev => ({ ...prev, total: Math.max(0, prev.total-1) }));
      showToast('Report deleted.', '#f59e0b');
    } catch {
      showToast('Delete failed.', '#ef4444');
    }
  };

  const upcoming = reports.filter(r => r.status==='ready');
  const statCards = [
    { val:stats.total, label:'Total Reports', bg:'#eff6ff', ib:'#dbeafe', ic:'#3b82f6', t:'doc' },
    { val:stats.ready, label:'Ready', bg:'#f0fdf4', ib:'#dcfce7', ic:'#22c55e', t:'check' },
    { val:stats.draft, label:'Drafts', bg:'#fffbeb', ib:'#fef9c3', ic:'#f59e0b', t:'clock' },
  ];

  return (
    <div style={{ display:'flex', height:'100vh', fontFamily:"'DM Sans',-apple-system,sans-serif", background:'#f1f5f9', overflow:'hidden' }}>
      <style>{`
        @keyframes toastIn{from{opacity:0;transform:translateY(-10px)}to{opacity:1;transform:none}}
        @keyframes spin{to{transform:rotate(360deg)}}
        *{box-sizing:border-box}
        ::-webkit-scrollbar{width:5px}::-webkit-scrollbar-track{background:transparent}::-webkit-scrollbar-thumb{background:#e2e8f0;border-radius:3px}
      `}</style>
      <Toast toast={toast}/>
      <Sidebar page={page} setPage={setPage}/>

      <div style={{ flex:1, display:'flex', flexDirection:'column', overflow:'hidden' }}>
        {page!=='ai' && (
          <div style={{ background:'white', borderBottom:'1px solid #e9eef5', padding:'17px 28px 15px', display:'flex', justifyContent:'space-between', alignItems:'flex-start', flexShrink:0 }}>
            <div>
              <h1 style={{ margin:0, fontSize:24, fontWeight:700, color:'#0d1829', letterSpacing:'-0.025em' }}>
                {page==='new'?'Create New Report':page==='reports'?'Reports':'Executive Dashboard'}
              </h1>
              <p style={{ margin:'4px 0 0', fontSize:12.5, color:'#94a3b8' }}>
                {page==='new'?'AI-powered Milken-style travel intelligence briefing':page==='reports'?`${reports.length} reports total`:'SFDA Travel Intelligence Overview'}
              </p>
            </div>
            {page!=='new' && (
              <button onClick={()=>setPage('new')} style={{ display:'flex', alignItems:'center', gap:7, padding:'9px 18px', background:'#0d1829', color:'white', border:'none', borderRadius:8, fontSize:13, fontWeight:600, cursor:'pointer', transition:'background 0.15s' }}
                onMouseEnter={e=>e.currentTarget.style.background='#1e293b'}
                onMouseLeave={e=>e.currentTarget.style.background='#0d1829'}>
                + New Report
              </button>
            )}
          </div>
        )}

        {page==='ai' && (
          <div style={{ flex:1, display:'flex', flexDirection:'column', overflow:'hidden' }}>
            <AIAssistant reports={reports}/>
          </div>
        )}

        {page!=='ai' && (
          <div style={{ flex:1, overflowY:'auto', padding:page==='new'?'28px 32px 50px':'20px 28px 32px' }}>

            {/* â”€â”€ DASHBOARD â”€â”€ */}
            {page==='dashboard' && (
              <>
                {/* Stats */}
                <div style={{ display:'grid', gridTemplateColumns:'repeat(3,1fr)', gap:14, marginBottom:18 }}>
                  {statCards.map((s,i) => (
                    <div key={i} style={{ background:s.bg, borderRadius:12, padding:'16px 20px', display:'flex', alignItems:'center', gap:15, border:'1px solid rgba(0,0,0,0.04)', transition:'transform 0.15s,box-shadow 0.15s', cursor:'default' }}
                      onMouseEnter={e=>{ e.currentTarget.style.transform='translateY(-1px)'; e.currentTarget.style.boxShadow='0 4px 12px rgba(0,0,0,0.08)'; }}
                      onMouseLeave={e=>{ e.currentTarget.style.transform='none'; e.currentTarget.style.boxShadow='none'; }}>
                      <div style={{ width:44, height:44, borderRadius:11, background:s.ib, display:'flex', alignItems:'center', justifyContent:'center', flexShrink:0 }}>
                        {s.t==='doc'&&<svg width="20" height="20" viewBox="0 0 24 24" fill="none"><rect x="3" y="4" width="18" height="16" rx="2" stroke={s.ic} strokeWidth="2"/><line x1="8" y1="9" x2="16" y2="9" stroke={s.ic} strokeWidth="2" strokeLinecap="round"/><line x1="8" y1="13" x2="13" y2="13" stroke={s.ic} strokeWidth="2" strokeLinecap="round"/></svg>}
                        {s.t==='check'&&<svg width="20" height="20" viewBox="0 0 24 24" fill="none"><circle cx="12" cy="12" r="9" stroke={s.ic} strokeWidth="2"/><path d="M8 12l3 3 5-5" stroke={s.ic} strokeWidth="2" strokeLinecap="round" strokeLinejoin="round"/></svg>}
                        {s.t==='clock'&&<svg width="20" height="20" viewBox="0 0 24 24" fill="none"><circle cx="12" cy="12" r="9" stroke={s.ic} strokeWidth="2"/><path d="M12 7v5l3 2" stroke={s.ic} strokeWidth="2" strokeLinecap="round"/></svg>}
                      </div>
                      <div>
                        <div style={{ fontSize:30, fontWeight:700, color:'#0d1829', lineHeight:1 }}>{loading?'â€¦':s.val}</div>
                        <div style={{ fontSize:12, color:'#64748b', marginTop:4 }}>{s.label}</div>
                      </div>
                    </div>
                  ))}
                </div>

                {/* Map */}
                <div style={{ background:'white', borderRadius:14, boxShadow:'0 1px 4px rgba(0,0,0,0.06)', overflow:'hidden', border:'1px solid #edf2f7', marginBottom:18 }}>
                  <div style={{ padding:'13px 20px', display:'flex', justifyContent:'space-between', alignItems:'center', borderBottom:'1px solid #f1f5f9' }}>
                    <div style={{ display:'flex', alignItems:'center', gap:10 }}>
                      <div style={{ width:30, height:30, borderRadius:8, background:'#eff6ff', display:'flex', alignItems:'center', justifyContent:'center' }}>
                        <svg width="16" height="16" viewBox="0 0 24 24" fill="none"><circle cx="12" cy="12" r="9" stroke="#3b82f6" strokeWidth="2"/><ellipse cx="12" cy="12" rx="4" ry="9" stroke="#3b82f6" strokeWidth="1.5"/><line x1="3" y1="9" x2="21" y2="9" stroke="#3b82f6" strokeWidth="1.5"/><line x1="3" y1="15" x2="21" y2="15" stroke="#3b82f6" strokeWidth="1.5"/></svg>
                      </div>
                      <div>
                        <div style={{ fontWeight:700, fontSize:14.5, color:'#0d1829' }}>Global Intelligence Heat Map</div>
                        <div style={{ fontSize:11, color:'#94a3b8', marginTop:1 }}>Real-time geopolitical risk assessment for CEO travel destinations</div>
                      </div>
                    </div>
                    <div style={{ display:'flex', alignItems:'center', gap:14 }}>
                      {[['#22c55e','Low Risk'],['#f59e0b','Medium'],['#ef4444','High Risk']].map(([c,l])=>(
                        <span key={l} style={{ display:'flex', alignItems:'center', gap:5, fontSize:12, color:'#64748b' }}>
                          <span style={{ width:8, height:8, borderRadius:'50%', background:c, display:'inline-block' }}/>{l}
                        </span>
                      ))}
                      <span style={{ display:'flex', alignItems:'center', gap:5, fontSize:12, color:'#64748b' }}>
                        <span style={{ width:9, height:9, borderRadius:'50%', border:'2px solid #3b82f6', display:'inline-block' }}/>Upcoming
                      </span>
                    </div>
                  </div>
                  <WorldMap upcomingReports={upcoming}/>
                </div>

                {/* Upcoming */}
                <div style={{ background:'white', borderRadius:14, padding:'16px 20px', border:'1px solid #edf2f7', marginBottom:18 }}>
                  <div style={{ display:'flex', alignItems:'center', gap:8, marginBottom:upcoming.length?12:0 }}>
                    <span style={{ fontSize:16 }}>âœˆï¸</span>
                    <span style={{ fontWeight:700, fontSize:14.5, color:'#0d1829' }}>Upcoming CEO Travel Destinations</span>
                  </div>
                  {upcoming.length===0
                    ? <p style={{ margin:0, fontSize:13, color:'#94a3b8' }}>No upcoming destinations. <button onClick={()=>setPage('new')} style={{ background:'none', border:'none', color:'#3b82f6', cursor:'pointer', fontSize:13, padding:0, fontWeight:600 }}>Create a report â†’</button></p>
                    : <div style={{ display:'flex', flexWrap:'wrap', gap:10 }}>
                        {upcoming.map(r=>(
                          <div key={r.id} style={{ display:'flex', alignItems:'center', gap:9, background:'#f8fafc', borderRadius:9, padding:'8px 14px', border:'1px solid #e2e8f0' }}>
                            <span style={{ width:9, height:9, borderRadius:'50%', background:RISK_COLORS[r.risk_level||'low'], display:'inline-block', flexShrink:0 }}/>
                            <span style={{ fontSize:13, fontWeight:600, color:'#1e293b' }}>{r.country}</span>
                            <span style={{ color:'#e2e8f0' }}>Â·</span>
                            <span style={{ fontSize:11.5, color:'#64748b' }}>ðŸ“… {r.start_date}</span>
                          </div>
                        ))}
                      </div>
                  }
                </div>

                {/* Feed + WHO */}
                <div style={{ display:'grid', gridTemplateColumns:'1fr 1fr', gap:16, marginBottom:20 }}>
                  <div style={{ background:'white', borderRadius:14, padding:'18px 20px', border:'1px solid #edf2f7' }}>
                    <div style={{ display:'flex', justifyContent:'space-between', alignItems:'center', marginBottom:14 }}>
                      <div style={{ display:'flex', alignItems:'center', gap:10 }}>
                        <div style={{ width:30, height:30, borderRadius:8, background:'#eff6ff', display:'flex', alignItems:'center', justifyContent:'center', fontSize:14 }}>ðŸ“°</div>
                        <div>
                          <div style={{ fontWeight:700, fontSize:15, color:'#0d1829' }}>Political Intelligence Feed</div>
                          <div style={{ fontSize:11, color:'#94a3b8', marginTop:1 }}>Latest political news relevant to SFDA</div>
                        </div>
                      </div>
                      <button style={{ background:'none', border:'none', cursor:'pointer', color:'#94a3b8', fontSize:16, padding:4 }}>â†»</button>
                    </div>
                    {FEED.map((item,i)=>(
                      <div key={item.id} style={{ display:'flex', gap:12, padding:'13px 0', borderBottom:i<FEED.length-1?'1px solid #f1f5f9':'none', alignItems:'flex-start' }}>
                        <div style={{ width:36, height:36, borderRadius:9, background:item.iconBg, display:'flex', alignItems:'center', justifyContent:'center', fontSize:16, flexShrink:0 }}>{item.icon}</div>
                        <div style={{ flex:1, minWidth:0 }}>
                          <div style={{ fontWeight:600, fontSize:13.5, color:'#0d1829', marginBottom:3, lineHeight:1.35 }}>{item.title}</div>
                          <div style={{ fontSize:12, color:'#64748b', marginBottom:5 }}>{item.summary}</div>
                          <div style={{ fontSize:11, color:'#94a3b8' }}>{item.source} Â· {item.time}</div>
                        </div>
                        {item.impact && <span style={{ border:'1.5px solid #22c55e', color:'#16a34a', borderRadius:20, padding:'3px 10px', fontSize:10, fontWeight:700, whiteSpace:'nowrap', flexShrink:0 }}>{item.impact}</span>}
                      </div>
                    ))}
                  </div>

                  <div style={{ background:'white', borderRadius:14, padding:'18px 20px', border:'1px solid #edf2f7' }}>
                    <div style={{ display:'flex', justifyContent:'space-between', alignItems:'center', marginBottom:14 }}>
                      <div style={{ display:'flex', alignItems:'center', gap:10 }}>
                        <div style={{ width:30, height:30, borderRadius:8, background:'#fffbeb', display:'flex', alignItems:'center', justifyContent:'center', fontSize:14 }}>âš ï¸</div>
                        <div>
                          <div style={{ fontWeight:700, fontSize:15, color:'#0d1829' }}>WHO Health Alerts</div>
                          <div style={{ fontSize:11, color:'#94a3b8', marginTop:1 }}>Disease outbreaks and health travel advisories</div>
                        </div>
                      </div>
                      <button style={{ background:'none', border:'none', cursor:'pointer', color:'#94a3b8', fontSize:16, padding:4 }}>â†»</button>
                    </div>
                    {WHO.map((item,i)=>(
                      <div key={item.id} style={{ paddingBottom:i<WHO.length-1?16:0, borderBottom:i<WHO.length-1?'1px solid #f1f5f9':'none', marginBottom:i<WHO.length-1?16:0 }}>
                        <div style={{ display:'flex', alignItems:'center', gap:8, marginBottom:8 }}>
                          <span style={{ background:item.typeColor, color:'white', borderRadius:6, padding:'2px 10px', fontSize:11, fontWeight:700 }}>{item.type}</span>
                          <span style={{ fontSize:16 }}>ðŸ¥</span>
                        </div>
                        <div style={{ fontWeight:700, fontSize:14, color:'#0d1829', marginBottom:4 }}>{item.title}</div>
                        <div style={{ fontSize:12, color:'#64748b', marginBottom:8 }}>{item.summary}</div>
                        <div style={{ display:'flex', gap:16, fontSize:11, color:'#94a3b8', marginBottom:8 }}>
                          <span>ðŸ“ {item.region}</span><span>ðŸ“… {item.date}</span>
                        </div>
                        <div style={{ display:'flex', flexWrap:'wrap', gap:5, marginBottom:8 }}>
                          {item.tags.map(t=><span key={t} style={{ background:'#f1f5f9', color:'#475569', borderRadius:6, padding:'2px 9px', fontSize:11 }}>{t}</span>)}
                        </div>
                        <a href="#" onClick={e=>e.preventDefault()} style={{ fontSize:12, color:'#3b82f6', textDecoration:'none' }}>â†— {item.link}</a>
                      </div>
                    ))}
                  </div>
                </div>

                {/* Recent Reports */}
                <div>
                  <div style={{ display:'flex', justifyContent:'space-between', alignItems:'center', marginBottom:14 }}>
                    <div style={{ fontWeight:700, fontSize:17, color:'#0d1829' }}>Recent Reports</div>
                    <button onClick={()=>setPage('reports')} style={{ background:'none', border:'none', cursor:'pointer', color:'#3b82f6', fontSize:13, fontWeight:600 }}>View All â†’</button>
                  </div>
                  <div style={{ display:'flex', flexDirection:'column', gap:10 }}>
                    {reports.slice(0,5).map(r=>(
                      <div key={r.id} style={{ background:'white', borderRadius:12, padding:'14px 18px', display:'flex', alignItems:'center', gap:14, border:'1px solid #edf2f7', cursor:'pointer', transition:'all 0.15s' }}
                        onMouseEnter={e=>{ e.currentTarget.style.boxShadow='0 4px 14px rgba(0,0,0,0.09)'; e.currentTarget.style.transform='translateX(2px)'; }}
                        onMouseLeave={e=>{ e.currentTarget.style.boxShadow='none'; e.currentTarget.style.transform='none'; }}>
                        <div style={{ width:38, height:38, borderRadius:9, background:'#eff6ff', display:'flex', alignItems:'center', justifyContent:'center', flexShrink:0 }}>
                          <svg width="18" height="18" viewBox="0 0 24 24" fill="none"><rect x="3" y="4" width="18" height="16" rx="2" stroke="#3b82f6" strokeWidth="2"/><line x1="8" y1="9" x2="16" y2="9" stroke="#3b82f6" strokeWidth="2" strokeLinecap="round"/><line x1="8" y1="13" x2="13" y2="13" stroke="#3b82f6" strokeWidth="2" strokeLinecap="round"/></svg>
                        </div>
                        <div style={{ flex:1, minWidth:0 }}>
                          <div style={{ fontWeight:600, fontSize:14, color:'#0d1829', marginBottom:3 }}>{r.title||r.event_name}</div>
                          <div style={{ display:'flex', gap:14, fontSize:12, color:'#94a3b8' }}>
                            <span>ðŸ“ {r.city}, {r.country}</span>
                            <span>ðŸ“… {r.start_date}</span>
                            {r.ai_generated && <span style={{ color:'#3b82f6', fontWeight:600, fontSize:11 }}>âœ¦ AI</span>}
                          </div>
                        </div>
                        <div style={{ display:'flex', gap:8, alignItems:'center', flexShrink:0 }}>
                          <span style={{ padding:'3px 11px', borderRadius:20, fontSize:11, fontWeight:700, background:r.status==='ready'?'#dcfce7':'#fef9c3', color:r.status==='ready'?'#15803d':'#a16207' }}>
                            {r.status?.charAt(0).toUpperCase()+r.status?.slice(1)}
                          </span>
                          <button onClick={()=>setPreviewModal({url:getPDFUrl(r.id), title:r.title||r.event_name, downloadUrl:getPDFUrl(r.id)})}
                            title="View PDF"
                            style={{ width:30, height:30, borderRadius:7, background:'#eff6ff', border:'none', display:'flex', alignItems:'center', justifyContent:'center', cursor:'pointer', fontSize:14, transition:'background 0.15s' }}
                            onMouseEnter={e=>e.currentTarget.style.background='#dbeafe'}
                            onMouseLeave={e=>e.currentTarget.style.background='#eff6ff'}>ðŸ“„</button>
                        </div>
                      </div>
                    ))}
                    {reports.length===0 && !loading && (
                      <div style={{ background:'white', borderRadius:12, padding:'36px 18px', textAlign:'center', color:'#94a3b8', border:'1px solid #edf2f7', fontSize:14 }}>
                        No reports yet.<br/>
                        <button onClick={()=>setPage('new')} style={{ marginTop:12, padding:'8px 16px', background:'#0d1829', color:'white', border:'none', borderRadius:7, cursor:'pointer', fontSize:12, fontWeight:600 }}>Create First Report</button>
                      </div>
                    )}
                  </div>
                </div>
              </>
            )}

            {/* â”€â”€ REPORTS PAGE â”€â”€ */}
            {page==='reports' && (
              <>
                <div style={{ display:'flex', justifyContent:'space-between', alignItems:'center', marginBottom:20 }}>
                  <div><h2 style={{ margin:0, fontSize:21, fontWeight:700, color:'#0d1829' }}>All Reports</h2><p style={{ margin:'4px 0 0', fontSize:13, color:'#94a3b8' }}>{reports.length} total</p></div>
                  <button onClick={()=>setPage('new')} style={{ padding:'9px 18px', background:'#0d1829', color:'white', border:'none', borderRadius:8, fontSize:13, fontWeight:600, cursor:'pointer' }}>+ New Report</button>
                </div>
                <div style={{ background:'white', borderRadius:14, overflow:'hidden', border:'1px solid #edf2f7' }}>
                  <table style={{ width:'100%', borderCollapse:'collapse' }}>
                    <thead>
                      <tr style={{ borderBottom:'2px solid #f1f5f9' }}>
                        {['Title','City','Dates','Risk','Status','PDF','Actions'].map(h=>(
                          <th key={h} style={{ textAlign:'left', padding:'12px 16px', fontSize:10.5, fontWeight:700, color:'#94a3b8', textTransform:'uppercase', letterSpacing:'0.07em' }}>{h}</th>
                        ))}
                      </tr>
                    </thead>
                    <tbody>
                      {reports.length===0 ? (
                        <tr><td colSpan={7} style={{ textAlign:'center', padding:'60px', color:'#94a3b8' }}>No reports. <button onClick={()=>setPage('new')} style={{ background:'none', border:'none', color:'#3b82f6', cursor:'pointer', fontWeight:600 }}>Create one â†’</button></td></tr>
                      ) : reports.map((r,i)=>(
                        <tr key={r.id} style={{ borderBottom:i<reports.length-1?'1px solid #f8fafc':'none', transition:'background 0.1s' }}
                          onMouseEnter={e=>e.currentTarget.style.background='#fafafa'}
                          onMouseLeave={e=>e.currentTarget.style.background='transparent'}>
                          <td style={{ padding:'14px 16px', fontSize:13, fontWeight:600, color:'#1e293b' }}>
                            {r.title||r.event_name}
                            {r.ai_generated && <span style={{ marginLeft:6, fontSize:10, background:'#eff6ff', color:'#3b82f6', borderRadius:4, padding:'1px 5px', fontWeight:600 }}>AI</span>}
                          </td>
                          <td style={{ padding:'14px 16px', fontSize:13, color:'#64748b' }}>{r.city}, {r.country}</td>
                          <td style={{ padding:'14px 16px', fontSize:12, color:'#64748b' }}>{r.start_date}{r.end_date?' â€“ '+r.end_date:''}</td>
                          <td style={{ padding:'14px 16px' }}><span style={{ padding:'3px 10px', borderRadius:20, fontSize:11, fontWeight:700, background:RISK_COLORS[r.risk_level||'low']+'22', color:RISK_COLORS[r.risk_level||'low'] }}>{(r.risk_level||'low').charAt(0).toUpperCase()+(r.risk_level||'low').slice(1)}</span></td>
                          <td style={{ padding:'14px 16px' }}><span style={{ padding:'3px 10px', borderRadius:20, fontSize:11, fontWeight:700, background:r.status==='ready'?'#dcfce7':'#fef9c3', color:r.status==='ready'?'#15803d':'#a16207' }}>{r.status?.charAt(0).toUpperCase()+r.status?.slice(1)}</span></td>
                          <td style={{ padding:'14px 16px' }}>
                            <div style={{ display:'flex', gap:6 }}>
                              <button onClick={()=>setPreviewModal({url:getPDFUrl(r.id), title:r.title||r.event_name, downloadUrl:getPDFUrl(r.id)})} style={{ padding:'5px 10px', background:'#eff6ff', color:'#3b82f6', border:'none', borderRadius:6, fontSize:11, fontWeight:600, cursor:'pointer' }}>ðŸ“„ PDF</button>
                              <button onClick={()=>setPreviewModal({url:getPreviewUrl(r.id), title:r.title||r.event_name, downloadUrl:getPDFUrl(r.id)})} style={{ padding:'5px 10px', background:'#f1f5f9', color:'#374151', border:'none', borderRadius:6, fontSize:11, fontWeight:600, cursor:'pointer' }}>ðŸ‘ï¸ HTML</button>
                            </div>
                          </td>
                          <td style={{ padding:'14px 16px' }}>
                            <button onClick={()=>handleDelete(r.id)} style={{ background:'none', border:'none', cursor:'pointer', color:'#cbd5e1', fontSize:14, padding:'3px 7px', borderRadius:5, transition:'color 0.15s' }}
                              onMouseEnter={e=>e.currentTarget.style.color='#ef4444'}
                              onMouseLeave={e=>e.currentTarget.style.color='#cbd5e1'}>âœ•</button>
                          </td>
                        </tr>
                      ))}
                    </tbody>
                  </table>
                </div>
              </>
            )}

            {/* â”€â”€ NEW REPORT â”€â”€ */}
            {page==='new' && (
              <NewReportForm onSuccess={handleNewReport} onCancel={()=>setPage('dashboard')}/>
            )}
          </div>
        )}
      </div>

        {/* â”€â”€ PDF / HTML PREVIEW MODAL â”€â”€ */}
        {previewModal && (
          <div style={{ position:'fixed', inset:0, zIndex:2000, background:'rgba(0,0,0,0.75)', display:'flex', flexDirection:'column' }}>
            <div style={{ background:'#0d1829', padding:'12px 20px', display:'flex', alignItems:'center', justifyContent:'space-between', flexShrink:0 }}>
              <span style={{ color:'white', fontSize:14, fontWeight:700 }}>ðŸ“„ {previewModal.title}</span>
              <div style={{ display:'flex', gap:8 }}>
                <a href={previewModal.downloadUrl} download
                  style={{ padding:'7px 16px', background:'#3b82f6', color:'white', borderRadius:7, fontSize:12, fontWeight:600, textDecoration:'none' }}>
                  â¬‡ Download PDF
                </a>
                <button onClick={()=>setPreviewModal(null)}
                  style={{ padding:'7px 16px', background:'rgba(255,255,255,0.15)', color:'white', border:'none', borderRadius:7, fontSize:12, fontWeight:600, cursor:'pointer' }}>
                  âœ• Close
                </button>
              </div>
            </div>
            <iframe src={previewModal.url} title="Report Preview" style={{ flex:1, border:'none', background:'white' }}/>
          </div>
        )}
    </div>
  );
}
