/* â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€
   CustomDropdown â€” position:fixed panel, works in any 
   overflow:hidden or RTL container
â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€ */
function CustomDropdown({ value, onChange, options, placeholder, font, dir }) {
  const [open, setOpen]     = useState(false);
  const [pos,  setPos]      = useState({ top:0, left:0, width:0 });
  const btnRef              = useRef(null);
  const panelRef            = useRef(null);

  const recalc = useCallback(() => {
    if (!btnRef.current) return;
    const r = btnRef.current.getBoundingClientRect();
    const viewH = window.innerHeight;
    const panelH = Math.min(options.length * 40 + 8, 280);
    const showBelow = r.bottom + panelH < viewH;
    setPos({
      top:   showBelow ? r.bottom + 2 : r.top - panelH - 2,
      left:  r.left,
      width: r.width,
    });
  }, [options.length]);

  const toggle = (e) => {
    e.stopPropagation();
    if (!open) recalc();
    setOpen(o => !o);
  };

  // Close on outside click
  useEffect(() => {
    if (!open) return;
    const handler = (e) => {
      if (!btnRef.current?.contains(e.target) && !panelRef.current?.contains(e.target)) {
        setOpen(false);
      }
    };
    document.addEventListener('mousedown', handler);
    document.addEventListener('touchstart', handler);
    return () => {
      document.removeEventListener('mousedown', handler);
      document.removeEventListener('touchstart', handler);
    };
  }, [open]);

  // Reposition on scroll/resize
  useEffect(() => {
    if (!open) return;
    const handler = () => { recalc(); };
    window.addEventListener('scroll', handler, true);
    window.addEventListener('resize', handler);
    return () => {
      window.removeEventListener('scroll', handler, true);
      window.removeEventListener('resize', handler);
    };
  }, [open, recalc]);

  const selected = options.find(o => o.value === value);
  const label    = selected ? selected.label : placeholder;
  const isEmpty  = !selected;

  return (
    <>
      {/* Trigger button */}
      <button
        ref={btnRef}
        type="button"
        onClick={toggle}
        style={{
          width: '100%', padding: '10px 36px 10px 12px', border: `1.5px solid ${open ? '#3b82f6' : '#e2e8f0'}`,
          borderRadius: 8, fontSize: 13, color: isEmpty ? '#9ca3af' : '#0d1829',
          background: 'white', cursor: 'pointer', fontFamily: font, boxSizing: 'border-box',
          textAlign: dir === 'rtl' ? 'right' : 'left', direction: dir,
          display: 'flex', alignItems: 'center', justifyContent: 'space-between',
          transition: 'border-color 0.15s', outline: 'none', position: 'relative',
        }}
      >
        <span style={{ flex: 1, overflow: 'hidden', textOverflow: 'ellipsis', whiteSpace: 'nowrap' }}>
          {label}
        </span>
        <svg
          width="12" height="12" viewBox="0 0 12 12" fill="none"
          style={{ flexShrink: 0, marginLeft: dir === 'rtl' ? 0 : 6, marginRight: dir === 'rtl' ? 6 : 0, transition: 'transform 0.2s', transform: open ? 'rotate(180deg)' : 'none', color: '#94a3b8' }}
        >
          <path d="M2 4l4 4 4-4" stroke="currentColor" strokeWidth="1.5" strokeLinecap="round" strokeLinejoin="round"/>
        </svg>
      </button>

      {/* Options panel â€” rendered via portal at fixed position */}
      {open && (
        <div
          ref={panelRef}
          style={{
            position: 'fixed',
            top: pos.top, left: pos.left, width: pos.width,
            zIndex: 999999,
            background: 'white', borderRadius: 8,
            border: '1.5px solid #e2e8f0',
            boxShadow: '0 8px 32px rgba(0,0,0,0.14)',
            maxHeight: 280, overflowY: 'auto',
            padding: '4px 0',
            direction: dir,
          }}
        >
          {options.map((opt) => (
            <div
              key={opt.value}
              onMouseDown={(e) => {
                e.preventDefault();
                e.stopPropagation();
                onChange(opt.value);
                setOpen(false);
              }}
              style={{
                padding: '9px 14px', fontSize: 13, fontFamily: font,
                cursor: 'pointer', color: opt.value === value ? '#1d4ed8' : '#0d1829',
                background: opt.value === value ? '#eff6ff' : 'transparent',
                fontWeight: opt.value === value ? 600 : 400,
                transition: 'background 0.1s',
                textAlign: dir === 'rtl' ? 'right' : 'left',
              }}
              onMouseEnter={e => { if (opt.value !== value) e.currentTarget.style.background = '#f8fafc'; }}
              onMouseLeave={e => { if (opt.value !== value) e.currentTarget.style.background = 'transparent'; }}
            >
              {opt.value === value && (
                <span style={{ marginLeft: dir === 'rtl' ? 0 : 6, marginRight: dir === 'rtl' ? 6 : 0, color: '#1d4ed8' }}>âœ“ </span>
              )}
              {opt.label}
            </div>
          ))}
        </div>
      )}
    </>
  );
}

