  };
  const onFocus = e => { e.target.style.borderColor = '#3b82f6'; };
  const onBlur  = e => { e.target.style.borderColor = '#e2e8f0'; };

  const Block = ({ icon, title, badge, children }) => (
    <div style={{ background: 'white', borderRadius: 12, padding: '20px 22px', border: '1px solid #edf2f7', marginBottom: 14, boxShadow: '0 1px 3px rgba(0,0,0,0.04)' }}>
      <div style={{ display: 'flex', alignItems: 'center', gap: 10, marginBottom: 18, direction: dir }}>
        <div style={{ width: 28, height: 28, borderRadius: 7, background: '#eff6ff', display: 'flex', alignItems: 'center', justifyContent: 'center', fontSize: 14, flexShrink: 0 }}>{icon}</div>
        <span style={{ fontWeight: 700, fontSize: 15, color: '#0d1829', fontFamily: font }}>{title}</span>
        {badge && <span style={{ fontSize: 10, fontWeight: 600, color: '#94a3b8', background: '#f1f5f9', padding: '2px 7px', borderRadius: 4 }}>{badge}</span>}
      </div>
      {children}
    </div>
  );

  /* â”€â”€ Preview overlay â”€â”€ */
  if (previewMode && report) {
    const url = previewMode === 'pdf' ? getPDFUrl(report.id) : getPreviewUrl(report.id);
    return (
      <div style={{ position: 'fixed', inset: 0, zIndex: 2000, background: 'rgba(0,0,0,0.75)', display: 'flex', flexDirection: 'column' }}>
        <div style={{ background: '#0d1829', padding: '12px 20px', display: 'flex', alignItems: 'center', justifyContent: 'space-between', flexShrink: 0 }}>
          <span style={{ color: 'white', fontSize: 14, fontWeight: 700, fontFamily: font }}>{previewMode === 'pdf' ? 'ðŸ“„ PDF' : 'ðŸ‘ï¸ HTML'} â€” {report.title || report.event_name}</span>
          <div style={{ display: 'flex', gap: 8 }}>
            <a href={getPDFUrl(report.id)} download style={{ padding: '7px 16px', background: '#3b82f6', color: 'white', borderRadius: 7, fontSize: 12, fontWeight: 600, textDecoration: 'none', fontFamily: font }}>â¬‡ {isAr ? 'ØªØ­Ù…ÙŠÙ„' : 'Download'}</a>
            <button onClick={() => setPreviewMode(null)} style={{ padding: '7px 16px', background: 'rgba(255,255,255,0.15)', color: 'white', border: 'none', borderRadius: 7, fontSize: 12, fontWeight: 600, cursor: 'pointer', fontFamily: font }}>âœ• {isAr ? 'Ø¥ØºÙ„Ø§Ù‚' : 'Close'}</button>
          </div>
        </div>
        <iframe src={url} title="Report Preview" style={{ flex: 1, border: 'none', background: 'white' }} />
      </div>
    );
  }

  /* â”€â”€ Generating phase â”€â”€ */
  if (phase === 'generating') {
    return (
      <div style={{ display: 'flex', flexDirection: 'column', alignItems: 'center', justifyContent: 'center', height: '60vh', gap: 20, direction: dir }}>
        <div style={{ width: 64, height: 64, borderRadius: 16, background: 'linear-gradient(135deg,#1d4ed8,#3b82f6)', display: 'flex', alignItems: 'center', justifyContent: 'center', fontSize: 28, boxShadow: '0 4px 20px rgba(59,130,246,0.4)' }}>âœ¦</div>
        <h2 style={{ fontSize: 22, fontWeight: 700, color: '#0d1829', margin: 0, fontFamily: font }}>{l.genTitle}</h2>
        <p style={{ color: '#64748b', fontSize: 13, textAlign: 'center', maxWidth: 400, fontFamily: font }}>{l.genSubtitle}</p>
        <div style={{ background: 'white', borderRadius: 12, padding: '16px 24px', border: '1px solid #e2e8f0', minWidth: 340, textAlign: 'center' }}>
          <div style={{ display: 'flex', alignItems: 'center', gap: 10, justifyContent: 'center' }}>
            <span style={{ display: 'inline-block', animation: 'spin 1s linear infinite', fontSize: 18 }}>â³</span>
            <span style={{ fontSize: 13, color: '#374151', fontWeight: 500, fontFamily: font }}>{genStep}</span>
          </div>
          <div style={{ marginTop: 14, background: '#f1f5f9', borderRadius: 8, height: 6, overflow: 'hidden' }}>
            <div style={{ height: '100%', background: 'linear-gradient(90deg,#3b82f6,#1d4ed8)', animation: 'progress 30s linear forwards', borderRadius: 8 }} />
          </div>
        </div>
        <style>{`@keyframes spin{to{transform:rotate(360deg)}}@keyframes progress{from{width:0}to{width:95%}}`}</style>
      </div>
    );
  }

  /* â”€â”€ Done phase â”€â”€ */
  if (phase === 'done' && report) {
    const counts = [
      report.visit_objectives?.length,
      report.agenda?.length,
      report.delegation?.length,
      report.speakers?.length,
      report.bilateral_meetings?.length,
      report.conference_tracks?.length,
      (report.sessions?.day1?.length || 0) + (report.sessions?.day2?.length || 0) + (report.sessions?.day3?.length || 0),
      report.weather?.length,
      report.prayer_times?.length,
    ];
    return (
      <div style={{ display: 'flex', flexDirection: 'column', alignItems: 'center', gap: 20, padding: '40px 20px', direction: dir }}>
        <div style={{ width: 64, height: 64, borderRadius: '50%', background: '#dcfce7', display: 'flex', alignItems: 'center', justifyContent: 'center', fontSize: 28 }}>âœ…</div>
        <h2 style={{ fontSize: 22, fontWeight: 700, color: '#0d1829', margin: 0, fontFamily: font }}>{l.doneTitle}</h2>
        <p style={{ color: '#64748b', fontSize: 13, textAlign: 'center', maxWidth: 440, fontFamily: font }}>
          <strong>{report.event_name}</strong> Â· {report.city}, {report.country} Â· {report.speakers?.length || 0} {l.speakersSuffix} Â· {report.bilateral_meetings?.length || 0} {l.meetingsSuffix}
        </p>
        <div style={{ display: 'flex', gap: 12, flexWrap: 'wrap', justifyContent: 'center' }}>
          <button onClick={() => setPreviewMode('pdf')} style={{ padding: '12px 24px', background: 'linear-gradient(135deg,#1c3370,#2563eb)', color: 'white', border: 'none', borderRadius: 10, fontSize: 13, fontWeight: 700, cursor: 'pointer', boxShadow: '0 4px 14px rgba(29,78,216,0.35)', fontFamily: font }}>{l.viewPDF}</button>
          <button onClick={() => setPreviewMode('html')} style={{ padding: '12px 24px', background: 'white', color: '#1d4ed8', border: '1.5px solid #1d4ed8', borderRadius: 10, fontSize: 13, fontWeight: 700, cursor: 'pointer', fontFamily: font }}>{l.previewHTML}</button>
          <button onClick={onCancel} style={{ padding: '12px 24px', background: '#f1f5f9', color: '#374151', border: 'none', borderRadius: 10, fontSize: 13, fontWeight: 600, cursor: 'pointer', fontFamily: font }}>{l.backDash}</button>
        </div>
        <div style={{ background: 'white', borderRadius: 14, border: '1px solid #edf2f7', padding: '18px 22px', maxWidth: 560, width: '100%' }}>
          <div style={{ fontWeight: 700, fontSize: 14, color: '#0d1829', marginBottom: 12, fontFamily: font }}>{l.sectionsGen}</div>
          {l.sections.map(([icon, label], i) => (
            <div key={i} style={{ display: 'flex', justifyContent: 'space-between', padding: '5px 0', borderBottom: '1px solid #f8fafc', fontSize: 13, fontFamily: font }}>
              <span style={{ color: '#374151' }}>{icon} {label}</span>
              <span style={{ background: '#eff6ff', color: '#3b82f6', borderRadius: 12, padding: '1px 10px', fontSize: 11, fontWeight: 700 }}>{counts[i] || 0}</span>
            </div>
          ))}
        </div>
      </div>
    );
  }

  /* â”€â”€ Form â”€â”€ */
  return (
    <div style={{ maxWidth: 720, margin: '0 auto', paddingBottom: 50, direction: dir, fontFamily: font }}>
      {/* Header */}
      <div style={{ textAlign: 'center', marginBottom: 28 }}>
        <div style={{ width: 54, height: 54, borderRadius: 16, background: 'linear-gradient(135deg,#1d4ed8,#3b82f6)', display: 'flex', alignItems: 'center', justifyContent: 'center', fontSize: 22, margin: '0 auto 14px', boxShadow: '0 4px 16px rgba(59,130,246,0.38)' }}>âœ¦</div>
        <h2 style={{ margin: 0, fontSize: 24, fontWeight: 700, color: '#0d1829', fontFamily: font }}>{l.pageTitle}</h2>
        <p style={{ margin: '6px 0 0', fontSize: 13, color: '#94a3b8', fontFamily: font }}>{l.pageSubtitle}</p>
      </div>

      {error && (
        <div style={{ background: '#fef2f2', border: '1.5px solid #fecaca', borderRadius: 10, padding: '12px 16px', marginBottom: 16, color: '#dc2626', fontSize: 13, fontFamily: font }}>
          {error}
        </div>
      )}

      {/* Step indicator */}
      <div style={{ display: 'flex', justifyContent: 'center', alignItems: 'center', marginBottom: 28 }}>
        {l.stepLabels.map((s, i, arr) => (
          <div key={s} style={{ display: 'flex', alignItems: 'center' }}>
            <div style={{ display: 'flex', flexDirection: 'column', alignItems: 'center' }}>
              <div style={{ width: 30, height: 30, borderRadius: '50%', background: i === 0 ? '#1d4ed8' : '#f1f5f9', display: 'flex', alignItems: 'center', justifyContent: 'center', fontSize: 12, color: i === 0 ? 'white' : '#94a3b8', border: `2px solid ${i === 0 ? '#1d4ed8' : '#e2e8f0'}` }}>{i + 1}</div>
              <span style={{ fontSize: 10.5, color: i === 0 ? '#1d4ed8' : '#94a3b8', marginTop: 5, fontWeight: i === 0 ? 700 : 400, fontFamily: font }}>{s}</span>
            </div>
            {i < arr.length - 1 && <div style={{ width: 60, height: 2, background: '#e2e8f0', margin: '0 4px', marginBottom: 16 }} />}
          </div>
        ))}
      </div>

      {/* Event Details */}
      <Block icon="ðŸ“„" title={l.eventDetails}>
        <div style={{ marginBottom: 14 }}>
          <label style={lS}>{l.eventName} <span style={{ color: '#ef4444' }}>*</span></label>
          <input style={iS} placeholder={l.eventNamePH} value={form.event_name} onChange={set('event_name')} onFocus={onFocus} onBlur={onBlur} />
        </div>
        <div style={{ marginBottom: 14 }}>
          <label style={lS}>{l.reportTitle}</label>
          <input style={iS} placeholder={l.reportTitlePH} value={form.title} onChange={set('title')} onFocus={onFocus} onBlur={onBlur} />
        </div>
        <div style={{ display: 'grid', gridTemplateColumns: '1fr 1fr', gap: 12, marginBottom: 14 }}>
          <div>
            <label style={lS}>{l.eventType}</label>
            <CustomDropdown
              value={form.event_type}
              onChange={v => setVal('event_type', v)}
              options={eventTypeOptions}
              placeholder={eventTypeOptions[0]?.label}
              font={font}
              dir={dir}
            />
          </div>
          <div>
            <label style={lS}>{l.reportLang}</label>
            <CustomDropdown
              value={form.language}
              onChange={v => setVal('language', v)}
              options={languageOptions}
              placeholder={languageOptions[0]?.label}
              font={font}
              dir={dir}
            />
          </div>
        </div>
        <div>
          <label style={lS}>{l.eventWebsite}</label>
          <div style={{ position: 'relative' }}>
            <span style={{ position: 'absolute', [isAr ? 'right' : 'left']: 11, top: '50%', transform: 'translateY(-50%)', fontSize: 13, color: '#94a3b8', pointerEvents: 'none' }}>ðŸ”—</span>
            <input style={{ ...iS, [isAr ? 'paddingRight' : 'paddingLeft']: 32 }} placeholder={l.websitePH} value={form.event_website} onChange={set('event_website')} onFocus={onFocus} onBlur={onBlur} />
          </div>
        </div>
      </Block>

      {/* Location */}
      <Block icon="ðŸ“" title={l.location}>
        <div style={{ display: 'grid', gridTemplateColumns: '1fr 1fr 1fr', gap: 12 }}>
          <div>
            <label style={lS}>{l.city} <span style={{ color: '#ef4444' }}>*</span></label>
            <input style={iS} placeholder={l.cityPH} value={form.city} onChange={set('city')} onFocus={onFocus} onBlur={onBlur} />
          </div>
          <div>
            <label style={lS}>{l.country} <span style={{ color: '#ef4444' }}>*</span></label>
            <CustomDropdown
              value={form.country}
              onChange={v => setVal('country', v)}
              options={countryOptions}
              placeholder={l.countryPH}
              font={font}
              dir={dir}
            />
          </div>
          <div>
            <label style={lS}>{l.venue}</label>
            <input style={iS} placeholder={l.venuePH} value={form.venue} onChange={set('venue')} onFocus={onFocus} onBlur={onBlur} />
          </div>
        </div>
      </Block>

      {/* Dates */}
      <Block icon="ðŸ“…" title={l.dates}>
        <div style={{ display: 'grid', gridTemplateColumns: '1fr 1fr', gap: 12 }}>
          <div>
            <label style={lS}>{l.startDate} <span style={{ color: '#ef4444' }}>*</span></label>
            <input type="date" style={iS} value={form.start_date} onChange={set('start_date')} />
          </div>
          <div>
            <label style={lS}>{l.endDate}</label>
            <input type="date" style={iS} value={form.end_date} onChange={set('end_date')} />
          </div>
        </div>
      </Block>

      {/* Traveler */}
      <Block icon="ðŸ‘¤" title={l.traveler}>
        <div style={{ display: 'grid', gridTemplateColumns: '1fr 1fr', gap: 12 }}>
          <div><label style={lS}>{l.travelerName}</label><input style={iS} value={form.traveler_name} onChange={set('traveler_name')} onFocus={onFocus} onBlur={onBlur} /></div>
          <div><label style={lS}>{l.travelerTitle}</label><input style={iS} value={form.traveler_title} onChange={set('traveler_title')} onFocus={onFocus} onBlur={onBlur} /></div>
        </div>
      </Block>

      {/* Context */}
      <Block icon="ðŸ’¬" title={l.context} badge={l.contextBadge}>
        <textarea style={{ ...iS, resize: 'vertical', minHeight: 90, lineHeight: 1.65 }} placeholder={l.contextPH} value={form.context} onChange={set('context')} onFocus={onFocus} onBlur={onBlur} />
      </Block>

      {/* Actions */}
      <div style={{ display: 'flex', justifyContent: 'center', gap: 12, paddingTop: 4 }}>
        <button onClick={onCancel} style={{ padding: '11px 26px', background: 'white', border: '1.5px solid #e2e8f0', borderRadius: 9, fontSize: 13, fontWeight: 600, cursor: 'pointer', color: '#64748b', fontFamily: font }}>
          {l.cancel}
        </button>
        <button
          onClick={handleGenerate}
          disabled={isSubmitting}
          style={{ padding: '11px 28px', background: isSubmitting ? '#93c5fd' : 'linear-gradient(135deg,#1d4ed8,#3b82f6)', border: 'none', borderRadius: 9, color: 'white', fontSize: 13, fontWeight: 700, cursor: isSubmitting ? 'not-allowed' : 'pointer', display: 'flex', alignItems: 'center', gap: 9, boxShadow: '0 4px 16px rgba(59,130,246,0.38)', minWidth: 200, justifyContent: 'center', opacity: isSubmitting ? 0.7 : 1, fontFamily: font }}
        >
          <span>âœ¦</span> {isSubmitting ? l.generating : l.generate}
        </button>
      </div>
    </div>
  );
}
