import { useState, useEffect } from 'react';
import { T, getLang, setLang as persistLang } from '../i18n';

// Design tokens
const S = {
  bg: '#060914',
  border: 'rgba(212,175,55,0.1)',
  gold: '#d4af37',
  goldDim: 'rgba(212,175,55,0.6)',
  text: '#e8edf8',
  textSec: 'rgba(180,196,225,0.55)',
  active: 'rgba(212,175,55,0.1)',
};

export default function Sidebar({ page, setPage, lang: parentLang, setLang: notifyParent }) {
  const [lang, setLangState] = useState(() => getLang());
  const [hovered, setHovered] = useState(null);
  const [time, setTime] = useState(new Date());

  useEffect(() => {
    const t = setInterval(() => setTime(new Date()), 1000);
    return () => clearInterval(t);
  }, []);

  useEffect(() => {
    if (parentLang && parentLang !== lang) setLangState(parentLang);
  }, [parentLang]);

  const switchLang = (l) => {
    if (l === lang) return;
    persistLang(l); setLangState(l); notifyParent?.(l);
  };

  const isAr = lang === 'ar';
  const font = isAr ? "'Cairo',sans-serif" : "'Cairo',sans-serif";
  const mono = "'JetBrains Mono',monospace";

  const NAV = [
    {
      id: 'dashboard', label: isAr ? 'لوحة التحكم' : 'Dashboard',
      icon: (active) => (
        <svg width="16" height="16" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth={active ? 2 : 1.6} strokeLinecap="round" strokeLinejoin="round">
          <rect x="3" y="3" width="7" height="7" rx="1.5"/><rect x="14" y="3" width="7" height="7" rx="1.5"/>
          <rect x="3" y="14" width="7" height="7" rx="1.5"/><rect x="14" y="14" width="7" height="7" rx="1.5"/>
        </svg>
      )
    },
    {
      id: 'reports', label: isAr ? 'التقارير' : 'Reports',
      icon: (active) => (
        <svg width="16" height="16" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth={active ? 2 : 1.6} strokeLinecap="round" strokeLinejoin="round">
          <path d="M14 2H6a2 2 0 0 0-2 2v16a2 2 0 0 0 2 2h12a2 2 0 0 0 2-2V8z"/>
          <polyline points="14 2 14 8 20 8"/><line x1="8" y1="13" x2="16" y2="13"/><line x1="8" y1="17" x2="12" y2="17"/>
        </svg>
      )
    },
    {
      id: 'new', label: isAr ? 'تقرير جديد' : 'New Report', accent: true,
      icon: (active) => (
        <svg width="16" height="16" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round">
          <circle cx="12" cy="12" r="9"/><line x1="12" y1="8" x2="12" y2="16"/><line x1="8" y1="12" x2="16" y2="12"/>
        </svg>
      )
    },
    {
      id: 'ai', label: isAr ? 'المساعد الذكي' : 'AI Assistant', badge: 'AI',
      icon: (active) => (
        <svg width="16" height="16" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth={active ? 2 : 1.6} strokeLinecap="round">
          <path d="M12 2a2 2 0 0 1 2 2c0 .74-.4 1.39-1 1.73V7h1a7 7 0 0 1 7 7h1a1 1 0 0 1 0 2h-1v1a2 2 0 0 1-2 2H5a2 2 0 0 1-2-2v-1H2a1 1 0 0 1 0-2h1a7 7 0 0 1 7-7h1V5.73c-.6-.34-1-.99-1-1.73a2 2 0 0 1 2-2z"/>
          <circle cx="9" cy="13" r="1" fill="currentColor"/><circle cx="15" cy="13" r="1" fill="currentColor"/>
        </svg>
      )
    },
  ];

  const hhmm = time.toLocaleTimeString('en-US', { hour: '2-digit', minute: '2-digit', hour12: false });
  const ddmm = time.toLocaleDateString('en-US', { day: '2-digit', month: 'short' }).toUpperCase();

  return (
    <aside style={{
      width: 230, minWidth: 230, height: '100vh',
      background: S.bg,
      display: 'flex', flexDirection: 'column',
      borderInlineEnd: `1px solid ${S.border}`,
      position: 'relative', zIndex: 20, flexShrink: 0,
      fontFamily: font,
    }}>
      <style>{`
        @keyframes sb-glow { 0%,100%{opacity:.6} 50%{opacity:1} }
        @keyframes sb-dot { 0%,100%{transform:scale(1)} 50%{transform:scale(1.4)} }
        @keyframes sb-badge { 0%,100%{transform:translateY(0)} 50%{transform:translateY(-1px)} }
      `}</style>

      {/* Top gold accent line */}
      <div style={{
        position: 'absolute', top: 0, left: 0, right: 0, height: 2,
        background: `linear-gradient(90deg, transparent, ${S.gold}, transparent)`,
        animation: 'sb-glow 4s ease-in-out infinite',
      }}/>

      {/* Brand header */}
      <div style={{ padding: '28px 20px 20px', borderBottom: `1px solid ${S.border}` }}>
        <div style={{ display: 'flex', alignItems: 'center', gap: 12, marginBottom: 16, direction: 'ltr' }}>
          {/* Emblem */}
          <div style={{
            width: 40, height: 40, borderRadius: 11, flexShrink: 0,
            background: `linear-gradient(135deg, rgba(212,175,55,0.18), rgba(212,175,55,0.06))`,
            border: `1px solid rgba(212,175,55,0.3)`,
            display: 'flex', alignItems: 'center', justifyContent: 'center',
            boxShadow: '0 0 20px rgba(212,175,55,0.12)',
          }}>
            <svg width="22" height="22" viewBox="0 0 24 24" fill="none">
              <path d="M12 2L2 7l10 5 10-5-10-5z" fill="rgba(212,175,55,0.2)" stroke="#d4af37" strokeWidth="1.4" strokeLinejoin="round"/>
              <path d="M2 17l10 5 10-5" stroke="#d4af37" strokeWidth="1.3" strokeLinejoin="round" opacity="0.5"/>
              <path d="M2 12l10 5 10-5" stroke="#d4af37" strokeWidth="1.3" strokeLinejoin="round" opacity="0.3"/>
            </svg>
          </div>
          <div>
            <div style={{ fontSize: 14, fontWeight: 700, color: S.text, letterSpacing: '0.04em', fontFamily: "'Cairo',sans-serif" }}>SFDA</div>
            <div style={{ fontSize: 9, color: S.goldDim, letterSpacing: '0.22em', fontFamily: mono, marginTop: 2 }}>TRAVEL INTEL</div>
          </div>
        </div>

        {/* Live clock chip */}
        <div style={{
          background: 'rgba(212,175,55,0.05)',
          border: `1px solid rgba(212,175,55,0.12)`,
          borderRadius: 10, padding: '10px 14px',
          display: 'flex', justifyContent: 'space-between', alignItems: 'center',
          direction: 'ltr',
        }}>
          <div>
            <div style={{ fontSize: 22, fontWeight: 300, color: S.gold, fontFamily: mono, lineHeight: 1, letterSpacing: '0.02em' }}>{hhmm}</div>
            <div style={{ fontSize: 9, color: 'rgba(212,175,55,0.4)', fontFamily: mono, marginTop: 3, letterSpacing: '0.15em' }}>{ddmm} · KSA</div>
          </div>
          <div style={{ textAlign: 'right' }}>
            <div style={{ display: 'flex', alignItems: 'center', gap: 5, justifyContent: 'flex-end', marginBottom: 5 }}>
              <span style={{ fontSize: 9, color: '#4ade80', fontFamily: mono, letterSpacing: '0.1em' }}>LIVE</span>
              <span style={{ width: 6, height: 6, borderRadius: '50%', background: '#4ade80', display: 'inline-block', boxShadow: '0 0 8px #4ade80', animation: 'sb-dot 2s ease-in-out infinite' }}/>
            </div>
            <div style={{ fontSize: 9, color: 'rgba(255,255,255,0.15)', fontFamily: mono, letterSpacing: '0.08em' }}>SYS·OK</div>
          </div>
        </div>
      </div>

      {/* Navigation */}
      <nav style={{ flex: 1, padding: '18px 12px', overflowY: 'auto' }}>
        <div style={{ fontSize: 9, letterSpacing: '0.22em', color: 'rgba(212,175,55,0.3)', marginBottom: 10, paddingInlineStart: 10, fontFamily: mono }}>
          NAVIGATION
        </div>

        {NAV.map(item => {
          const active = page === item.id;
          const hover = hovered === item.id;

          if (item.accent) {
            return (
              <button key={item.id}
                onClick={() => setPage(item.id)}
                onMouseEnter={() => setHovered(item.id)}
                onMouseLeave={() => setHovered(null)}
                style={{
                  width: '100%', display: 'flex', alignItems: 'center', gap: 10,
                  padding: '10px 14px', borderRadius: 10, marginBottom: 4,
                  cursor: 'pointer', fontFamily: font, fontSize: 13, fontWeight: 600,
                  textAlign: 'start', direction: isAr ? 'rtl' : 'ltr',
                  transition: 'all 0.2s ease',
                  border: `1px solid ${active || hover ? 'rgba(212,175,55,0.4)' : 'rgba(212,175,55,0.18)'}`,
                  background: active
                    ? 'linear-gradient(135deg, rgba(212,175,55,0.2), rgba(212,175,55,0.08))'
                    : hover
                    ? 'linear-gradient(135deg, rgba(212,175,55,0.12), rgba(212,175,55,0.04))'
                    : 'linear-gradient(135deg, rgba(212,175,55,0.07), transparent)',
                  color: active ? S.gold : hover ? 'rgba(212,175,55,0.85)' : 'rgba(212,175,55,0.65)',
                  boxShadow: active ? '0 4px 16px rgba(212,175,55,0.12)' : 'none',
                }}>
                <span>{item.icon(active)}</span>
                <span style={{ flex: 1 }}>{item.label}</span>
              </button>
            );
          }

          return (
            <button key={item.id}
              onClick={() => setPage(item.id)}
              onMouseEnter={() => setHovered(item.id)}
              onMouseLeave={() => setHovered(null)}
              style={{
                width: '100%', display: 'flex', alignItems: 'center', gap: 10,
                padding: '10px 14px', borderRadius: 10, border: 'none', marginBottom: 3,
                cursor: 'pointer', fontFamily: font, fontSize: 13, fontWeight: active ? 600 : 400,
                textAlign: 'start', direction: isAr ? 'rtl' : 'ltr',
                transition: 'all 0.18s ease',
                background: active ? S.active : hover ? 'rgba(255,255,255,0.03)' : 'transparent',
                color: active ? S.gold : hover ? 'rgba(232,237,248,0.8)' : S.textSec,
                borderInlineStart: `2px solid ${active ? S.gold : 'transparent'}`,
                position: 'relative',
              }}>
              <span style={{ opacity: active ? 1 : 0.65 }}>{item.icon(active)}</span>
              <span style={{ flex: 1 }}>{item.label}</span>
              {item.badge && (
                <span style={{
                  fontSize: 8.5, fontWeight: 700, padding: '2px 6px', borderRadius: 5,
                  background: 'linear-gradient(135deg, #7c3aed, #6d28d9)',
                  color: 'rgba(255,255,255,0.95)', fontFamily: mono,
                  letterSpacing: '0.08em', animation: 'sb-badge 3s ease-in-out infinite',
                }}>AI</span>
              )}
            </button>
          );
        })}
      </nav>

      {/* Language switcher */}
      <div style={{ padding: '12px 12px', borderTop: `1px solid ${S.border}` }}>
        <div style={{ fontSize: 9, letterSpacing: '0.22em', color: 'rgba(212,175,55,0.3)', marginBottom: 8, paddingInlineStart: 10, fontFamily: mono }}>
          LANGUAGE
        </div>
        <div style={{ display: 'flex', gap: 6, padding: '0 2px' }}>
          {[['ar', 'عربي'], ['en', 'EN']].map(([l, label]) => (
            <button key={l} onClick={() => switchLang(l)}
              style={{
                flex: 1, padding: '8px 4px', cursor: 'pointer', transition: 'all 0.15s',
                borderRadius: 8,
                border: `1px solid ${lang === l ? 'rgba(212,175,55,0.35)' : 'rgba(255,255,255,0.06)'}`,
                background: lang === l ? 'rgba(212,175,55,0.1)' : 'transparent',
                color: lang === l ? S.gold : 'rgba(232,237,248,0.3)',
                fontFamily: l === 'ar' ? "'Cairo',sans-serif" : mono,
                fontSize: l === 'ar' ? 13 : 11, fontWeight: 500,
              }}>{label}</button>
          ))}
        </div>
      </div>

      {/* User strip */}
      <div style={{ padding: '14px 16px 22px', borderTop: `1px solid ${S.border}`, display: 'flex', alignItems: 'center', gap: 10, direction: isAr ? 'rtl' : 'ltr' }}>
        <div style={{
          width: 34, height: 34, borderRadius: 9, flexShrink: 0,
          background: 'linear-gradient(135deg, rgba(212,175,55,0.2), rgba(212,175,55,0.07))',
          border: `1px solid rgba(212,175,55,0.25)`,
          display: 'flex', alignItems: 'center', justifyContent: 'center',
          fontSize: 12, fontWeight: 700, color: S.gold, fontFamily: "'Cairo',sans-serif",
        }}>
          {isAr ? 'هـ' : 'SU'}
        </div>
        <div style={{ flex: 1, minWidth: 0 }}>
          <div style={{ fontSize: 12, fontWeight: 500, color: 'rgba(232,237,248,0.65)', overflow: 'hidden', textOverflow: 'ellipsis', whiteSpace: 'nowrap', fontFamily: font }}>
            {isAr ? 'مستخدم النظام' : 'System User'}
          </div>
          <div style={{ fontSize: 9, color: S.goldDim, fontFamily: mono, letterSpacing: '0.12em', marginTop: 2 }}>SFDA · ADMIN</div>
        </div>
        <div style={{ width: 7, height: 7, borderRadius: '50%', background: '#4ade80', boxShadow: '0 0 8px #4ade80', flexShrink: 0 }}/>
      </div>
    </aside>
  );
}
