import { useState, useEffect } from 'react';
import ReactDOM from 'react-dom';
import Sidebar from './components/Sidebar';
import WorldMap from './components/WorldMap';
import NewReportForm from './components/NewReportForm';
import ReportDetail from './components/ReportDetail';
import AIAssistant from './components/AIAssistant';
import { getReports, getStats, deleteReport as apiDelete, getInlinePDFUrl, getPreviewUrl } from './services/api';
import { downloadPDF } from './utils/downloadPDF';
import { T, getLang, setLang as saveLang } from './i18n';

const RISK_COLORS = { low:'#22c55e', medium:'#f59e0b', high:'#ef4444' };

const FEED = [
  { id:1, icon:'üìã', iconBg:'#dbeafe', title:'US FDA Announces Expedited Drug Approval Process', summary:'New fast-track pathway for critical medications.', source:'Bloomberg', time:'3 hours ago', impact:null },
  { id:2, icon:'üåê', iconBg:'#fed7aa', title:'G20 Leaders Summit Addresses Global Trade Tensions', summary:'New agreements on tariff reductions impact pharmaceutical trade.', source:'Reuters', time:'Just now', impact:'HIGH IMPACT' },
  { id:3, icon:'üìà', iconBg:'#fce7f3', title:'Saudi-Japan Bilateral Summit Strengthens Healthcare Ties', summary:'Joint investment in pharmaceutical research announced.', source:'Arab News', time:'2 hours ago', impact:'HIGH IMPACT' },
];

const WHO = [
  { id:1, type:'Outbreak', typeColor:'#ef4444', title:'H5N1 Avian Influenza Monitoring', summary:'Enhanced surveillance for avian influenza cases in poultry workers.', region:'Southeast Asia', date:'2/18/2026', tags:['Vietnam','Thailand','Indonesia'] },
  { id:2, type:'Update',   typeColor:'#f59e0b', title:'Middle East Respiratory Syndrome Update', summary:'Sporadic cases of MERS-CoV continue to be reported.', region:'Middle East', date:'2/18/2026', tags:['Saudi Arabia','UAE','Qatar'] },
];

// ‚îÄ‚îÄ‚îÄ Portal Modal ‚Äî renders on document.body, bypasses overflow:hidden ‚îÄ‚îÄ‚îÄ
function PreviewModal({ modal, onClose }) {
  if (!modal) return null;
  return ReactDOM.createPortal(
    <div style={{ position:'fixed', inset:0, zIndex:99999, display:'flex', flexDirection:'column', background:'rgba(0,0,0,0.85)' }}>
      <div style={{ background:'#0d1829', padding:'12px 20px', display:'flex', alignItems:'center', justifyContent:'space-between', flexShrink:0, borderBottom:'1px solid rgba(255,255,255,0.1)' }}>
        <span style={{ color:'white', fontSize:14, fontWeight:700 }}>
          {modal.type === 'pdf' ? 'üìÑ' : 'üëÅÔ∏è'} {modal.title}
        </span>
        <div style={{ display:'flex', gap:10 }}>
          <button onClick={() => downloadPDF(modal.reportId, `SFDA_${modal.title || modal.reportId}.pdf`)}
            style={{ padding:'8px 18px', background:'#3b82f6', color:'white', borderRadius:8, fontSize:12, fontWeight:700, border:'none', cursor:'pointer', display:'flex', alignItems:'center', gap:6 }}>
            ‚¨á Download PDF
          </button>
          <button onClick={onClose}
            style={{ padding:'8px 18px', background:'rgba(255,255,255,0.12)', color:'white', border:'1px solid rgba(255,255,255,0.2)', borderRadius:8, fontSize:12, fontWeight:700, cursor:'pointer' }}>
            ‚úï Close
          </button>
        </div>
      </div>
      <div style={{ flex:1, overflow:'hidden', background:'white' }}>
        <iframe src={modal.url} title="Preview" style={{ width:'100%', height:'100%', border:'none', display:'block' }} />
      </div>
    </div>,
    document.body
  );
}

function Toast({ toast }) {
  if (!toast) return null;
  return ReactDOM.createPortal(
    <div style={{ position:'fixed', top:20, right:24, zIndex:99998, background:toast.color, color:'white', borderRadius:10, padding:'12px 20px', fontSize:13, fontWeight:600, boxShadow:'0 6px 24px rgba(0,0,0,0.25)', animation:'toastIn 0.25s ease', maxWidth:400 }}>
      {toast.msg}
    </div>,
    document.body
  );
}

export default function App() {
  const [page, setPage]               = useState('dashboard');
  const [selectedReport, setSelected] = useState(null); // report id for detail view
  const [reports, setReports]         = useState([]);
  const [stats, setStats]             = useState({ total:0, ready:0, draft:0 });
  const [toast, setToast]             = useState(null);
  const [loading, setLoading]         = useState(true);
  const [previewModal, setPreviewModal] = useState(null); // { url, reportId, title, type }
  const [lang, setLangState] = useState(getLang());

  const setLang = (l) => { saveLang(l); setLangState(l); };
  const t = T[lang];
  const isAr = lang === 'ar';

  const showToast = (msg, color='#22c55e') => { setToast({ msg, color }); setTimeout(() => setToast(null), 3500); };

  const openPreview = (reportId, title, type) => {
    // Use inline URL for iframe (no download prompt), direct URL for download button
    const url = type === 'pdf' ? getInlinePDFUrl(reportId) : getPreviewUrl(reportId);
    setPreviewModal({ url, reportId, title, type });
  };

  const loadData = async () => {
    try {
      const [rRes, sRes] = await Promise.all([getReports(), getStats()]);
      setReports(rRes.data);
      setStats(sRes.data);
    } catch (e) { console.error(e); }
    finally { setLoading(false); }
  };

  useEffect(() => { loadData(); }, []);

  const handleNewReport = (report) => {
    loadData(); // fresh from server ‚Äî prevents duplicates
    showToast(`‚úì "${report.title || report.event_name}" created!`);
    setPage('reports');
  };

  const handleDelete = async (id, e) => {
    e?.stopPropagation();
    if (!window.confirm('Delete this report?')) return;
    try {
      await apiDelete(id);
      setReports(prev => prev.filter(r => r.id !== id));
      setStats(prev => ({ ...prev, total: Math.max(0, prev.total - 1) }));
      if (selectedReport === id) { setSelected(null); setPage('reports'); }
      showToast('Report deleted.', '#f59e0b');
    } catch { showToast('Delete failed.', '#ef4444'); }
  };

  const openDetail = (id) => { setSelected(id); setPage('detail'); };

  const upcoming = reports.filter(r => r.status === 'ready');
  const statCards = [
    { val:stats.total, label:'Total Reports', bg:'#eff6ff', ib:'#dbeafe', ic:'#3b82f6', t:'doc'   },
    { val:stats.ready, label:'Ready',          bg:'#f0fdf4', ib:'#dcfce7', ic:'#22c55e', t:'check' },
    { val:stats.draft, label:'Drafts',         bg:'#fffbeb', ib:'#fef9c3', ic:'#f59e0b', t:'clock' },
  ];

  const pageTitle = { new:'Create New Report', reports:'Reports', detail:'Report Detail', dashboard:'Executive Dashboard' };
  const pageSubtitle = { new:'AI-powered Milken-style travel intelligence briefing', reports:`${reports.length} reports total`, detail:'', dashboard:'SFDA Travel Intelligence Overview' };

  return (
    <div style={{ display:'flex', height:'100vh', fontFamily:"'DM Sans',-apple-system,sans-serif", background:'#f1f5f9', overflow:'hidden' }}>
      <style>{`
        @keyframes toastIn { from{opacity:0;transform:translateY(-10px)} to{opacity:1;transform:none} }
        @keyframes spin    { to{transform:rotate(360deg)} }
        * { box-sizing:border-box }
        ::-webkit-scrollbar{width:5px} ::-webkit-scrollbar-track{background:transparent} ::-webkit-scrollbar-thumb{background:#e2e8f0;border-radius:3px}
      `}</style>

      <Toast toast={toast} />
      <PreviewModal modal={previewModal} onClose={() => setPreviewModal(null)} />
      <Sidebar page={page} setPage={p => { setPage(p); setSelected(null); }} />

      <div style={{ flex:1, display:'flex', flexDirection:'column', overflow:'hidden' }}>
        {/* Header */}
        {page !== 'ai' && (
          <div style={{ background:'white', borderBottom:'1px solid #e9eef5', padding:'17px 28px 15px', display:'flex', justifyContent:'space-between', alignItems:'flex-start', flexShrink:0 }}>
            <div>
              <h1 style={{ margin:0, fontSize:24, fontWeight:700, color:'#0d1829', letterSpacing:'-0.025em' }}>{pageTitle[page]}</h1>
              <p style={{ margin:'4px 0 0', fontSize:12.5, color:'#94a3b8' }}>{pageSubtitle[page]}</p>
            </div>
            {page !== 'new' && (
              <button onClick={() => setPage('new')}
                style={{ display:'flex', alignItems:'center', gap:7, padding:'9px 18px', background:'#0d1829', color:'white', border:'none', borderRadius:8, fontSize:13, fontWeight:600, cursor:'pointer' }}
                onMouseEnter={e => e.currentTarget.style.background='#1e293b'}
                onMouseLeave={e => e.currentTarget.style.background='#0d1829'}>
                + New Report
              </button>
            )}
          </div>
        )}

        {/* AI page */}
        {page === 'ai' && <div style={{ flex:1, display:'flex', flexDirection:'column', overflow:'hidden' }}><AIAssistant reports={reports} /></div>}

        {/* Main content */}
        {page !== 'ai' && (
          <div style={{ flex:1, overflowY:'auto', padding: page === 'new' ? '28px 32px 50px' : '20px 28px 32px' }}>

            {/* ‚îÄ‚îÄ DASHBOARD ‚îÄ‚îÄ */}
            {page === 'dashboard' && (
              <>
                <div style={{ display:'grid', gridTemplateColumns:'repeat(3,1fr)', gap:14, marginBottom:18 }}>
                  {statCards.map((s, i) => (
                    <div key={i} style={{ background:s.bg, borderRadius:12, padding:'16px 20px', display:'flex', alignItems:'center', gap:15, border:'1px solid rgba(0,0,0,0.04)', transition:'transform 0.15s,box-shadow 0.15s', cursor:'default' }}
                      onMouseEnter={e => { e.currentTarget.style.transform='translateY(-1px)'; e.currentTarget.style.boxShadow='0 4px 12px rgba(0,0,0,0.08)'; }}
                      onMouseLeave={e => { e.currentTarget.style.transform='none'; e.currentTarget.style.boxShadow='none'; }}>
                      <div style={{ width:44, height:44, borderRadius:11, background:s.ib, display:'flex', alignItems:'center', justifyContent:'center', flexShrink:0 }}>
                        {s.t==='doc'   && <svg width="20" height="20" viewBox="0 0 24 24" fill="none"><rect x="3" y="4" width="18" height="16" rx="2" stroke={s.ic} strokeWidth="2"/><line x1="8" y1="9" x2="16" y2="9" stroke={s.ic} strokeWidth="2" strokeLinecap="round"/><line x1="8" y1="13" x2="13" y2="13" stroke={s.ic} strokeWidth="2" strokeLinecap="round"/></svg>}
                        {s.t==='check' && <svg width="20" height="20" viewBox="0 0 24 24" fill="none"><circle cx="12" cy="12" r="9" stroke={s.ic} strokeWidth="2"/><path d="M8 12l3 3 5-5" stroke={s.ic} strokeWidth="2" strokeLinecap="round" strokeLinejoin="round"/></svg>}
                        {s.t==='clock' && <svg width="20" height="20" viewBox="0 0 24 24" fill="none"><circle cx="12" cy="12" r="9" stroke={s.ic} strokeWidth="2"/><path d="M12 7v5l3 2" stroke={s.ic} strokeWidth="2" strokeLinecap="round"/></svg>}
                      </div>
                      <div>
                        <div style={{ fontSize:30, fontWeight:700, color:'#0d1829', lineHeight:1 }}>{loading ? '‚Ä¶' : s.val}</div>
                        <div style={{ fontSize:12, color:'#64748b', marginTop:4 }}>{s.label}</div>
                      </div>
                    </div>
                  ))}
                </div>

                {/* Map */}
                <div style={{ background:'white', borderRadius:14, boxShadow:'0 1px 4px rgba(0,0,0,0.06)', overflow:'hidden', border:'1px solid #edf2f7', marginBottom:18 }}>
                  <div style={{ padding:'13px 20px', display:'flex', justifyContent:'space-between', alignItems:'center', borderBottom:'1px solid #f1f5f9' }}>
                    <div style={{ display:'flex', alignItems:'center', gap:10 }}>
                      <div style={{ width:30, height:30, borderRadius:8, background:'#eff6ff', display:'flex', alignItems:'center', justifyContent:'center' }}>
                        <svg width="16" height="16" viewBox="0 0 24 24" fill="none"><circle cx="12" cy="12" r="9" stroke="#3b82f6" strokeWidth="2"/><ellipse cx="12" cy="12" rx="4" ry="9" stroke="#3b82f6" strokeWidth="1.5"/><line x1="3" y1="9" x2="21" y2="9" stroke="#3b82f6" strokeWidth="1.5"/><line x1="3" y1="15" x2="21" y2="15" stroke="#3b82f6" strokeWidth="1.5"/></svg>
                      </div>
                      <div>
                        <div style={{ fontWeight:700, fontSize:14.5, color:'#0d1829' }}>Global Intelligence Heat Map</div>
                        <div style={{ fontSize:11, color:'#94a3b8', marginTop:1 }}>Real-time geopolitical risk assessment</div>
                      </div>
                    </div>
                    <div style={{ display:'flex', alignItems:'center', gap:14 }}>
                      {[['#22c55e','Low'],['#f59e0b','Medium'],['#ef4444','High']].map(([c,l]) => (
                        <span key={l} style={{ display:'flex', alignItems:'center', gap:5, fontSize:12, color:'#64748b' }}>
                          <span style={{ width:8, height:8, borderRadius:'50%', background:c, display:'inline-block' }}/>{l}
                        </span>
                      ))}
                    </div>
                  </div>
                  <WorldMap upcomingReports={upcoming} />
                </div>

                {/* Feeds */}
                <div style={{ display:'grid', gridTemplateColumns:'1fr 1fr', gap:16, marginBottom:20 }}>
                  <div style={{ background:'white', borderRadius:14, padding:'18px 20px', border:'1px solid #edf2f7' }}>
                    <div style={{ display:'flex', alignItems:'center', gap:10, marginBottom:14 }}>
                      <div style={{ width:30, height:30, borderRadius:8, background:'#eff6ff', display:'flex', alignItems:'center', justifyContent:'center', fontSize:14 }}>üì∞</div>
                      <span style={{ fontWeight:700, fontSize:15, color:'#0d1829' }}>Political Intelligence Feed</span>
                    </div>
                    {FEED.map((item, i) => (
                      <div key={item.id} style={{ display:'flex', gap:12, padding:'12px 0', borderBottom: i < FEED.length-1 ? '1px solid #f1f5f9' : 'none', alignItems:'flex-start' }}>
                        <div style={{ width:36, height:36, borderRadius:9, background:item.iconBg, display:'flex', alignItems:'center', justifyContent:'center', fontSize:16, flexShrink:0 }}>{item.icon}</div>
                        <div style={{ flex:1, minWidth:0 }}>
                          <div style={{ fontWeight:600, fontSize:13, color:'#0d1829', marginBottom:2, lineHeight:1.35 }}>{item.title}</div>
                          <div style={{ fontSize:12, color:'#64748b', marginBottom:4 }}>{item.summary}</div>
                          <div style={{ fontSize:11, color:'#94a3b8' }}>{item.source} ¬∑ {item.time}</div>
                        </div>
                        {item.impact && <span style={{ border:'1.5px solid #22c55e', color:'#16a34a', borderRadius:20, padding:'3px 9px', fontSize:10, fontWeight:700, whiteSpace:'nowrap', flexShrink:0 }}>{item.impact}</span>}
                      </div>
                    ))}
                  </div>
                  <div style={{ background:'white', borderRadius:14, padding:'18px 20px', border:'1px solid #edf2f7' }}>
                    <div style={{ display:'flex', alignItems:'center', gap:10, marginBottom:14 }}>
                      <div style={{ width:30, height:30, borderRadius:8, background:'#fffbeb', display:'flex', alignItems:'center', justifyContent:'center', fontSize:14 }}>‚ö†Ô∏è</div>
                      <span style={{ fontWeight:700, fontSize:15, color:'#0d1829' }}>WHO Health Alerts</span>
                    </div>
                    {WHO.map((item, i) => (
                      <div key={item.id} style={{ paddingBottom:16, borderBottom: i < WHO.length-1 ? '1px solid #f1f5f9' : 'none', marginBottom: i < WHO.length-1 ? 16 : 0 }}>
                        <span style={{ background:item.typeColor, color:'white', borderRadius:6, padding:'2px 10px', fontSize:11, fontWeight:700 }}>{item.type}</span>
                        <div style={{ fontWeight:700, fontSize:14, color:'#0d1829', margin:'7px 0 4px' }}>{item.title}</div>
                        <div style={{ fontSize:12, color:'#64748b', marginBottom:7 }}>{item.summary}</div>
                        <div style={{ display:'flex', flexWrap:'wrap', gap:5 }}>
                          {item.tags.map(t => <span key={t} style={{ background:'#f1f5f9', color:'#475569', borderRadius:6, padding:'2px 9px', fontSize:11 }}>{t}</span>)}
                        </div>
                      </div>
                    ))}
                  </div>
                </div>

                {/* Recent reports */}
                <div>
                  <div style={{ display:'flex', justifyContent:'space-between', alignItems:'center', marginBottom:14 }}>
                    <div style={{ fontWeight:700, fontSize:17, color:'#0d1829' }}>Recent Reports</div>
                    <button onClick={() => setPage('reports')} style={{ background:'none', border:'none', cursor:'pointer', color:'#3b82f6', fontSize:13, fontWeight:600 }}>View All ‚Üí</button>
                  </div>
                  {reports.slice(0, 5).map(r => (
                    <div key={r.id} onClick={() => openDetail(r.id)}
                      style={{ background:'white', borderRadius:12, padding:'14px 18px', display:'flex', alignItems:'center', gap:14, border:'1px solid #edf2f7', cursor:'pointer', transition:'all 0.15s', marginBottom:10 }}
                      onMouseEnter={e => { e.currentTarget.style.boxShadow='0 4px 14px rgba(0,0,0,0.09)'; e.currentTarget.style.transform='translateX(2px)'; }}
                      onMouseLeave={e => { e.currentTarget.style.boxShadow='none'; e.currentTarget.style.transform='none'; }}>
                      <div style={{ width:38, height:38, borderRadius:9, background:'#eff6ff', display:'flex', alignItems:'center', justifyContent:'center', flexShrink:0, fontSize:18 }}>üìÑ</div>
                      <div style={{ flex:1, minWidth:0 }}>
                        <div style={{ fontWeight:600, fontSize:14, color:'#0d1829', marginBottom:3 }}>{r.title || r.event_name}</div>
                        <div style={{ display:'flex', gap:14, fontSize:12, color:'#94a3b8' }}>
                          <span>üìç {r.city}, {r.country}</span>
                          <span>üìÖ {r.start_date}</span>
                          {r.ai_generated && <span style={{ color:'#3b82f6', fontWeight:600, fontSize:11 }}>‚ú¶ AI</span>}
                        </div>
                      </div>
                      <div style={{ display:'flex', gap:8, alignItems:'center', flexShrink:0 }}>
                        <span style={{ padding:'3px 11px', borderRadius:20, fontSize:11, fontWeight:700, background:r.status==='ready'?'#dcfce7':'#fef9c3', color:r.status==='ready'?'#15803d':'#a16207' }}>
                          {r.status?.charAt(0).toUpperCase()+r.status?.slice(1)}
                        </span>
                        <span style={{ color:'#94a3b8', fontSize:12 }}>‚Üí</span>
                      </div>
                    </div>
                  ))}
                  {reports.length === 0 && !loading && (
                    <div style={{ background:'white', borderRadius:12, padding:'36px 18px', textAlign:'center', color:'#94a3b8', border:'1px solid #edf2f7', fontSize:14 }}>
                      No reports yet.
                      <button onClick={() => setPage('new')} style={{ marginTop:12, padding:'8px 16px', background:'#0d1829', color:'white', border:'none', borderRadius:7, cursor:'pointer', fontSize:12, fontWeight:600, display:'block', margin:'12px auto 0' }}>
                        Create First Report
                      </button>
                    </div>
                  )}
                </div>
              </>
            )}

            {/* ‚îÄ‚îÄ REPORTS CARD GRID ‚îÄ‚îÄ */}
            {page === 'reports' && (
              <>
                {/* Header */}
                <div style={{ display:'flex', justifyContent:'space-between', alignItems:'flex-start', marginBottom:24 }}>
                  <div>
                    <h2 style={{ margin:0, fontSize:22, fontWeight:800, color:'#0d1829' }}>{t.reportsTitle}</h2>
                    <p style={{ margin:'4px 0 0', fontSize:13, color:'#94a3b8' }}>View and manage all your travel intelligence reports</p>
                  </div>
                  <button onClick={() => setPage('new')}
                    style={{ display:'flex', alignItems:'center', gap:8, padding:'10px 20px', background:'#1c3370', color:'white', border:'none', borderRadius:10, fontSize:13.5, fontWeight:700, cursor:'pointer', boxShadow:'0 4px 12px rgba(28,51,112,0.3)' }}>
                    + {t.newReport}
                  </button>
                </div>

                {/* Search + filter bar */}
                <div style={{ display:'flex', gap:12, marginBottom:24 }}>
                  <div style={{ flex:1, position:'relative' }}>
                    <span style={{ position:'absolute', left:14, top:'50%', transform:'translateY(-50%)', color:'#94a3b8', fontSize:15 }}>üîç</span>
                    <input
                      placeholder={t.searchPlaceholder}
                      style={{ width:'100%', padding:'10px 14px 10px 40px', border:'1px solid #e2e8f0', borderRadius:10, fontSize:13.5, color:'#334155', background:'white', outline:'none', boxSizing:'border-box' }}
                    />
                  </div>
                  <button style={{ display:'flex', alignItems:'center', gap:7, padding:'10px 18px', background:'white', border:'1px solid #e2e8f0', borderRadius:10, fontSize:13.5, color:'#64748b', fontWeight:600, cursor:'pointer' }}>
                    ‚öô Filter
                  </button>
                </div>

                {/* Card grid */}
                {reports.length === 0 ? (
                  <div style={{ textAlign:'center', padding:'80px 20px', color:'#94a3b8', background:'white', borderRadius:16, border:'1px solid #e2e8f0' }}>
                    <div style={{ fontSize:36, marginBottom:12 }}>üìã</div>
                    <div style={{ fontSize:15, fontWeight:600, marginBottom:8 }}>No reports yet</div>
                    <button onClick={() => setPage('new')} style={{ background:'none', border:'none', color:'#3b82f6', cursor:'pointer', fontWeight:700, fontSize:14 }}>+ Create your first report</button>
                  </div>
                ) : (
                  <div style={{ display:'grid', gridTemplateColumns:'repeat(auto-fill, minmax(300px, 1fr))', gap:16 }}>
                    {reports.map(r => (
                      <div key={r.id}
                        onClick={() => openDetail(r.id)}
                        style={{ background:'white', borderRadius:16, border:'1px solid #e2e8f0', padding:'20px', cursor:'pointer', position:'relative', boxShadow:'0 1px 4px rgba(0,0,0,0.05)', transition:'box-shadow 0.15s, transform 0.15s' }}
                        onMouseEnter={e => { e.currentTarget.style.boxShadow='0 6px 20px rgba(0,0,0,0.1)'; e.currentTarget.style.transform='translateY(-2px)'; }}
                        onMouseLeave={e => { e.currentTarget.style.boxShadow='0 1px 4px rgba(0,0,0,0.05)'; e.currentTarget.style.transform='translateY(0)'; }}>

                        {/* top row: icon + status + delete */}
                        <div style={{ display:'flex', alignItems:'flex-start', justifyContent:'space-between', marginBottom:14 }}>
                          <div style={{ width:42, height:42, background:'#f0f4ff', borderRadius:12, display:'flex', alignItems:'center', justifyContent:'center', fontSize:20 }}>
                            üìÑ
                          </div>
                          <div style={{ display:'flex', alignItems:'center', gap:8 }}>
                            <span style={{ background: r.status==='ready'?'#f0fdf4':'#fef9c3', color: r.status==='ready'?'#15803d':'#a16207', border: `1px solid ${r.status==='ready'?'#bbf7d0':'#fde68a'}`, borderRadius:20, padding:'3px 11px', fontSize:11.5, fontWeight:700 }}>
                              {r.status?.charAt(0).toUpperCase()+r.status?.slice(1) || 'Draft'}
                            </span>
                            <button
                              onClick={e => handleDelete(r.id, e)}
                              style={{ background:'none', border:'none', cursor:'pointer', color:'#cbd5e1', fontSize:16, lineHeight:1, padding:'2px 4px', borderRadius:5 }}
                              onMouseEnter={e => e.currentTarget.style.color='#ef4444'}
                              onMouseLeave={e => e.currentTarget.style.color='#cbd5e1'}>‚úï</button>
                          </div>
                        </div>

                        {/* title */}
                        <div style={{ fontWeight:800, fontSize:15, color:'#0f172a', marginBottom:10, lineHeight:1.4 }}>
                          {r.title || r.event_name}
                          {r.ai_generated && <span style={{ marginRight:6, fontSize:10, background:'#eff6ff', color:'#3b82f6', borderRadius:4, padding:'1px 6px', fontWeight:700 }}>AI</span>}
                        </div>

                        {/* location + dates */}
                        <div style={{ display:'flex', flexDirection:'column', gap:5, marginBottom:16 }}>
                          <div style={{ display:'flex', alignItems:'center', gap:7, fontSize:12.5, color:'#2563eb', fontWeight:500 }}>
                            <span>üìç</span>
                            <span>{r.city}{r.country ? `, ${r.country}` : ''}</span>
                          </div>
                          <div style={{ display:'flex', alignItems:'center', gap:7, fontSize:12.5, color:'#64748b' }}>
                            <span>üìÖ</span>
                            <span>{r.start_date}{r.end_date ? ` - ${r.end_date}` : ''}</span>
                          </div>
                        </div>

                        {/* divider */}
                        <div style={{ borderTop:'1px solid #f1f5f9', paddingTop:12, display:'flex', alignItems:'center', justifyContent:'space-between' }}>
                          <span style={{ fontSize:11.5, color:'#94a3b8' }}>
                            Created {r.created_at ? new Date(r.created_at).toLocaleDateString('en-US', { month:'short', day:'numeric', year:'numeric' }) : '‚Äî'}
                          </span>
                          <div style={{ display:'flex', gap:6 }} onClick={e => e.stopPropagation()}>
                            <button
                              onClick={e => { e.stopPropagation(); openPreview(r.id, r.title||r.event_name, 'html'); }}
                              style={{ padding:'4px 10px', background:'#f1f5f9', color:'#374151', border:'none', borderRadius:6, fontSize:11, fontWeight:700, cursor:'pointer' }}>
                              üëÅÔ∏è
                            </button>
                            <button
                              onClick={e => { e.stopPropagation(); downloadPDF(r.id, `SFDA_${r.event_name || r.id}.pdf`); }}
                              style={{ padding:'4px 10px', background:'#eff6ff', color:'#3b82f6', border:'none', borderRadius:6, fontSize:11, fontWeight:700, cursor:'pointer' }}>
                              ‚¨á PDF
                            </button>
                          </div>
                        </div>
                      </div>
                    ))}
                  </div>
                )}
              </>
            )}

            {/* ‚îÄ‚îÄ REPORT DETAIL ‚îÄ‚îÄ */}
            {page === 'detail' && selectedReport && (
              <ReportDetail
                lang={lang}
                reportId={selectedReport}
                onBack={() => setPage('reports')}
                onPDFOpen={(id, title, type) => openPreview(id, title, type)}
              />
            )}

            {/* ‚îÄ‚îÄ NEW REPORT ‚îÄ‚îÄ */}
            {page === 'new' && (
              <NewReportForm onSuccess={handleNewReport} onCancel={() => setPage('dashboard')} />
            )}

          </div>
        )}
      </div>
    </div>
  );
}
