â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€ */
function DropdownPortal({ pos, options, value, dir, font, onSelect, onClose }) {
  // Close when clicking outside
  useEffect(() => {
    const handler = (e) => {
      // If click target is NOT inside the panel, close
      const panel = document.getElementById('sfda-dropdown-panel');
      if (panel && !panel.contains(e.target)) {
        onClose();
      }
    };
    // Use capture phase so we get it before anything else
    document.addEventListener('mousedown', handler, true);
    return () => document.removeEventListener('mousedown', handler, true);
  }, [onClose]);

  return ReactDOM.createPortal(
    <div
      id="sfda-dropdown-panel"
      style={{
        position: 'fixed',
        top: pos.top, left: pos.left, width: pos.width,
        zIndex: 2147483647, // max possible z-index
        background: 'white',
        borderRadius: 8,
        border: '1.5px solid #bfdbfe',
        boxShadow: '0 12px 40px rgba(0,0,0,0.18)',
        maxHeight: 280,
        overflowY: 'auto',
        padding: '4px 0',
        direction: dir,
      }}
    >
      {options.map((opt) => {
        const isSel = opt.value === value;
        return (
          <div
            key={opt.value}
            onClick={() => { onSelect(opt.value); }}
            style={{
              padding: '10px 14px',
              fontSize: 13,
              fontFamily: font,
              cursor: 'pointer',
              color: isSel ? '#1d4ed8' : '#111827',
              background: isSel ? '#eff6ff' : 'white',
              fontWeight: isSel ? 600 : 400,
              textAlign: dir === 'rtl' ? 'right' : 'left',
              userSelect: 'none',
              display: 'flex',
              alignItems: 'center',
              gap: 8,
            }}
            onMouseEnter={e => { e.currentTarget.style.background = isSel ? '#dbeafe' : '#f1f5f9'; }}
            onMouseLeave={e => { e.currentTarget.style.background = isSel ? '#eff6ff' : 'white'; }}
          >
            {isSel && <span style={{ fontSize: 10, color: '#1d4ed8' }}>âœ“</span>}
            {opt.label}
          </div>
        );
      })}
    </div>,
    document.body
  );
}

function CustomDropdown({ value, onChange, options, placeholder, font, dir }) {
  const [open, setOpen] = useState(false);
  const [pos,  setPos]  = useState({ top: 0, left: 0, width: 0 });
  const btnRef          = useRef(null);

  const toggle = () => {
    if (open) { setOpen(false); return; }
    const r      = btnRef.current.getBoundingClientRect();
    const panelH = Math.min(options.length * 42 + 8, 284);
    const below  = r.bottom + panelH + 6 < window.innerHeight;
    setPos({
      top:   below ? r.bottom + 4 : r.top - panelH - 4,
      left:  r.left,
      width: r.width,
    });
    setOpen(true);
  };

  const select = (val) => { onChange(val); setOpen(false); };

  const selected = options.find(o => o.value === value);
  const label    = selected ? selected.label : placeholder;

  return (
    <div style={{ position: 'relative' }}>
      <button
        ref={btnRef}
        type="button"
        onClick={toggle}
        style={{
          width: '100%',
          padding: '10px 12px',
          border: `1.5px solid ${open ? '#3b82f6' : '#e2e8f0'}`,
          borderRadius: 8,
          fontSize: 13,
          color: selected ? '#0d1829' : '#9ca3af',
          background: 'white',
          cursor: 'pointer',
          fontFamily: font,
          boxSizing: 'border-box',
          textAlign: dir === 'rtl' ? 'right' : 'left',
          direction: dir,
          display: 'flex',
          alignItems: 'center',
          justifyContent: 'space-between',
          outline: 'none',
          userSelect: 'none',
        }}
      >
        <span style={{ flex: 1, overflow: 'hidden', textOverflow: 'ellipsis', whiteSpace: 'nowrap' }}>
          {label}
        </span>
        <svg width="10" height="10" viewBox="0 0 10 10" fill="none"
          style={{ flexShrink: 0, marginLeft: 6, opacity: 0.45,
                   transform: open ? 'rotate(180deg)' : 'none',
                   transition: 'transform 0.15s' }}>
          <path d="M1 3l4 4 4-4" stroke="#374151" strokeWidth="1.5" strokeLinecap="round" strokeLinejoin="round"/>
        </svg>
      </button>

      {open && (
        <DropdownPortal
          pos={pos}
          options={options}
          value={value}
          dir={dir}
          font={font}
          onSelect={select}
          onClose={() => setOpen(false)}
        />
      )}
    </div>
  );
}

