    ...COUNTRIES.map(c => ({ value: isAr ? c.ar : c.en, label: isAr ? c.ar : c.en })),
  ];

  const handleGenerate = async () => {
    if (submittingRef.current) return;
    if (!form.event_name || !form.city || !form.country || !form.start_date) {
      setError(l.required); return;
    }
    submittingRef.current = true;
    setIsSubmitting(true);
    setError('');
    setPhase('generating');
    let stepIdx = 0;
    const stepInterval = setInterval(() => {
      setGenStep(l.genSteps[stepIdx % l.genSteps.length]);
      stepIdx++;
    }, 700);
    try {
      const payload = { ...form, title: form.title || form.event_name };
      let newReport;
      if (createdReportRef.current) {
        newReport = createdReportRef.current;
      } else {
        const createRes = await createReport(payload);
        newReport = createRes.data;
        createdReportRef.current = newReport;
      }
      setGenStep(isAr ? 'Ø§Ø³ØªØ¯Ø¹Ø§Ø¡ Ø§Ù„Ø°ÙƒØ§Ø¡ Ø§Ù„Ø§ØµØ·Ù†Ø§Ø¹ÙŠ Ù„Ø¥Ù†Ø´Ø§Ø¡ Ø§Ù„Ù…Ø­ØªÙˆÙ‰...' : 'Calling AI to generate full report content...');
      const genRes = await generateAI(newReport.id);
      const fullReport = genRes.data.report;
      clearInterval(stepInterval);
      setReport(fullReport);
      setPhase('done');
      onSuccess(fullReport);
    } catch (e) {
      clearInterval(stepInterval);
      const raw = e.response?.data?.error || e.message || 'Generation failed.';
      const friendly = raw.includes('OPENAI_API_KEY') || raw.includes('ANTHROPIC_API_KEY') || raw.includes('Authentication')
        ? 'âš ï¸ API key missing or invalid. Set ANTHROPIC_API_KEY in backend/.env'
        : raw.includes('quota') || raw.includes('billing')
        ? 'âš ï¸ API quota exceeded. Check billing at console.anthropic.com'
        : `âš ï¸ ${raw}`;
      setError(friendly);
      setPhase('form');
    } finally {
      submittingRef.current = false;
      setIsSubmitting(false);
    }
  };

  const iS = {
    width: '100%', padding: '10px 12px', border: '1.5px solid #e2e8f0',
    borderRadius: 8, fontSize: 13, color: '#0d1829', background: 'white',
    outline: 'none', fontFamily: font, transition: 'border-color 0.15s',
    boxSizing: 'border-box', direction: dir,
  };
  const lS = {
    fontSize: 10.5, fontWeight: 700, color: '#64748b', letterSpacing: '0.06em',
    textTransform: 'uppercase', marginBottom: 6, display: 'block', fontFamily: font,
  };
  const onFocus = e => { e.target.style.borderColor = '#3b82f6'; };
  const onBlur  = e => { e.target.style.borderColor = '#e2e8f0'; };

  const Block = ({ icon, title, badge, children }) => (
    <div style={{ background: 'white', borderRadius: 12, padding: '20px 22px', border: '1px solid #edf2f7', marginBottom: 14, boxShadow: '0 1px 3px rgba(0,0,0,0.04)' }}>
