            <a href={getPDFUrl(report.id)} download style={{ padding: '7px 16px', background: '#3b82f6', color: 'white', borderRadius: 7, fontSize: 12, fontWeight: 600, textDecoration: 'none', fontFamily: font }}>â¬‡ {isAr ? 'ØªØ­Ù…ÙŠÙ„' : 'Download'}</a>
