    );
  }

  /* â”€â”€ Generating phase â”€â”€ */
  if (phase === 'generating') {
    return (
      <div style={{ display: 'flex', flexDirection: 'column', alignItems: 'center', justifyContent: 'center', height: '60vh', gap: 20, direction: dir }}>
        <div style={{ width: 64, height: 64, borderRadius: 16, background: 'linear-gradient(135deg,#1d4ed8,#3b82f6)', display: 'flex', alignItems: 'center', justifyContent: 'center', fontSize: 28, boxShadow: '0 4px 20px rgba(59,130,246,0.4)' }}>âœ¦</div>
        <h2 style={{ fontSize: 22, fontWeight: 700, color: '#0d1829', margin: 0, fontFamily: font }}>{l.genTitle}</h2>
        <p style={{ color: '#64748b', fontSize: 13, textAlign: 'center', maxWidth: 400, fontFamily: font }}>{l.genSubtitle}</p>
        <div style={{ background: 'white', borderRadius: 12, padding: '16px 24px', border: '1px solid #e2e8f0', minWidth: 340, textAlign: 'center' }}>
          <div style={{ display: 'flex', alignItems: 'center', gap: 10, justifyContent: 'center' }}>
            <span style={{ display: 'inline-block', animation: 'spin 1s linear infinite', fontSize: 18 }}>â³</span>
            <span style={{ fontSize: 13, color: '#374151', fontWeight: 500, fontFamily: font }}>{genStep}</span>
          </div>
          <div style={{ marginTop: 14, background: '#f1f5f9', borderRadius: 8, height: 6, overflow: 'hidden' }}>
            <div style={{ height: '100%', background: 'linear-gradient(90deg,#3b82f6,#1d4ed8)', animation: 'progress 30s linear forwards', borderRadius: 8 }} />
          </div>
        </div>
        <style>{`@keyframes spin{to{transform:rotate(360deg)}}@keyframes progress{from{width:0}to{width:95%}}`}</style>
      </div>
    );
  }

  /* â”€â”€ Done phase â”€â”€ */
  if (phase === 'done' && report) {
    const counts = [
      report.visit_objectives?.length,
      report.agenda?.length,
      report.delegation?.length,
      report.speakers?.length,
      report.bilateral_meetings?.length,
      report.conference_tracks?.length,
      (report.sessions?.day1?.length || 0) + (report.sessions?.day2?.length || 0) + (report.sessions?.day3?.length || 0),
      report.weather?.length,
      report.prayer_times?.length,
    ];
    return (
      <div style={{ display: 'flex', flexDirection: 'column', alignItems: 'center', gap: 20, padding: '40px 20px', direction: dir }}>
        <div style={{ width: 64, height: 64, borderRadius: '50%', background: '#dcfce7', display: 'flex', alignItems: 'center', justifyContent: 'center', fontSize: 28 }}>âœ…</div>
        <h2 style={{ fontSize: 22, fontWeight: 700, color: '#0d1829', margin: 0, fontFamily: font }}>{l.doneTitle}</h2>
        <p style={{ color: '#64748b', fontSize: 13, textAlign: 'center', maxWidth: 440, fontFamily: font }}>
          <strong>{report.event_name}</strong> Â· {report.city}, {report.country} Â· {report.speakers?.length || 0} {l.speakersSuffix} Â· {report.bilateral_meetings?.length || 0} {l.meetingsSuffix}
