          <button onClick={() => setPreviewMode('html')} style={{ padding: '12px 24px', background: 'white', color: '#1d4ed8', border: '1.5px solid #1d4ed8', borderRadius: 10, fontSize: 13, fontWeight: 700, cursor: 'pointer', fontFamily: font }}>{l.previewHTML}</button>
          <button onClick={onCancel} style={{ padding: '12px 24px', background: '#f1f5f9', color: '#374151', border: 'none', borderRadius: 10, fontSize: 13, fontWeight: 600, cursor: 'pointer', fontFamily: font }}>{l.backDash}</button>
        </div>
        <div style={{ background: 'white', borderRadius: 14, border: '1px solid #edf2f7', padding: '18px 22px', maxWidth: 560, width: '100%' }}>
          <div style={{ fontWeight: 700, fontSize: 14, color: '#0d1829', marginBottom: 12, fontFamily: font }}>{l.sectionsGen}</div>
          {l.sections.map(([icon, label], i) => (
            <div key={i} style={{ display: 'flex', justifyContent: 'space-between', padding: '5px 0', borderBottom: '1px solid #f8fafc', fontSize: 13, fontFamily: font }}>
              <span style={{ color: '#374151' }}>{icon} {label}</span>
              <span style={{ background: '#eff6ff', color: '#3b82f6', borderRadius: 12, padding: '1px 10px', fontSize: 11, fontWeight: 700 }}>{counts[i] || 0}</span>
            </div>
          ))}
        </div>
      </div>
    );
  }

  /* â”€â”€ Form â”€â”€ */
  return (
    <div style={{ maxWidth: 720, margin: '0 auto', paddingBottom: 50, direction: dir, fontFamily: font }}>
      {/* Header */}
      <div style={{ textAlign: 'center', marginBottom: 28 }}>
        <div style={{ width: 54, height: 54, borderRadius: 16, background: 'linear-gradient(135deg,#1d4ed8,#3b82f6)', display: 'flex', alignItems: 'center', justifyContent: 'center', fontSize: 22, margin: '0 auto 14px', boxShadow: '0 4px 16px rgba(59,130,246,0.38)' }}>âœ¦</div>
        <h2 style={{ margin: 0, fontSize: 24, fontWeight: 700, color: '#0d1829', fontFamily: font }}>{l.pageTitle}</h2>
        <p style={{ margin: '6px 0 0', fontSize: 13, color: '#94a3b8', fontFamily: font }}>{l.pageSubtitle}</p>
      </div>

      {error && (
        <div style={{ background: '#fef2f2', border: '1.5px solid #fecaca', borderRadius: 10, padding: '12px 16px', marginBottom: 16, color: '#dc2626', fontSize: 13, fontFamily: font }}>
          {error}
        </div>
      )}

      {/* Step indicator */}
      <div style={{ display: 'flex', justifyContent: 'center', alignItems: 'center', marginBottom: 28 }}>
