          <div key={s} style={{ display: 'flex', alignItems: 'center' }}>
            <div style={{ display: 'flex', flexDirection: 'column', alignItems: 'center' }}>
              <div style={{ width: 30, height: 30, borderRadius: '50%', background: i === 0 ? '#1d4ed8' : '#f1f5f9', display: 'flex', alignItems: 'center', justifyContent: 'center', fontSize: 12, color: i === 0 ? 'white' : '#94a3b8', border: `2px solid ${i === 0 ? '#1d4ed8' : '#e2e8f0'}` }}>{i + 1}</div>
              <span style={{ fontSize: 10.5, color: i === 0 ? '#1d4ed8' : '#94a3b8', marginTop: 5, fontWeight: i === 0 ? 700 : 400, fontFamily: font }}>{s}</span>
            </div>
            {i < arr.length - 1 && <div style={{ width: 60, height: 2, background: '#e2e8f0', margin: '0 4px', marginBottom: 16 }} />}
          </div>
        ))}
      </div>

      {/* Event Details */}
      <Block icon="ðŸ“„" title={l.eventDetails}>
        <div style={{ marginBottom: 14 }}>
          <label style={lS}>{l.eventName} <span style={{ color: '#ef4444' }}>*</span></label>
          <input style={iS} placeholder={l.eventNamePH} value={form.event_name} onChange={set('event_name')} onFocus={onFocus} onBlur={onBlur} />
        </div>
        <div style={{ marginBottom: 14 }}>
          <label style={lS}>{l.reportTitle}</label>
          <input style={iS} placeholder={l.reportTitlePH} value={form.title} onChange={set('title')} onFocus={onFocus} onBlur={onBlur} />
        </div>
        <div style={{ display: 'grid', gridTemplateColumns: '1fr 1fr', gap: 12, marginBottom: 14 }}>
          <div>
            <label style={lS}>{l.eventType}</label>
            <CustomDropdown
              value={form.event_type}
              onChange={v => setVal('event_type', v)}
              options={eventTypeOptions}
              placeholder={eventTypeOptions[0]?.label}
              font={font}
              dir={dir}
            />
          </div>
          <div>
            <label style={lS}>{l.reportLang}</label>
            <CustomDropdown
              value={form.language}
              onChange={v => setVal('language', v)}
              options={languageOptions}
              placeholder={languageOptions[0]?.label}
              font={font}
              dir={dir}
            />
          </div>
        </div>
        <div>
          <label style={lS}>{l.eventWebsite}</label>
          <div style={{ position: 'relative' }}>
            <span style={{ position: 'absolute', [isAr ? 'right' : 'left']: 11, top: '50%', transform: 'translateY(-50%)', fontSize: 13, color: '#94a3b8', pointerEvents: 'none' }}>ðŸ”—</span>
            <input style={{ ...iS, [isAr ? 'paddingRight' : 'paddingLeft']: 32 }} placeholder={l.websitePH} value={form.event_website} onChange={set('event_website')} onFocus={onFocus} onBlur={onBlur} />
          </div>
        </div>
      </Block>

      {/* Location */}
      <Block icon="ðŸ“" title={l.location}>
        <div style={{ display: 'grid', gridTemplateColumns: '1fr 1fr 1fr', gap: 12 }}>
          <div>
            <label style={lS}>{l.city} <span style={{ color: '#ef4444' }}>*</span></label>
            <input style={iS} placeholder={l.cityPH} value={form.city} onChange={set('city')} onFocus={onFocus} onBlur={onBlur} />
          </div>
          <div>
            <label style={lS}>{l.country} <span style={{ color: '#ef4444' }}>*</span></label>
            <CustomDropdown
              value={form.country}
              onChange={v => setVal('country', v)}
              options={countryOptions}
              placeholder={l.countryPH}
              font={font}
              dir={dir}
            />
          </div>
          <div>
            <label style={lS}>{l.venue}</label>
            <input style={iS} placeholder={l.venuePH} value={form.venue} onChange={set('venue')} onFocus={onFocus} onBlur={onBlur} />
          </div>
        </div>
      </Block>

      {/* Dates */}
      <Block icon="ðŸ“…" title={l.dates}>
        <div style={{ display: 'grid', gridTemplateColumns: '1fr 1fr', gap: 12 }}>
          <div>
            <label style={lS}>{l.startDate} <span style={{ color: '#ef4444' }}>*</span></label>
            <input type="date" style={iS} value={form.start_date} onChange={set('start_date')} />
          </div>
          <div>
            <label style={lS}>{l.endDate}</label>
            <input type="date" style={iS} value={form.end_date} onChange={set('end_date')} />
          </div>
        </div>
      </Block>

      {/* Traveler */}
      <Block icon="ðŸ‘¤" title={l.traveler}>
        <div style={{ display: 'grid', gridTemplateColumns: '1fr 1fr', gap: 12 }}>
          <div><label style={lS}>{l.travelerName}</label><input style={iS} value={form.traveler_name} onChange={set('traveler_name')} onFocus={onFocus} onBlur={onBlur} /></div>
          <div><label style={lS}>{l.travelerTitle}</label><input style={iS} value={form.traveler_title} onChange={set('traveler_title')} onFocus={onFocus} onBlur={onBlur} /></div>
        </div>
      </Block>

      {/* Context */}
      <Block icon="ðŸ’¬" title={l.context} badge={l.contextBadge}>
        <textarea style={{ ...iS, resize: 'vertical', minHeight: 90, lineHeight: 1.65 }} placeholder={l.contextPH} value={form.context} onChange={set('context')} onFocus={onFocus} onBlur={onBlur} />
      </Block>

      {/* Actions */}
      <div style={{ display: 'flex', justifyContent: 'center', gap: 12, paddingTop: 4 }}>
        <button onClick={onCancel} style={{ padding: '11px 26px', background: 'white', border: '1.5px solid #e2e8f0', borderRadius: 9, fontSize: 13, fontWeight: 600, cursor: 'pointer', color: '#64748b', fontFamily: font }}>
          {l.cancel}
        </button>
        <button
          onClick={handleGenerate}
          disabled={isSubmitting}
          style={{ padding: '11px 28px', background: isSubmitting ? '#93c5fd' : 'linear-gradient(135deg,#1d4ed8,#3b82f6)', border: 'none', borderRadius: 9, color: 'white', fontSize: 13, fontWeight: 700, cursor: isSubmitting ? 'not-allowed' : 'pointer', display: 'flex', alignItems: 'center', gap: 9, boxShadow: '0 4px 16px rgba(59,130,246,0.38)', minWidth: 200, justifyContent: 'center', opacity: isSubmitting ? 0.7 : 1, fontFamily: font }}
        >
          <span>âœ¦</span> {isSubmitting ? l.generating : l.generate}
        </button>
      </div>
    </div>
  );
}
