import { useState } from 'react';
import { createReport, generateAI, getPDFUrl, getPreviewUrl } from '../services/api';

const COUNTRIES = ['USA','UK','Germany','France','Japan','China','Singapore','Australia','UAE','Saudi Arabia','India','Brazil','Canada','South Korea','Netherlands','Switzerland','Sweden','Norway','Italy','Spain','Egypt','Turkey'];
const EVENT_TYPES = ['Conference','Summit','Exhibition','Forum','Workshop','Bilateral Meeting','Scientific Congress','Trade Show'];

const GEN_STEPS = [
  'Analyzing destination risk profile...',
  'Collecting geopolitical intelligence...',
  'Reviewing WHO health advisories...',
  'Generating conference details...',
  'Building delegation and agenda...',
  'Compiling speakers and bilateral meetings...',
  'Adding consulate, weather & prayer times...',
  'Finalizing report structure...',
];

export default function NewReportForm({ onSuccess, onCancel }) {
  const [form, setForm] = useState({
    title:'', event_name:'', event_type:'', language:'English',
    event_website:'', city:'', country:'', venue:'',
    start_date:'', end_date:'', context:'',
    traveler_name:'Dr. Hisham Al Jadhey',
    traveler_title:'ÿßŸÑÿ±ÿ¶Ÿäÿ≥ ÿßŸÑÿ™ŸÜŸÅŸäÿ∞Ÿä ŸÑŸÑŸáŸäÿ¶ÿ© ÿßŸÑÿπÿßŸÖÿ© ŸÑŸÑÿ∫ÿ∞ÿßÿ° ŸàÿßŸÑÿØŸàÿßÿ°',
  });
  const [phase, setPhase] = useState('form'); // form | generating | done
  const [genStep, setGenStep] = useState('');
  const [report, setReport] = useState(null);
  const [error, setError] = useState('');

  const set = k => e => setForm(p => ({...p, [k]: e.target.value}));

  const handleGenerate = async () => {
    if (!form.event_name || !form.city || !form.country || !form.start_date) {
      setError('Please fill in: Event Name, City, Country, and Start Date.');
      return;
    }
    setError('');
    setPhase('generating');

    let stepIdx = 0;
    const stepInterval = setInterval(() => {
      setGenStep(GEN_STEPS[stepIdx % GEN_STEPS.length]);
      stepIdx++;
    }, 700);

    try {
      // 1. Create report
      const payload = { ...form, title: form.title || form.event_name };
      const createRes = await createReport(payload);
      const newReport = createRes.data;

      // 2. Generate AI content
      setGenStep('Calling AI to generate full report content...');
      const genRes = await generateAI(newReport.id);
      const fullReport = genRes.data.report;

      clearInterval(stepInterval);
      setReport(fullReport);
      setPhase('done');
      onSuccess(fullReport);
    } catch (e) {
      clearInterval(stepInterval);
      setError(e.response?.data?.error || 'Generation failed. Please check your API key and try again.');
      setPhase('form');
    }
  };

  const iS = {
    width:'100%', padding:'10px 12px', border:'1.5px solid #e2e8f0', borderRadius:8,
    fontSize:13, color:'#0d1829', background:'white', outline:'none', fontFamily:'inherit',
    transition:'border-color 0.15s', boxSizing:'border-box',
  };
  const lS = { fontSize:10.5, fontWeight:700, color:'#64748b', letterSpacing:'0.08em', textTransform:'uppercase', marginBottom:6, display:'block' };
  const onFocus = e => e.target.style.borderColor = '#3b82f6';
  const onBlur  = e => e.target.style.borderColor = '#e2e8f0';

  const Block = ({ icon, title, badge, children }) => (
    <div style={{ background:'white', borderRadius:12, padding:'20px 22px', border:'1px solid #edf2f7', marginBottom:14, boxShadow:'0 1px 3px rgba(0,0,0,0.04)' }}>
      <div style={{ display:'flex', alignItems:'center', gap:10, marginBottom:18 }}>
        <div style={{ width:28, height:28, borderRadius:7, background:'#eff6ff', display:'flex', alignItems:'center', justifyContent:'center', fontSize:14 }}>{icon}</div>
        <span style={{ fontWeight:700, fontSize:15, color:'#0d1829' }}>{title}</span>
        {badge && <span style={{ fontSize:10, fontWeight:600, color:'#94a3b8', background:'#f1f5f9', padding:'2px 7px', borderRadius:4 }}>{badge}</span>}
      </div>
      {children}
    </div>
  );

  // ‚îÄ‚îÄ GENERATING SCREEN ‚îÄ‚îÄ
  if (phase === 'generating') {
    return (
      <div style={{ display:'flex', flexDirection:'column', alignItems:'center', justifyContent:'center', height:'60vh', gap:20 }}>
        <div style={{ width:64, height:64, borderRadius:16, background:'linear-gradient(135deg,#1d4ed8,#3b82f6)', display:'flex', alignItems:'center', justifyContent:'center', fontSize:28, boxShadow:'0 4px 20px rgba(59,130,246,0.4)' }}>‚ú¶</div>
        <h2 style={{ fontSize:22, fontWeight:700, color:'#0d1829', margin:0 }}>Generating Your Report</h2>
        <p style={{ color:'#64748b', fontSize:13, textAlign:'center', maxWidth:400 }}>AI is compiling a complete intelligence briefing matching the Milken PDF format...</p>
        <div style={{ background:'white', borderRadius:12, padding:'16px 24px', border:'1px solid #e2e8f0', minWidth:340, textAlign:'center', boxShadow:'0 2px 8px rgba(0,0,0,0.06)' }}>
          <div style={{ display:'flex', alignItems:'center', gap:10, justifyContent:'center' }}>
            <span style={{ display:'inline-block', animation:'spin 1s linear infinite', fontSize:18 }}>‚è≥</span>
            <span style={{ fontSize:13, color:'#374151', fontWeight:500 }}>{genStep}</span>
          </div>
          <div style={{ marginTop:14, background:'#f1f5f9', borderRadius:8, height:6, overflow:'hidden' }}>
            <div style={{ height:'100%', background:'linear-gradient(90deg,#3b82f6,#1d4ed8)', animation:'progress 8s linear forwards', borderRadius:8 }}/>
          </div>
        </div>
        <style>{`
          @keyframes spin{to{transform:rotate(360deg)}}
          @keyframes progress{from{width:0}to{width:95%}}
        `}</style>
      </div>
    );
  }

  // ‚îÄ‚îÄ DONE SCREEN ‚îÄ‚îÄ
  if (phase === 'done' && report) {
    return (
      <div style={{ display:'flex', flexDirection:'column', alignItems:'center', justifyContent:'center', gap:20, padding:'40px 20px' }}>
        <div style={{ width:64, height:64, borderRadius:'50%', background:'#dcfce7', display:'flex', alignItems:'center', justifyContent:'center', fontSize:28 }}>‚úÖ</div>
        <h2 style={{ fontSize:22, fontWeight:700, color:'#0d1829', margin:0 }}>Report Generated!</h2>
        <p style={{ color:'#64748b', fontSize:13, textAlign:'center', maxWidth:440 }}>
          Your travel intelligence briefing for <strong>{report.event_name}</strong> in <strong>{report.city}, {report.country}</strong> has been generated successfully with {report.speakers?.length || 0} speakers, {report.bilateral_meetings?.length || 0} bilateral meetings, and all PDF sections.
        </p>
        <div style={{ display:'flex', gap:12, flexWrap:'wrap', justifyContent:'center' }}>
          <a href={getPDFUrl(report.id)} target="_blank" rel="noreferrer"
            style={{ padding:'12px 24px', background:'linear-gradient(135deg,#1c3370,#2563eb)', color:'white', borderRadius:10, fontSize:13, fontWeight:700, textDecoration:'none', display:'flex', alignItems:'center', gap:8, boxShadow:'0 4px 14px rgba(29,78,216,0.35)' }}>
            üì• Download PDF
          </a>
          <a href={getPreviewUrl(report.id)} target="_blank" rel="noreferrer"
            style={{ padding:'12px 24px', background:'white', color:'#1d4ed8', border:'1.5px solid #1d4ed8', borderRadius:10, fontSize:13, fontWeight:700, textDecoration:'none', display:'flex', alignItems:'center', gap:8 }}>
            üëÅÔ∏è Preview HTML
          </a>
          <button onClick={onCancel}
            style={{ padding:'12px 24px', background:'#f1f5f9', color:'#374151', border:'none', borderRadius:10, fontSize:13, fontWeight:600, cursor:'pointer' }}>
            Back to Dashboard
          </button>
        </div>

        {/* Report summary */}
        <div style={{ background:'white', borderRadius:14, border:'1px solid #edf2f7', padding:'18px 22px', maxWidth:600, width:'100%', boxShadow:'0 2px 8px rgba(0,0,0,0.06)' }}>
          <div style={{ fontWeight:700, fontSize:14, color:'#0d1829', marginBottom:12 }}>Report Sections Generated:</div>
          {[
            ['üéØ', 'Visit Objectives', report.visit_objectives?.length],
            ['üìÖ', 'Agenda Days', report.agenda?.length],
            ['üë•', 'Delegation Members', report.delegation?.length],
            ['üé§', 'Key Speakers', report.speakers?.length],
            ['ü§ù', 'Bilateral Meetings', report.bilateral_meetings?.length],
            ['üî∑', 'Conference Tracks', report.conference_tracks?.length],
            ['üìã', 'Sessions', (report.sessions?.day1?.length||0)+(report.sessions?.day2?.length||0)+(report.sessions?.day3?.length||0)],
            ['üå§Ô∏è', 'Weather Days', report.weather?.length],
            ['üïå', 'Prayer Time Entries', report.prayer_times?.length],
          ].map(([icon, label, count]) => (
            <div key={label} style={{ display:'flex', justifyContent:'space-between', padding:'5px 0', borderBottom:'1px solid #f8fafc', fontSize:13 }}>
              <span style={{ color:'#374151' }}>{icon} {label}</span>
              <span style={{ background:'#eff6ff', color:'#3b82f6', borderRadius:12, padding:'1px 10px', fontSize:11, fontWeight:700 }}>{count || 0}</span>
            </div>
          ))}
        </div>
      </div>
    );
  }

  // ‚îÄ‚îÄ FORM ‚îÄ‚îÄ
  return (
    <div style={{ maxWidth:720, margin:'0 auto', paddingBottom:50 }}>
      <div style={{ textAlign:'center', marginBottom:28 }}>
        <div style={{ width:54, height:54, borderRadius:16, background:'linear-gradient(135deg,#1d4ed8,#3b82f6)', display:'flex', alignItems:'center', justifyContent:'center', fontSize:22, margin:'0 auto 14px', boxShadow:'0 4px 16px rgba(59,130,246,0.38)' }}>‚ú¶</div>
        <h2 style={{ margin:0, fontSize:24, fontWeight:700, color:'#0d1829', letterSpacing:'-0.02em' }}>Create New Report</h2>
        <p style={{ margin:'6px 0 0', fontSize:13, color:'#94a3b8' }}>AI will generate a complete Milken-style travel intelligence briefing PDF</p>
      </div>

      {error && (
        <div style={{ background:'#fef2f2', border:'1.5px solid #fecaca', borderRadius:10, padding:'12px 16px', marginBottom:16, color:'#dc2626', fontSize:13 }}>
          ‚ö†Ô∏è {error}
        </div>
      )}

      {/* Step bar */}
      <div style={{ display:'flex', justifyContent:'center', alignItems:'center', gap:0, marginBottom:28 }}>
        {['Event','Location','Dates','Context'].map((s,i,arr) => (
          <div key={s} style={{ display:'flex', alignItems:'center' }}>
            <div style={{ display:'flex', flexDirection:'column', alignItems:'center' }}>
              <div style={{ width:30, height:30, borderRadius:'50%', background:i===arr.length-1?'#1d4ed8':'#f1f5f9', display:'flex', alignItems:'center', justifyContent:'center', fontSize:12, color:i===arr.length-1?'white':'#94a3b8', border:`2px solid ${i===arr.length-1?'#1d4ed8':'#e2e8f0'}` }}>
                {i+1}
              </div>
              <span style={{ fontSize:10.5, color:i===arr.length-1?'#1d4ed8':'#94a3b8', marginTop:5, fontWeight:i===arr.length-1?700:400 }}>{s}</span>
            </div>
            {i<arr.length-1 && <div style={{ width:72, height:2, background:'#e2e8f0', margin:'0 2px', marginBottom:16 }}/>}
          </div>
        ))}
      </div>

      <Block icon="üìÑ" title="Event Details">
        <div style={{ marginBottom:14 }}>
          <label style={lS}>Event Name <span style={{ color:'#ef4444' }}>*</span></label>
          <input style={iS} placeholder="e.g., Milken Institute Global Conference 2026" value={form.event_name} onChange={set('event_name')} onFocus={onFocus} onBlur={onBlur}/>
        </div>
        <div style={{ marginBottom:14 }}>
          <label style={lS}>Report Title (Arabic/English)</label>
          <input style={iS} placeholder="e.g., ÿßŸÑŸÖÿ§ÿ™ŸÖÿ± ÿßŸÑÿπÿßŸÑŸÖŸä 2026" value={form.title} onChange={set('title')} onFocus={onFocus} onBlur={onBlur}/>
        </div>
        <div style={{ display:'grid', gridTemplateColumns:'1fr 1fr', gap:12, marginBottom:14 }}>
          <div>
            <label style={lS}>Event Type</label>
            <select style={{...iS, cursor:'pointer'}} value={form.event_type} onChange={set('event_type')}>
              <option value="">Select event type</option>
              {EVENT_TYPES.map(t=><option key={t}>{t}</option>)}
            </select>
          </div>
          <div>
            <label style={lS}>Report Language</label>
            <select style={{...iS, cursor:'pointer'}} value={form.language} onChange={set('language')}>
              <option>English</option><option>Arabic</option>
            </select>
          </div>
        </div>
        <div>
          <label style={lS}>Event Website</label>
          <div style={{ position:'relative' }}>
            <span style={{ position:'absolute', left:11, top:'50%', transform:'translateY(-50%)', fontSize:13, color:'#94a3b8' }}>üîó</span>
            <input style={{...iS, paddingLeft:32}} placeholder="https://event-website.com" value={form.event_website} onChange={set('event_website')} onFocus={onFocus} onBlur={onBlur}/>
          </div>
        </div>
      </Block>

      <Block icon="üìç" title="Location">
        <div style={{ display:'grid', gridTemplateColumns:'1fr 1fr 1fr', gap:12 }}>
          <div>
            <label style={lS}>City <span style={{ color:'#ef4444' }}>*</span></label>
            <input style={iS} placeholder="e.g., Los Angeles" value={form.city} onChange={set('city')} onFocus={onFocus} onBlur={onBlur}/>
          </div>
          <div>
            <label style={lS}>Country <span style={{ color:'#ef4444' }}>*</span></label>
            <select style={{...iS, cursor:'pointer'}} value={form.country} onChange={set('country')}>
              <option value="">Select country</option>
              {COUNTRIES.map(c=><option key={c}>{c}</option>)}
            </select>
          </div>
          <div>
            <label style={lS}>Venue</label>
            <input style={iS} placeholder="e.g., Beverly Hilton" value={form.venue} onChange={set('venue')} onFocus={onFocus} onBlur={onBlur}/>
          </div>
        </div>
      </Block>

      <Block icon="üìÖ" title="Event Dates">
        <div style={{ display:'grid', gridTemplateColumns:'1fr 1fr', gap:12 }}>
          <div>
            <label style={lS}>Start Date <span style={{ color:'#ef4444' }}>*</span></label>
            <input type="date" style={{...iS, cursor:'pointer'}} value={form.start_date} onChange={set('start_date')}/>
          </div>
          <div>
            <label style={lS}>End Date</label>
            <input type="date" style={{...iS, cursor:'pointer'}} value={form.end_date} onChange={set('end_date')}/>
          </div>
        </div>
      </Block>

      <Block icon="üë§" title="Traveler Details">
        <div style={{ display:'grid', gridTemplateColumns:'1fr 1fr', gap:12 }}>
          <div>
            <label style={lS}>Traveler Name</label>
            <input style={iS} value={form.traveler_name} onChange={set('traveler_name')} onFocus={onFocus} onBlur={onBlur}/>
          </div>
          <div>
            <label style={lS}>Title</label>
            <input style={iS} value={form.traveler_title} onChange={set('traveler_title')} onFocus={onFocus} onBlur={onBlur}/>
          </div>
        </div>
      </Block>

      <Block icon="üí¨" title="Additional Context" badge="OPTIONAL">
        <textarea style={{...iS, resize:'vertical', minHeight:90, lineHeight:1.65}}
          placeholder="Add any context, objectives, or requirements for this trip..."
          value={form.context} onChange={set('context')} onFocus={onFocus} onBlur={onBlur}/>
      </Block>

      <div style={{ display:'flex', justifyContent:'center', gap:12, paddingTop:4 }}>
        <button onClick={onCancel} style={{ padding:'11px 26px', background:'white', border:'1.5px solid #e2e8f0', borderRadius:9, fontSize:13, fontWeight:600, cursor:'pointer', color:'#64748b' }}>
          Cancel
        </button>
        <button onClick={handleGenerate}
          style={{ padding:'11px 28px', background:'linear-gradient(135deg,#1d4ed8,#3b82f6)', border:'none', borderRadius:9, color:'white', fontSize:13, fontWeight:700, cursor:'pointer', display:'flex', alignItems:'center', gap:9, boxShadow:'0 4px 16px rgba(59,130,246,0.38)', minWidth:200, justifyContent:'center' }}
          onMouseEnter={e=>e.currentTarget.style.boxShadow='0 6px 22px rgba(59,130,246,0.5)'}
          onMouseLeave={e=>e.currentTarget.style.boxShadow='0 4px 16px rgba(59,130,246,0.38)'}>
          <span style={{ fontSize:14 }}>‚ú¶</span> Generate AI Report
        </button>
      </div>
    </div>
  );
}
