import { useState, useEffect } from 'react';
import { getReport } from '../services/api';
import { downloadPDF } from '../utils/downloadPDF';

/* â”€â”€ helpers â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€ */
const initials = n => (n||'').split(' ').slice(0,2).map(w=>w[0]||'').join('').toUpperCase()||'?';
const GRAD = [
  ['#1c3370','#3b5bdb'],['#0f766e','#14b8a6'],['#7e22ce','#a855f7'],
  ['#b45309','#f59e0b'],['#be123c','#fb7185'],['#0369a1','#38bdf8'],
];
const grad = n => GRAD[(n?.charCodeAt(0)||0)%GRAD.length];
const wIcon = c => { const s=(c||'').toLowerCase(); return s.includes('sun')||s.includes('clear')?'â˜€ï¸':s.includes('cloud')?'â›…':s.includes('rain')?'ðŸŒ§ï¸':s.includes('storm')?'â›ˆï¸':s.includes('snow')?'â„ï¸':'ðŸŒ¤ï¸'; };

/* â”€â”€ tiny primitives â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€ */
const Tag = ({children, bg='#eff6ff', color='#1c3370'}) => (
  <span style={{background:bg, color, borderRadius:20, padding:'3px 10px', fontSize:11, fontWeight:700, border:`1px solid ${color}22`}}>{children}</span>
);

const InfoRow = ({icon, label, value}) => value && value !== 'â€”' ? (
  <div style={{display:'flex', alignItems:'flex-start', gap:10, padding:'9px 0', borderBottom:'1px solid #f1f5f9'}}>
    <div style={{width:32, height:32, borderRadius:8, background:'#1c3370', color:'white', fontSize:14, textAlign:'center', lineHeight:'32px', flexShrink:0}}>{icon}</div>
    <div style={{flex:1}}>
      <div style={{fontSize:10, color:'#94a3b8', fontWeight:700, marginBottom:2, textTransform:'uppercase', letterSpacing:.5}}>{label}</div>
      <div style={{fontSize:12.5, fontWeight:700, color:'#1e293b', wordBreak:'break-word'}}>{value}</div>
    </div>
  </div>
) : null;

const Chip = ({label, value, accent='#1c3370'}) => (
  <div style={{background:'#f8fafc', border:'1px solid #e2e8f0', borderRadius:10, padding:'11px 14px', borderTop:`3px solid ${accent}`}}>
    <div style={{fontSize:9.5, fontWeight:700, color:'#94a3b8', textTransform:'uppercase', letterSpacing:.8, marginBottom:5}}>{label}</div>
    <div style={{fontSize:12.5, fontWeight:700, color:'#0f172a', lineHeight:1.4}}>{value||'â€”'}</div>
  </div>
);

const SectionHead = ({icon, title, badge}) => (
  <div style={{display:'flex', alignItems:'center', gap:10, marginBottom:16}}>
    <div style={{width:34,height:34,borderRadius:9,background:'linear-gradient(135deg,#1c3370,#2563eb)',display:'flex',alignItems:'center',justifyContent:'center',fontSize:16,flexShrink:0}}>{icon}</div>
    <span style={{fontWeight:800, fontSize:15, color:'#0f172a', flex:1}}>{title}</span>
    {badge!=null && <Tag>{badge}</Tag>}
  </div>
);

/* â”€â”€ main â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€ */
const TABS = [
  {id:'overview',  icon:'ðŸŽ¯', label:'Ù†Ø¸Ø±Ø© Ø¹Ø§Ù…Ø©'},
  {id:'agenda',    icon:'ðŸ“…', label:'Ø¬Ø¯ÙˆÙ„ Ø§Ù„Ø£Ø¹Ù…Ø§Ù„'},
  {id:'speakers',  icon:'ðŸŽ¤', label:'Ø§Ù„Ù…ØªØ­Ø¯Ø«ÙˆÙ†'},
  {id:'meetings',  icon:'ðŸ¤', label:'Ø§Ù„Ø§Ø¬ØªÙ…Ø§Ø¹Ø§Øª'},
  {id:'logistics', icon:'ðŸŒ¦ï¸', label:'Ø§Ù„Ù„ÙˆØ¬Ø³ØªÙŠØ§Øª'},
];

export default function ReportDetail({reportId, onBack, onPDFOpen}) {
  const [report, setReport] = useState(null);
  const [tab, setTab] = useState('overview');
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    setLoading(true);
    getReport(reportId).then(r => { setReport(r.data); setLoading(false); }).catch(()=>setLoading(false));
  }, [reportId]);

  if (loading) return (
    <div style={{display:'flex',alignItems:'center',justifyContent:'center',height:'60vh',flexDirection:'column',gap:14}}>
      <div style={{width:44,height:44,border:'4px solid #e2e8f0',borderTopColor:'#1c3370',borderRadius:'50%',animation:'spin 0.8s linear infinite'}}/>
      <div style={{color:'#64748b',fontSize:14}}>Ø¬Ø§Ø±ÙŠ ØªØ­Ù…ÙŠÙ„ Ø§Ù„ØªÙ‚Ø±ÙŠØ±...</div>
      <style>{`@keyframes spin{to{transform:rotate(360deg)}}`}</style>
    </div>
  );
  if (!report) return null;

  const riskClr = {low:'#22c55e',medium:'#f59e0b',high:'#ef4444'}[report.risk_level||'low'];
  const sess = report.sessions||{};

  return (
    <div style={{background:'#f1f5f9', minHeight:'100vh', fontFamily:"'Cairo',sans-serif", direction:'rtl'}}>

      {/* â•â•â• HERO â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â• */}
      <div style={{background:'linear-gradient(150deg,#0a1628 0%,#1c3370 55%,#1e4a9a 100%)', position:'relative', overflow:'hidden'}}>
        {/* bg deco blobs */}
        <div style={{position:'absolute',top:-100,right:-80,width:360,height:360,borderRadius:'50%',background:'rgba(255,255,255,0.03)',pointerEvents:'none'}}/>
        <div style={{position:'absolute',bottom:-80,left:-60,width:260,height:260,borderRadius:'50%',background:'rgba(255,255,255,0.04)',pointerEvents:'none'}}/>

        <div style={{padding:'24px 32px 0', position:'relative'}}>
          {/* back + status row */}
          <div style={{display:'flex',alignItems:'center',gap:10,marginBottom:18,flexWrap:'wrap'}}>
            <button onClick={onBack}
              style={{background:'rgba(255,255,255,0.1)',border:'1px solid rgba(255,255,255,0.18)',color:'white',borderRadius:8,padding:'6px 14px',cursor:'pointer',fontSize:13,fontWeight:600,display:'flex',alignItems:'center',gap:6,fontFamily:"'Cairo',sans-serif"}}>
              â† Ø§Ù„ØªÙ‚Ø§Ø±ÙŠØ±
            </button>
            <span style={{color:'rgba(255,255,255,0.35)'}}>â–¸</span>
            <span style={{color:'rgba(255,255,255,0.55)',fontSize:12,maxWidth:300,overflow:'hidden',textOverflow:'ellipsis',whiteSpace:'nowrap'}}>{report.event_name}</span>
            <div style={{marginRight:'auto',display:'flex',gap:7,flexWrap:'wrap'}}>
              {[
                {bg:riskClr+'25',cl:riskClr, label:`${(report.risk_level||'LOW').toUpperCase()} RISK`},
                {bg:report.status==='ready'?'#22c55e22':'#f59e0b22', cl:report.status==='ready'?'#22c55e':'#f59e0b', label:`â— ${report.status||'draft'}`},
                {bg:'rgba(255,255,255,0.12)', cl:'white', label:'ðŸ“‹ ÙˆØ«ÙŠÙ‚Ø© Ø¥Ø­Ø§Ø·Ø© Ø±Ø³Ù…ÙŠØ©'},
              ].map(t=><Tag key={t.label} bg={t.bg} color={t.cl}>{t.label}</Tag>)}
            </div>
          </div>

          {/* title + actions */}
          <div style={{display:'flex',gap:20,alignItems:'flex-start',marginBottom:20}}>
            <div style={{flex:1,minWidth:0}}>
              <h1 style={{color:'white',fontWeight:900,fontSize:22,margin:'0 0 6px',lineHeight:1.3}}>
                {report.event_name}
              </h1>
              {report.report_subtitle&&<div style={{color:'rgba(255,255,255,0.65)',fontSize:13.5,marginBottom:14}}>{report.report_subtitle}</div>}
              <div style={{display:'flex',flexWrap:'wrap',gap:8}}>
                {[
                  {i:'ðŸ“…', v:`${report.start_date}${report.end_date?' â€“ '+report.end_date:''}`},
                  {i:'ðŸ“', v:`${report.venue||''}${report.venue?', ':''}${report.city}, ${report.country}`},
                  {i:'ðŸ‘¤', v:report.traveler_name},
                ].map(m=>(
                  <div key={m.v} style={{display:'flex',alignItems:'center',gap:6,background:'rgba(255,255,255,0.1)',borderRadius:8,padding:'5px 12px',color:'rgba(255,255,255,0.85)',fontSize:12.5,fontWeight:600,border:'1px solid rgba(255,255,255,0.1)'}}>
                    <span>{m.i}</span><span>{m.v}</span>
                  </div>
                ))}
              </div>
            </div>

            <div style={{display:'flex',flexDirection:'column',gap:8,flexShrink:0,minWidth:140}}>
              <button onClick={()=>onPDFOpen(reportId, report.event_name,'html')}
                style={{padding:'10px 16px',background:'rgba(255,255,255,0.12)',color:'white',border:'1px solid rgba(255,255,255,0.22)',borderRadius:10,fontSize:13,fontWeight:700,cursor:'pointer',whiteSpace:'nowrap',fontFamily:"'Cairo',sans-serif",textAlign:'center'}}>
                ðŸ‘ï¸ Ù…Ø¹Ø§ÙŠÙ†Ø© HTML
              </button>
              <button onClick={()=>downloadPDF(reportId,`SFDA_Milken2025.pdf`)}
                style={{padding:'10px 16px',background:'white',color:'#1c3370',border:'none',borderRadius:10,fontSize:13,fontWeight:800,cursor:'pointer',whiteSpace:'nowrap',fontFamily:"'Cairo',sans-serif",boxShadow:'0 4px 16px rgba(0,0,0,0.3)',textAlign:'center'}}>
                â¬‡ ØªØµØ¯ÙŠØ± PDF
              </button>
            </div>
          </div>

          {/* TAB BAR */}
          <div style={{display:'flex',gap:0,borderTop:'1px solid rgba(255,255,255,0.1)'}}>
            {TABS.map(t=>(
              <button key={t.id} onClick={()=>setTab(t.id)}
                style={{padding:'12px 18px',background:'transparent',border:'none',cursor:'pointer',color:tab===t.id?'white':'rgba(255,255,255,0.5)',fontWeight:tab===t.id?800:500,fontSize:13,display:'flex',alignItems:'center',gap:6,borderBottom:tab===t.id?'3px solid #60a5fa':'3px solid transparent',transition:'all .15s',fontFamily:"'Cairo',sans-serif",whiteSpace:'nowrap'}}>
                {t.icon} {t.label}
              </button>
            ))}
          </div>
        </div>
      </div>

      {/* â•â•â• CONTENT â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â• */}
      <div style={{padding:'24px 32px 40px', maxWidth:1120}}>

        {/* â”€â”€ OVERVIEW â”€â”€ */}
        {tab==='overview'&&(
          <div style={{display:'grid',gridTemplateColumns:'1fr 1fr',gap:16}}>

            {/* summary chips */}
            <div style={{gridColumn:'1/-1',background:'white',border:'1px solid #e2e8f0',borderRadius:16,padding:20,boxShadow:'0 1px 3px rgba(0,0,0,0.05)'}}>
              <SectionHead icon="ðŸŽ¯" title="Ù…Ù„Ø®Øµ Ø§Ù„Ø²ÙŠØ§Ø±Ø©"/>
              <div style={{display:'grid',gridTemplateColumns:'repeat(3,1fr)',gap:10}}>
                <Chip label="Ø§Ù„ÙØ¹Ø§Ù„ÙŠØ©"       value={report.event_name}     accent="#1c3370"/>
                <Chip label="Ø§Ù„Ù…Ø¯ÙŠÙ†Ø©"        value={`${report.city}, ${report.country}`} accent="#2563eb"/>
                <Chip label="Ø§Ù„ØªÙˆØ§Ø±ÙŠØ®"       value={`${report.start_date} â€“ ${report.end_date||''}`} accent="#7c3aed"/>
                <Chip label="Ø§Ù„Ù…Ù‚Ø±"          value={report.venue}          accent="#0f766e"/>
                <Chip label="Ø§Ù„Ù…Ø³Ø¤ÙˆÙ„ Ø§Ù„Ø£ÙˆÙ„"  value={report.traveler_name}  accent="#b45309"/>
                <Chip label="Ø§Ù„ØµÙØ© Ø§Ù„ÙˆØ¸ÙŠÙÙŠØ©" value={report.traveler_title} accent="#be123c"/>
              </div>
            </div>

            {/* objectives */}
            <div style={{background:'white',border:'1px solid #e2e8f0',borderRadius:16,padding:20,boxShadow:'0 1px 3px rgba(0,0,0,0.05)'}}>
              <SectionHead icon="ðŸŽ¯" title="Ø£Ù‡Ø¯Ø§Ù Ø§Ù„Ø²ÙŠØ§Ø±Ø©" badge={(report.visit_objectives||[]).length}/>
              {(report.visit_objectives||[]).map((o,i)=>(
                <div key={i} style={{display:'flex',gap:10,alignItems:'flex-start',padding:'8px 0',borderBottom:'1px solid #f8fafc'}}>
                  <div style={{width:24,height:24,borderRadius:7,background:'linear-gradient(135deg,#1c3370,#2563eb)',color:'white',fontWeight:800,fontSize:11,textAlign:'center',lineHeight:'24px',flexShrink:0}}>{i+1}</div>
