import { T } from '../i18n';

export default function Sidebar({ page, setPage, lang, setLang }) {
  const t = T[lang];
  const isAr = lang === 'ar';

  const btn = id => ({
    width:'100%', display:'flex', alignItems:'center', gap:9, padding:'9px 10px',
    borderRadius:7, border:'none', marginBottom:2, cursor:'pointer',
    textAlign: isAr ? 'right' : 'left',
    fontSize:13, fontWeight:page===id?600:400, fontFamily:'inherit',
    background:page===id?'rgba(59,130,246,0.13)':'transparent',
    color:page===id?'#60a5fa':'#8899aa',
    borderLeft: isAr ? 'none' : (page===id?'3px solid #3b82f6':'3px solid transparent'),
    borderRight: isAr ? (page===id?'3px solid #3b82f6':'3px solid transparent') : 'none',
    transition:'all 0.15s', direction: isAr ? 'rtl' : 'ltr',
  });

  const hover = (e, on) => {
    if (page !== e.currentTarget.dataset.id) {
      e.currentTarget.style.background = on ? 'rgba(255,255,255,0.05)' : 'transparent';
      e.currentTarget.style.color = on ? '#cbd5e1' : '#8899aa';
    }
  };

  const secLabel = (txt) => (
    <div style={{ fontSize:9, fontWeight:700, color:'#3d5166', letterSpacing:'0.11em', margin:'16px 0 7px', paddingLeft: isAr?0:6, paddingRight: isAr?6:0, textAlign: isAr?'right':'left' }}>
      {txt}
    </div>
  );

  return (
    <aside style={{ width:208, minWidth:208, background:'#0f1c2e', display:'flex', flexDirection:'column', boxShadow:'2px 0 16px rgba(0,0,0,0.22)' }}>
      {/* Logo */}
      <div style={{ padding:'16px 14px 14px', borderBottom:'1px solid rgba(255,255,255,0.06)', display:'flex', justifyContent:'space-between', alignItems:'center' }}>
        <div style={{ display:'flex', alignItems:'center', gap:10, direction:'ltr' }}>
          <div style={{ width:34, height:34, borderRadius:9, background:'rgba(59,130,246,0.15)', border:'1px solid rgba(59,130,246,0.25)', display:'flex', alignItems:'center', justifyContent:'center', fontSize:17 }}>üåê</div>
          <div>
            <div style={{ color:'#f1f5f9', fontWeight:700, fontSize:14.5 }}>Travel</div>
            <div style={{ color:'#475569', fontSize:10, display:'flex', alignItems:'center', gap:4, marginTop:1 }}>
              <span style={{ color:'#22c55e', fontSize:7 }}>‚óè</span> AI Assistant
            </div>
          </div>
        </div>
        {/* Language toggle */}
        <div style={{ display:'flex', background:'rgba(255,255,255,0.07)', borderRadius:8, overflow:'hidden', border:'1px solid rgba(255,255,255,0.1)' }}>
          {['ar','en'].map(l => (
            <button key={l} onClick={() => setLang(l)}
              style={{ padding:'5px 8px', background: lang===l ? 'rgba(59,130,246,0.35)' : 'transparent', color: lang===l ? '#93c5fd' : '#4b6280', border:'none', cursor:'pointer', fontSize:11, fontWeight:700, transition:'all 0.15s' }}>
              {l === 'ar' ? 'ÿπ' : 'EN'}
            </button>
          ))}
        </div>
      </div>

      {/* Nav */}
      <nav style={{ flex:1, padding:'14px 10px', overflowY:'auto' }}>
        {secLabel(t.navigation)}
        {[
          { id:'dashboard', label:t.dashboard, icon:'‚äû' },
          { id:'reports',   label:t.reports,   icon:'‚ò∞' },
        ].map(item => (
          <button key={item.id} data-id={item.id} onClick={() => setPage(item.id)} style={btn(item.id)}
            onMouseEnter={e => hover(e,true)} onMouseLeave={e => hover(e,false)}>
            <span style={{ fontSize:15 }}>{item.icon}</span>{item.label}
          </button>
        ))}

        {secLabel(t.actions)}
        <button data-id="new" onClick={() => setPage('new')} style={btn('new')}
          onMouseEnter={e => hover(e,true)} onMouseLeave={e => hover(e,false)}>
          <span style={{ fontSize:16, fontWeight:300 }}>‚äï</span> {t.newReport}
        </button>

        {secLabel(t.aiTools)}
        <button data-id="ai" onClick={() => setPage('ai')} style={btn('ai')}
          onMouseEnter={e => hover(e,true)} onMouseLeave={e => hover(e,false)}>
          <span style={{ fontSize:13 }}>üéÅ</span> {t.aiAssistant}
        </button>
      </nav>

      {/* User */}
      <div style={{ padding:'12px 14px', borderTop:'1px solid rgba(255,255,255,0.06)', display:'flex', alignItems:'center', gap:10, direction: isAr ? 'rtl' : 'ltr' }}>
        <div style={{ width:33, height:33, borderRadius:'50%', background:'linear-gradient(135deg,#3b82f6,#2563eb)', display:'flex', alignItems:'center', justifyContent:'center', fontSize:11, fontWeight:800, color:'white', flexShrink:0, position:'relative' }}>
          US
          <span style={{ position:'absolute', bottom:1, right:1, width:7, height:7, background:'#22c55e', borderRadius:'50%', border:'1.5px solid #0f1c2e' }}/>
        </div>
        <div style={{ flex:1, minWidth:0 }}>
          <div style={{ color:'#dde4ee', fontSize:12, fontWeight:600, overflow:'hidden', textOverflow:'ellipsis', whiteSpace:'nowrap' }}>Udemy suliman</div>
          <div style={{ color:'#475569', fontSize:10, overflow:'hidden', textOverflow:'ellipsis', whiteSpace:'nowrap' }}>udemysuliman@gmail.c...</div>
        </div>
        <button style={{ background:'none', border:'none', color:'#475569', cursor:'pointer', fontSize:14 }}>‚Üí</button>
      </div>
    </aside>
  );
}
