import { useState, useEffect } from 'react';
import ReactDOM from 'react-dom';
import Sidebar from './components/Sidebar';
import WorldMap from './components/WorldMap';
import NewReportForm from './components/NewReportForm';
import ReportDetail from './components/ReportDetail';
import AIAssistant from './components/AIAssistant';
import LoginPage from './components/auth/LoginPage';
import SignupPage from './components/auth/SignupPage';
import ForgotPasswordPage from './components/auth/ForgotPasswordPage';
import { getReports, getStats, deleteReport as apiDelete, getInlinePDFUrl, getPreviewUrl, clearAuth, isAuthenticated } from './services/api';
import { downloadPDF, fetchPreviewBlobUrl, revokePreviewUrl } from './utils/downloadPDF';
import { T, getLang, setLang as saveLang, translateCountry } from './i18n';

// â”€â”€â”€ Design System â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€
const C = {
  bg:        '#080c18',
  surface:   'rgba(12,16,32,0.95)',
  elevated:  'rgba(16,22,42,0.98)',
  glass:     'rgba(14,20,38,0.7)',
  border:    'rgba(212,175,55,0.1)',
  borderHi:  'rgba(212,175,55,0.3)',
  gold:      '#d4af37',
  goldLight: '#e8c84a',
  goldDim:   'rgba(212,175,55,0.55)',
  blue:      '#4a9eff',
  teal:      '#14b8a6',
  textPrim:  '#e8edf8',
  textSec:   'rgba(180,196,225,0.55)',
  textMuted: 'rgba(120,150,200,0.3)',
  success:   '#4ade80',
  warning:   '#fb923c',
  danger:    '#f43f5e',
  // legacy compat
  borderGold: 'rgba(212,175,55,0.25)',
  blueGlow: 'rgba(74,158,255,0.12)',
  cyan: '#4a9eff',
  amber: '#d4af37',
};

const FONT   = { ar: "'Cairo',sans-serif", en: "'Cairo',sans-serif" };
const MONO   = "'JetBrains Mono',monospace";
const SERIF  = "'Cairo',sans-serif";

// â”€â”€â”€ Global CSS â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€
const GLOBAL_CSS = `
  @keyframes fadeUp    { from{opacity:0;transform:translateY(18px)} to{opacity:1;transform:none} }
  @keyframes slideIn   { from{opacity:0;transform:translateX(${document?.documentElement?.dir==='rtl'?'':'-'}10px)} to{opacity:1;transform:none} }
  @keyframes toastIn   { from{opacity:0;transform:translateY(-10px) scale(0.96)} to{opacity:1;transform:none scale(1)} }
  @keyframes spin      { to{transform:rotate(360deg)} }
  @keyframes pulse     { 0%,100%{opacity:1} 50%{opacity:0.4} }
  @keyframes shimmer   { 0%,100%{opacity:.35} 50%{opacity:.75} }
  @keyframes goldGlow  { 0%,100%{box-shadow:0 0 12px rgba(212,175,55,.15)} 50%{box-shadow:0 0 28px rgba(212,175,55,.35)} }
  @keyframes floatUp   { 0%,100%{transform:translateY(0)} 50%{transform:translateY(-3px)} }
  @keyframes gridFade  { 0%,100%{opacity:.02} 50%{opacity:.055} }
  * { box-sizing:border-box }
  input,select,textarea { color-scheme:dark; }
  ::selection { background:rgba(212,175,55,0.22); }
`;

// â”€â”€â”€ Card wrapper â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€
function Card({ children, style = {}, glow, onClick, onMouseEnter, onMouseLeave }) {
  return (
    <div onClick={onClick} onMouseEnter={onMouseEnter} onMouseLeave={onMouseLeave}
      style={{
        background: C.surface,
        border: `1px solid ${C.border}`,
        borderRadius: 14,
        backdropFilter: 'blur(12px)',
        ...style,
      }}>
      {children}
    </div>
  );
}

// â”€â”€â”€ Section label â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€
function SectionLabel({ children, dir }) {
  return (
    <div style={{ display:'flex', alignItems:'center', gap:10, marginBottom:14, direction:dir }}>
      <div style={{ width:3, height:14, borderRadius:2, background:`linear-gradient(180deg,${C.gold},${C.goldDim})` }}/>
      <span style={{ fontSize:11, fontWeight:700, color:C.goldDim, letterSpacing:'0.16em', fontFamily:MONO, textTransform:'uppercase' }}>
        {children}
      </span>
    </div>
  );
}

// â”€â”€â”€ Status Badge â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€
function StatusBadge({ status, t }) {
  const ready = status === 'ready';
  return (
    <span style={{
      display: 'inline-flex', alignItems: 'center', gap: 5,
      padding: '3px 10px', borderRadius: 20, fontSize: 10.5, fontWeight: 600,
      background: ready ? 'rgba(74,222,128,0.12)' : 'rgba(251,146,60,0.12)',
      color: ready ? C.success : C.warning,
      border: `1px solid ${ready ? 'rgba(74,222,128,0.25)' : 'rgba(251,146,60,0.25)'}`,
    }}>
      <span style={{ width: 5, height: 5, borderRadius: '50%', background: ready ? C.success : C.warning, display: 'inline-block' }}/>
      {ready ? t.ready : t.draft}
    </span>
  );
}

// â”€â”€â”€ PreviewModal â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€
function PreviewModal({ modal, onClose, t }) {
  const [blobUrl, setBlobUrl] = useState(null);
  const [loading, setLoading] = useState(false);
  const [fetchErr, setFetchErr] = useState('');

  useEffect(() => {
    if (!modal) { setBlobUrl(null); return; }
    setLoading(true); setFetchErr('');
    fetchPreviewBlobUrl(modal.reportId)
      .then(url => setBlobUrl(url))
      .catch(e => setFetchErr(String(e)))
      .finally(() => setLoading(false));
  }, [modal?.reportId]);

  const handleClose = () => { revokePreviewUrl(blobUrl); setBlobUrl(null); onClose(); };
  if (!modal) return null;

  return ReactDOM.createPortal(
    <div style={{ position:'fixed', inset:0, zIndex:9999, background:'rgba(4,6,16,0.92)', display:'flex', flexDirection:'column', backdropFilter:'blur(16px)' }}>
      <div style={{ background:'rgba(10,14,28,0.98)', padding:'14px 24px', display:'flex', alignItems:'center', justifyContent:'space-between', borderBottom:`1px solid ${C.border}`, flexShrink:0 }}>
        <div style={{ display:'flex', alignItems:'center', gap:10 }}>
          <div style={{ width:32, height:32, borderRadius:8, background:'rgba(212,175,55,0.1)', border:`1px solid ${C.borderGold}`, display:'flex', alignItems:'center', justifyContent:'center', fontSize:14 }}>
            {modal.type==='pdf' ? 'ðŸ“„' : 'ðŸ‘'}
          </div>
          <span style={{ color:C.textPrim, fontSize:14, fontWeight:600, fontFamily:"'Cairo',sans-serif" }}>{modal.title}</span>
        </div>
        <div style={{ display:'flex', gap:8 }}>
          <button onClick={() => downloadPDF(modal.reportId, `SFDA_${modal.title||modal.reportId}.pdf`)}
            style={{ padding:'8px 18px', background:`linear-gradient(135deg,${C.gold},#b8932e)`, color:'#080c18', borderRadius:8, fontSize:12, fontWeight:700, border:'none', cursor:'pointer', fontFamily:"'Cairo',sans-serif", letterSpacing:'0.04em' }}>
            {t.downloadPDF||'Download PDF'}
          </button>
          <button onClick={handleClose}
            style={{ padding:'8px 16px', background:'rgba(255,255,255,0.06)', color:C.textSec, border:`1px solid ${C.border}`, borderRadius:8, fontSize:12, fontWeight:500, cursor:'pointer' }}>
            {t.closeBtn||'Close'}
          </button>
        </div>
      </div>
      <div style={{ flex:1, overflow:'hidden', background:'white', display:'flex', alignItems:'center', justifyContent:'center' }}>
        {loading && <div style={{ color:'#64748b', fontSize:15 }}>Loadingâ€¦</div>}
        {fetchErr && <div style={{ color:C.danger, fontSize:14, padding:20 }}>âš  {fetchErr}</div>}
        {blobUrl && <iframe src={blobUrl} title={t.preview||'Preview'} style={{ width:'100%', height:'100%', border:'none' }}/>}
      </div>
    </div>,
    document.body
  );
}

// â”€â”€â”€ Toast â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€
function Toast({ toast }) {
  if (!toast) return null;
  const isSuccess = !toast.color || toast.color === C.success || toast.color === '#22c55e';
  return ReactDOM.createPortal(
    <div style={{ position:'fixed', top:20, insetInlineEnd:20, zIndex:99999, animation:'toastIn 0.2s ease', maxWidth:380 }}>
      <div style={{ background:C.elevated, border:`1px solid ${isSuccess ? 'rgba(74,222,128,0.25)' : 'rgba(244,63,94,0.25)'}`, borderRadius:12, padding:'12px 18px', display:'flex', alignItems:'center', gap:10, boxShadow:'0 12px 40px rgba(0,0,0,0.5)', backdropFilter:'blur(20px)' }}>
        <div style={{ width:32, height:32, borderRadius:8, background: isSuccess?'rgba(74,222,128,0.12)':'rgba(244,63,94,0.12)', display:'flex', alignItems:'center', justifyContent:'center', fontSize:14, flexShrink:0 }}>
          {isSuccess ? 'âœ“' : 'âš '}
        </div>
        <span style={{ fontSize:13, fontWeight:500, color:C.textPrim, fontFamily:"'Cairo',sans-serif" }}>{toast.msg}</span>
      </div>
    </div>,
    document.body
  );
}

// â”€â”€â”€ Icon Button â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€
function IconBtn({ onClick, color, bg, children, title }) {
  const [hov, setHov] = useState(false);
  return (
    <button onClick={onClick} title={title}
      onMouseEnter={() => setHov(true)} onMouseLeave={() => setHov(false)}
      style={{ width:32, height:32, borderRadius:8, background:hov?bg.replace('0.08','0.18'):bg, border:`1px solid ${color}33`, display:'flex', alignItems:'center', justifyContent:'center', cursor:'pointer', transition:'all 0.15s', color }}>
      {children}
    </button>
  );
}

