function ReportsPage({ reports, lang, t, isAr, dir, setPage, handleDelete, openDetail }) {
  const [search, setSearch] = useState('');
  const [statusFilter, setStatusFilter] = useState('all');
  const font = FONT[lang] || FONT.en;

  const filtered = reports.filter(r => {
    const q = search.toLowerCase();
    const ms = !q || (r.title||r.event_name||'').toLowerCase().includes(q) || (r.city||'').toLowerCase().includes(q) || (r.country||'').toLowerCase().includes(q);
    const mf = statusFilter==='all' || r.status===statusFilter;
    return ms && mf;
  });

  return (
    <div style={{ direction:dir, animation:'fadeUp 0.35s ease both' }}>
      {/* Search bar + filter */}
      <div style={{ display:'flex', gap:10, marginBottom:22, direction:dir }}>
        <div style={{ flex:1, position:'relative' }}>
          <span style={{ position:'absolute', [isAr?'insetInlineEnd':'insetInlineStart']:14, top:'50%', transform:'translateY(-50%)', color:C.textMuted, pointerEvents:'none' }}>
            <svg width="14" height="14" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2.2" strokeLinecap="round"><circle cx="11" cy="11" r="8"/><line x1="21" y1="21" x2="16.65" y2="16.65"/></svg>
          </span>
          <input value={search} onChange={e => setSearch(e.target.value)} placeholder={t.searchPlaceholder}
            style={{ width:'100%', padding:isAr?'11px 42px 11px 16px':'11px 16px 11px 42px', background:C.surface, border:`1px solid ${C.border}`, borderRadius:10, fontSize:13, color:C.textPrim, outline:'none', fontFamily:font, transition:'border-color 0.15s', letterSpacing:'0.01em' }}
            onFocus={e => e.target.style.borderColor=C.borderHi}
            onBlur={e => e.target.style.borderColor=C.border}/>
        </div>
        <div style={{ display:'flex', gap:3, background:C.surface, borderRadius:10, padding:3, border:`1px solid ${C.border}` }}>
          {['all','ready','draft'].map(f => (
            <button key={f} onClick={() => setStatusFilter(f)}
              style={{ padding:'7px 16px', borderRadius:7, border:'none', cursor:'pointer', fontSize:12, fontWeight:600, fontFamily:font, transition:'all 0.15s',
                background: statusFilter===f ? 'rgba(212,175,55,0.15)' : 'transparent',
                color: statusFilter===f ? C.gold : C.textSec }}>
              {f==='all'?t.filterAll:f==='ready'?t.ready:t.draft}
            </button>
          ))}
        </div>
      </div>

      {filtered.length === 0 ? (
        <div style={{ textAlign:'center', padding:'80px 20px', color:C.textSec, fontFamily:font }}>
          <div style={{ fontSize:18, fontFamily:SERIF, color:C.textPrim, marginBottom:8 }}>No reports found</div>
          <div style={{ fontSize:13, marginBottom:20, color:C.textSec }}>Start by creating your first travel briefing</div>
          <button onClick={() => setPage('new')} style={{ padding:'10px 24px', background:`linear-gradient(135deg,${C.gold},#b8932e)`, color:'#080c18', border:'none', borderRadius:9, cursor:'pointer', fontSize:13, fontWeight:700, fontFamily:font }}>
            {t.createFirstBtn||'Create Report'}
          </button>
        </div>
      ) : (
        <div style={{ display:'grid', gridTemplateColumns:'repeat(auto-fill,minmax(330px,1fr))', gap:14 }}>
          {filtered.map(r => <ReportCard key={r.id} r={r} lang={lang} t={t} isAr={isAr} dir={dir} font={font} openDetail={openDetail} handleDelete={handleDelete}/>)}
        </div>
      )}
    </div>
  );
}

function ReportCard({ r, lang, t, isAr, dir, font, openDetail, handleDelete }) {
