  const isReady = r.status === 'ready';
  const rgb = isReady ? '74,222,128' : '251,146,60';

  return (
    <div onClick={() => openDetail(r.id)}
      onMouseEnter={() => setHov(true)} onMouseLeave={() => setHov(false)}
      style={{
        position:'relative', borderRadius:14, overflow:'hidden', cursor:'pointer',
        transition:'all 0.22s ease',
        border:`1px solid ${hov ? `rgba(${rgb},0.3)` : C.border}`,
        background: hov ? `linear-gradient(145deg,rgba(${rgb},0.05),${C.surface})` : C.surface,
        transform: hov ? 'translateY(-3px)' : 'none',
        boxShadow: hov ? `0 12px 40px rgba(0,0,0,0.35), 0 0 0 1px rgba(${rgb},0.1)` : 'none',
      }}>
      {/* Color bar top */}
      <div style={{ height:3, background:`linear-gradient(90deg,rgba(${rgb},0.7),rgba(${rgb},0.2))`, transition:'opacity 0.2s', opacity:hov?1:0.5 }}/>

      <div style={{ padding:'16px 18px 18px', direction:dir }}>
        {/* Top row: status + actions */}
        <div style={{ display:'flex', justifyContent:'space-between', alignItems:'center', marginBottom:12 }}>
          <StatusBadge status={r.status} t={t}/>
          <div style={{ display:'flex', gap:6 }} onClick={e => e.stopPropagation()}>

