              color={C.danger} bg={`rgba(244,63,94,0.08)`} title={t.deleteBtn}>
              <svg width="12" height="12" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round">
                <polyline points="3 6 5 6 21 6"/><path d="M19 6l-1 14a2 2 0 0 1-2 2H8a2 2 0 0 1-2-2L5 6"/>
                <path d="M10 11v6"/><path d="M14 11v6"/>
              </svg>
            </IconBtn>
          </div>
        </div>

        {/* Title */}
        <div style={{ fontWeight:600, fontSize:14.5, color:hov?C.textPrim:C.textPrim, marginBottom:12, lineHeight:1.4, fontFamily:font }}>
          {r.title||r.event_name}
        </div>

        {/* Dashed divider (boarding pass feel) */}
        <div style={{ height:1, marginBottom:12, background:`repeating-linear-gradient(${isAr?'270deg':'90deg'},transparent 0,transparent 5px,${C.border} 5px,${C.border} 10px)` }}/>

        {/* Meta chips */}
        <div style={{ display:'flex', flexWrap:'wrap', gap:8, fontSize:11.5, color:C.textSec }}>
          <div style={{ display:'flex', alignItems:'center', gap:4 }}>
            <svg width="10" height="10" viewBox="0 0 24 24" fill="none" stroke={C.gold} strokeWidth="2.5" strokeLinecap="round"><path d="M21 10c0 7-9 13-9 13s-9-6-9-13a9 9 0 0 1 18 0z"/><circle cx="12" cy="10" r="3"/></svg>
            <span style={{ fontFamily:font }}>{r.city}, {translateCountry(r.country, lang)}</span>
          </div>
          <div style={{ display:'flex', alignItems:'center', gap:4 }}>
            <svg width="10" height="10" viewBox="0 0 24 24" fill="none" stroke={C.textMuted} strokeWidth="2" strokeLinecap="round"><rect x="3" y="4" width="18" height="18" rx="2"/><line x1="16" y1="2" x2="16" y2="6"/><line x1="8" y1="2" x2="8" y2="6"/><line x1="3" y1="10" x2="21" y2="10"/></svg>
            <span style={{ fontFamily:MONO, fontSize:10.5 }}>{r.start_date}</span>
          </div>
          {r.ai_generated && (
            <span style={{ display:'inline-flex', alignItems:'center', gap:3, fontSize:9.5, fontWeight:700, padding:'2px 7px', borderRadius:5, background:'rgba(109,40,217,0.15)', color:'#a78bfa', border:'1px solid rgba(109,40,217,0.2)', fontFamily:MONO }}>
              âœ¦ AI
            </span>
          )}
        </div>
      </div>
    </div>
  );
}

