import { useState, useEffect } from 'react';
import ReactDOM from 'react-dom';
import Sidebar from './components/Sidebar';
import WorldMap from './components/WorldMap';
import NewReportForm from './components/NewReportForm';
import ReportDetail from './components/ReportDetail';
import AIAssistant from './components/AIAssistant';
import LoginPage from './components/auth/LoginPage';
import SignupPage from './components/auth/SignupPage';
import ForgotPasswordPage from './components/auth/ForgotPasswordPage';
import { getReports, getStats, deleteReport as apiDelete, getInlinePDFUrl, getPreviewUrl, clearAuth, isAuthenticated } from './services/api';
import { downloadPDF, fetchPreviewBlobUrl, revokePreviewUrl } from './utils/downloadPDF';
import { T, getLang, setLang as saveLang, translateCountry } from './i18n';

// â”€â”€â”€ PreviewModal â€” fetches HTML with auth token, displays in blob iframe â”€â”€â”€â”€
function PreviewModal({ modal, onClose, t }) {
  const [blobUrl,  setBlobUrl]  = useState(null);
  const [loading,  setLoading]  = useState(false);
  const [fetchErr, setFetchErr] = useState(null);

  useEffect(() => {
    if (!modal) { setBlobUrl(null); return; }
    setLoading(true); setFetchErr(null); setBlobUrl(null);
    fetchPreviewBlobUrl(modal.reportId)
      .then(url => { setBlobUrl(url);  setLoading(false); })
      .catch(e  => { setFetchErr(e.message); setLoading(false); });
    return () => { if (blobUrl) revokePreviewUrl(blobUrl); };
  }, [modal?.reportId]); // eslint-disable-line

  const handleClose = () => {
    revokePreviewUrl(blobUrl);
    setBlobUrl(null);
    onClose();
  };

  if (!modal) return null;
  return ReactDOM.createPortal(
    <div style={{ position:'fixed', inset:0, zIndex:99999, display:'flex', flexDirection:'column', background:'rgba(0,0,0,0.85)' }}>
      <div style={{ background:'#0d1829', padding:'12px 20px', display:'flex', alignItems:'center', justifyContent:'space-between', flexShrink:0, borderBottom:'1px solid rgba(255,255,255,0.1)' }}>
        <span style={{ color:'white', fontSize:14, fontWeight:700 }}>
          {modal.type === 'pdf' ? 'ðŸ“„' : 'ðŸ‘ï¸'} {modal.title}
        </span>
        <div style={{ display:'flex', gap:10 }}>
          <button onClick={() => downloadPDF(modal.reportId, `SFDA_${modal.title || modal.reportId}.pdf`)}
            style={{ padding:'8px 18px', background:'#3b82f6', color:'white', borderRadius:8, fontSize:12, fontWeight:700, border:'none', cursor:'pointer', display:'flex', alignItems:'center', gap:6 }}>
            {t.downloadPDF || 'Download PDF'}
          </button>
          <button onClick={handleClose}
            style={{ padding:'8px 18px', background:'rgba(255,255,255,0.12)', color:'white', border:'1px solid rgba(255,255,255,0.2)', borderRadius:8, fontSize:12, fontWeight:700, cursor:'pointer' }}>
            {t.closeBtn || 'Close'}
          </button>
        </div>
      </div>
      <div style={{ flex:1, overflow:'hidden', background:'white', display:'flex', alignItems:'center', justifyContent:'center' }}>
        {loading  && <div style={{ color:'#64748b', fontSize:15 }}>â³ Ø¬Ø§Ø±ÙŠ ØªØ­Ù…ÙŠÙ„ Ø§Ù„Ù…Ø¹Ø§ÙŠÙ†Ø©...</div>}
        {fetchErr && <div style={{ color:'#ef4444', fontSize:14, padding:20 }}>âš ï¸ {fetchErr}</div>}
        {blobUrl  && <iframe src={blobUrl} title={t.preview || 'Preview'} style={{ width:'100%', height:'100%', border:'none', display:'block' }} />}
      </div>
    </div>,
    document.body
  );
}

function Toast({ toast }) {
  if (!toast) return null;
  return ReactDOM.createPortal(
    <div style={{ position:'fixed', top:20, right:24, zIndex:99998, background:toast.color, color:'white', borderRadius:10, padding:'12px 20px', fontSize:13, fontWeight:600, boxShadow:'0 6px 24px rgba(0,0,0,0.25)', animation:'toastIn 0.25s ease', maxWidth:400 }}>
      {toast.msg}
    </div>,
    document.body
  );
}

// â”€â”€â”€ Reports page extracted as proper component (hooks can't be inside IIFE) â”€
function ReportsPage({ reports, lang, t, isAr, dir, setPage, openPreview, handleDelete, openDetail }) {
  const [search,       setSearch]      = useState('');
  const [statusFilter, setStatusFilter] = useState('all');
  const [filterOpen,   setFilterOpen]  = useState(false);

  const font = isAr ? "'Cairo','Segoe UI',sans-serif" : "'DM Sans',-apple-system,sans-serif";

  const filtered = reports.filter(r => {
    const q = search.toLowerCase();
    const matchSearch = !q
      || (r.title || r.event_name || '').toLowerCase().includes(q)
      || (r.city  || '').toLowerCase().includes(q)
      || (r.country || '').toLowerCase().includes(q);
    const matchStatus = statusFilter === 'all' || r.status === statusFilter;
    return matchSearch && matchStatus;
  });

  const filterOptions = [
    { key: 'all',   label: t.filterAll },
    { key: 'ready', label: t.ready },
    { key: 'draft', label: t.draft },
  ];

  return (
    <>
      {/* â”€â”€ Search + filter row (bare, no card wrapper) â”€â”€ */}
      <div style={{ display:'flex', alignItems:'center', gap:10, marginBottom:20, direction:dir }}>
        {/* Search */}
        <div style={{ flex:1, position:'relative' }}>
          <span style={{ position:'absolute', [isAr?'right':'left']:14, top:'50%', transform:'translateY(-50%)', color:'#b0bec5', pointerEvents:'none', display:'flex' }}>
            <svg width="16" height="16" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2.2" strokeLinecap="round"><circle cx="11" cy="11" r="8"/><line x1="21" y1="21" x2="16.65" y2="16.65"/></svg>
          </span>
          <input
            value={search}
            onChange={e => setSearch(e.target.value)}
            placeholder={t.searchPlaceholder}
            style={{
              width:'100%', padding: isAr ? '10px 42px 10px 14px' : '10px 14px 10px 42px',
              border:'1.5px solid #e2e8f0', borderRadius:10,
              fontSize:13.5, color:'#1e293b', background:'white',
              outline:'none', boxSizing:'border-box', direction:dir, fontFamily:font,
              boxShadow:'0 1px 3px rgba(0,0,0,0.05)', transition:'border-color 0.15s',
            }}
            onFocus={e => e.target.style.borderColor='#3b82f6'}
            onBlur={e  => e.target.style.borderColor='#e2e8f0'}
          />
        </div>

        {/* Filter button + dropdown */}
        <div style={{ position:'relative', flexShrink:0 }}>
          <button onClick={() => setFilterOpen(p => !p)}
            style={{
              display:'flex', alignItems:'center', gap:7, padding:'10px 16px',
              background:'white', border:`1.5px solid ${filterOpen?'#3b82f6':'#e2e8f0'}`,
              borderRadius:10, cursor:'pointer', fontSize:13.5, fontWeight:600,
              color: filterOpen?'#3b82f6':'#475569', fontFamily:font,
              boxShadow:'0 1px 3px rgba(0,0,0,0.05)', transition:'all 0.15s', whiteSpace:'nowrap',
            }}>
            <svg width="14" height="14" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2.2" strokeLinecap="round">
              <polygon points="22 3 2 3 10 12.46 10 19 14 21 14 12.46 22 3"/>
            </svg>
            {t.filterLabel}
            {statusFilter !== 'all' && <span style={{ width:6, height:6, borderRadius:'50%', background:'#3b82f6', display:'inline-block' }}/>}
          </button>

          {filterOpen && (
            <div style={{ position:'absolute', top:'calc(100% + 6px)', [isAr?'right':'left']:0, background:'white', borderRadius:10, border:'1.5px solid #e2e8f0', boxShadow:'0 8px 24px rgba(0,0,0,0.12)', zIndex:50, minWidth:140, overflow:'hidden' }}>
              {filterOptions.map(p => (
                <button key={p.key} onClick={() => { setStatusFilter(p.key); setFilterOpen(false); }}
                  style={{ width:'100%', display:'flex', alignItems:'center', gap:8, padding:'10px 14px', border:'none', cursor:'pointer', background: statusFilter===p.key ? '#eff6ff' : 'transparent', color: statusFilter===p.key ? '#1d4ed8' : '#374151', fontSize:13.5, fontFamily:font, textAlign: isAr?'right':'left', transition:'background 0.1s' }}
                  onMouseEnter={e => { if(statusFilter!==p.key) e.currentTarget.style.background='#f8fafc'; }}
                  onMouseLeave={e => { if(statusFilter!==p.key) e.currentTarget.style.background='transparent'; }}>
                  {statusFilter===p.key
                    ? <svg width="13" height="13" viewBox="0 0 24 24" fill="none" stroke="#1d4ed8" strokeWidth="2.5" strokeLinecap="round"><polyline points="20 6 9 17 4 12"/></svg>
                    : <span style={{width:13}}/>}
                  {p.label}
                </button>
              ))}
            </div>
          )}
        </div>
      </div>

      {/* â”€â”€ Cards â”€â”€ */}
      {filtered.length === 0 ? (
        <div style={{ textAlign:'center', padding:'72px 20px', color:'#94a3b8', background:'white', borderRadius:16, border:'1px solid #e2e8f0', direction:dir }}>
          <div style={{ fontSize:40, marginBottom:14 }}>ðŸ“‹</div>
          <div style={{ fontSize:15, fontWeight:600, marginBottom:12, color:'#64748b', fontFamily:font }}>
            {reports.length === 0 ? t.noReports : t.noMatchingReports}
          </div>
          {reports.length === 0
            ? <button onClick={() => setPage('new')} style={{ background:'#0d1829', border:'none', color:'white', cursor:'pointer', fontWeight:700, fontSize:13, fontFamily:font, padding:'9px 20px', borderRadius:8 }}>{t.createFirst}</button>
            : <button onClick={() => { setSearch(''); setStatusFilter('all'); }} style={{ background:'none', border:'none', color:'#3b82f6', cursor:'pointer', fontWeight:700, fontSize:13, fontFamily:font }}>{t.clearFilters}</button>
          }
        </div>
      ) : (
        <div style={{ display:'grid', gridTemplateColumns:'repeat(auto-fill, minmax(340px, 1fr))', gap:16 }}>
          {filtered.map(r => {
            const isReady = r.status === 'ready';
            const createdStr = r.created_at ? new Date(r.created_at).toLocaleDateString('en-US', { year:'numeric', month:'short', day:'numeric' }) : '';
            const dateRange = r.start_date ? (r.end_date && r.end_date !== r.start_date ? `${r.start_date} - ${r.end_date}` : r.start_date) : '';

            return (
              <div key={r.id} onClick={() => openDetail(r.id)}
                style={{ background:'white', borderRadius:16, border:'1px solid #eaeff6', padding:'20px', cursor:'pointer', boxShadow:'0 2px 8px rgba(0,0,0,0.05)', transition:'box-shadow 0.18s,transform 0.18s', direction:dir, position:'relative' }}
                onMouseEnter={e => { e.currentTarget.style.boxShadow='0 8px 28px rgba(0,0,0,0.1)'; e.currentTarget.style.transform='translateY(-2px)'; }}
                onMouseLeave={e => { e.currentTarget.style.boxShadow='0 2px 8px rgba(0,0,0,0.05)'; e.currentTarget.style.transform='none'; }}>

                {/* Top row: status badge + circular doc icon */}
                <div style={{ display:'flex', alignItems:'center', justifyContent:'space-between', marginBottom:18 }}>
                  <span style={{ display:'inline-flex', alignItems:'center', gap:5, background: isReady?'#e8fdf2':'#fef9e7', color: isReady?'#1a9655':'#b45309', border:`1px solid ${isReady?'#a7f0c4':'#fde68a'}`, borderRadius:20, padding:'4px 12px', fontSize:12, fontWeight:700, fontFamily:font }}>
                    <span style={{ width:6, height:6, borderRadius:'50%', background: isReady?'#22c55e':'#f59e0b', display:'inline-block' }}/>
                    {isReady ? t.ready : t.draft}
                  </span>
                  <div style={{ display:'flex', alignItems:'center', gap:6 }}>
                    {/* Preview button */}
                    <button onClick={e => { e.stopPropagation(); openPreview(r.id, r.title||r.event_name, 'html'); }}
                      title={t.previewBtn}
                      style={{ width:34, height:34, borderRadius:'50%', background:'#f4f6f9', border:'1.5px solid #e8edf4', display:'flex', alignItems:'center', justifyContent:'center', cursor:'pointer', transition:'all 0.15s', flexShrink:0 }}
                      onMouseEnter={e => { e.currentTarget.style.background='#eff6ff'; e.currentTarget.style.borderColor='#bfdbfe'; }}
                      onMouseLeave={e => { e.currentTarget.style.background='#f4f6f9'; e.currentTarget.style.borderColor='#e8edf4'; }}>
                      <svg width="14" height="14" viewBox="0 0 24 24" fill="none" stroke="#64748b" strokeWidth="2" strokeLinecap="round">
                        <path d="M14 2H6a2 2 0 0 0-2 2v16a2 2 0 0 0 2 2h12a2 2 0 0 0 2-2V8z"/>
                        <polyline points="14 2 14 8 20 8"/>
                        <line x1="16" y1="13" x2="8" y2="13"/>
                        <line x1="16" y1="17" x2="8" y2="17"/>
                      </svg>
                    </button>
                    {/* Delete button */}
                    <button onClick={e => handleDelete(r.id, e)}
                      title={t.deleteBtn}
                      style={{ width:34, height:34, borderRadius:'50%', background:'#f4f6f9', border:'1.5px solid #e8edf4', display:'flex', alignItems:'center', justifyContent:'center', cursor:'pointer', transition:'all 0.15s', flexShrink:0 }}
                      onMouseEnter={e => { e.currentTarget.style.background='#fff1f2'; e.currentTarget.style.borderColor='#fecdd3'; e.currentTarget.querySelector('svg').style.stroke='#ef4444'; }}
                      onMouseLeave={e => { e.currentTarget.style.background='#f4f6f9'; e.currentTarget.style.borderColor='#e8edf4'; e.currentTarget.querySelector('svg').style.stroke='#94a3b8'; }}>
                      <svg width="14" height="14" viewBox="0 0 24 24" fill="none" stroke="#94a3b8" strokeWidth="2" strokeLinecap="round" style={{ transition:'stroke 0.15s' }}>
                        <polyline points="3 6 5 6 21 6"/>
                        <path d="M19 6l-1 14a2 2 0 0 1-2 2H8a2 2 0 0 1-2-2L5 6"/>
                        <path d="M10 11v6"/><path d="M14 11v6"/>
                        <path d="M9 6V4a1 1 0 0 1 1-1h4a1 1 0 0 1 1 1v2"/>
                      </svg>
                    </button>
                  </div>
                </div>

                {/* Title */}
                <div style={{ fontWeight:800, fontSize:15, color:'#0d1829', marginBottom:12, lineHeight:1.4, fontFamily:font }}>
                  {r.title || r.event_name}
                </div>

                {/* Location */}
                <div style={{ display:'flex', alignItems:'center', gap:7, marginBottom:8, fontSize:13, color:'#4a7adb', fontWeight:500 }}>
                  <svg width="13" height="13" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2.2" strokeLinecap="round">
                    <path d="M21 10c0 7-9 13-9 13s-9-6-9-13a9 9 0 0 1 18 0z"/><circle cx="12" cy="10" r="3"/>
                  </svg>
                  <span style={{ fontFamily:"'DM Sans',sans-serif" }}>{r.city}{r.country ? `, ${translateCountry(r.country, lang)}` : ''}</span>
                </div>

                {/* Date */}
                {dateRange && (
                  <div style={{ display:'flex', alignItems:'center', gap:7, marginBottom:18, fontSize:13, color:'#64748b' }}>
                    <svg width="13" height="13" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2.2" strokeLinecap="round">
                      <rect x="3" y="4" width="18" height="18" rx="2"/><line x1="16" y1="2" x2="16" y2="6"/><line x1="8" y1="2" x2="8" y2="6"/><line x1="3" y1="10" x2="21" y2="10"/>
                    </svg>
                    <span style={{ fontFamily:"'DM Sans',sans-serif" }}>{dateRange}</span>
                  </div>
                )}

                {/* Created date footer */}
                {createdStr && (
                  <div style={{ borderTop:'1px solid #f1f5f9', paddingTop:12, fontSize:11.5, color:'#b0bec5', fontFamily:"'DM Sans',sans-serif", textAlign: isAr?'left':'right' }}>
                    {t.created} {createdStr}
                  </div>
                )}
              </div>
            );
          })}
        </div>
      )}
    </>
  );
}

// â”€â”€â”€ Main App â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€
export default function App() {
  // â”€â”€ Auth gate â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€
  const [authScreen, setAuthScreen] = useState(() => isAuthenticated() ? null : 'login');
  // authScreen: null = logged in | 'login' | 'signup' | 'forgot'

  const [page,         setPageRaw]      = useState('dashboard');
  const [selectedReport, setSelected]   = useState(null);
  const [reports,      setReports]      = useState([]);
  const [stats,        setStats]        = useState({ total:0, ready:0, draft:0 });
  const [toast,        setToast]        = useState(null);
  const [loading,      setLoading]      = useState(true);
  const [previewModal, setPreviewModal] = useState(null);
  const [lang,         setLangState]    = useState(getLang());

  const setLang = (l) => { saveLang(l); setLangState(l); };

  // â”€â”€ Live Intelligence Feed â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€
  const [liveFeed,      setLiveFeed]      = useState(null);
  const [feedLoading,   setFeedLoading]   = useState(false);
  const [feedLastUpdate,setFeedLastUpdate] = useState(null);
  const [feedError,     setFeedError]     = useState(false);
  const [feedCountdown, setFeedCountdown] = useState(300); // seconds until next refresh
  const [activeNewsIdx, setActiveNewsIdx] = useState(0);   // highlighted news item
  const [newItemIds,    setNewItemIds]    = useState(new Set()); // flash new items
