                                onClick={() => item.url && window.open(item.url, '_blank')}>
                                <div style={{ width:34, height:34, borderRadius:8, background: item.iconBg || '#f1f5f9', display:'flex', alignItems:'center', justifyContent:'center', fontSize:15, flexShrink:0, border: isNew ? '2px solid #f59e0b' : '2px solid transparent', transition:'border 0.3s' }}>
                                  {isNew ? 'ðŸ†•' : (item.icon || 'ðŸ“°')}
                                </div>
                                <div style={{ flex:1, minWidth:0 }}>
                                  <div style={{ fontWeight:600, fontSize:12.5, color: isActive ? '#1d4ed8' : '#0d1829', marginBottom:3, lineHeight:1.4, fontFamily:font, transition:'color 0.3s' }}>
                                    {item.title}
                                  </div>
                                  <div style={{ fontSize:11, color:'#64748b', marginBottom:4, fontFamily:font, lineHeight:1.5 }}>{item.summary}</div>
                                  <div style={{ display:'flex', alignItems:'center', gap:6 }}>
                                    <span style={{ fontSize:10, color:'#94a3b8' }}>{item.source}</span>
                                    <span style={{ width:3, height:3, borderRadius:'50%', background:'#cbd5e1', display:'inline-block' }}/>
                                    <span style={{ fontSize:10, color:'#94a3b8' }}>{item.time}</span>
                                    {item.url && <span style={{ fontSize:10, color:'#3b82f6' }}>â†—</span>}
                                  </div>
                                </div>
                                {item.impact && (
                                  <span style={{ background:'linear-gradient(135deg,#dcfce7,#bbf7d0)', color:'#15803d', borderRadius:20, padding:'2px 8px', fontSize:9.5, fontWeight:700, whiteSpace:'nowrap', flexShrink:0, border:'1px solid #86efac' }}>
                                    {item.impact}
                                  </span>
                                )}
                              </div>
                            );
                          })
                        )}
                      </div>

                      {/* Footer: dots navigator + next refresh */}
                      {isLive && displayFeed.length > 0 && (
                        <div style={{ padding:'8px 18px', borderTop:'1px solid #f1f5f9', display:'flex', alignItems:'center', justifyContent:'space-between', background:'#fafbff' }}>
                          <div style={{ display:'flex', gap:5, alignItems:'center' }}>
                            {displayFeed.map((_, i) => (
                              <button key={i} onClick={() => setActiveNewsIdx(i)}
                                style={{ width: i===activeNewsIdx ? 16 : 6, height:6, borderRadius:10, background: i===activeNewsIdx ? '#3b82f6' : '#cbd5e1', border:'none', padding:0, cursor:'pointer', transition:'all 0.3s ease' }}/>
                            ))}
                          </div>
                          <span style={{ fontSize:9.5, color:'#cbd5e1' }}>
                            {t.feedAutoRefresh} Â· {mins}:{String(secs).padStart(2,'0')}
                          </span>
                        </div>
                      )}
                    </div>

                    {/* â”€â”€ WHO Health Alerts â”€â”€ */}
                    <div style={{ background:'white', borderRadius:14, border:'1px solid #edf2f7', display:'flex', flexDirection:'column', overflow:'hidden' }}>
                      {/* Header */}
                      <div style={{ display:'flex', alignItems:'center', justifyContent:'space-between', padding:'14px 18px 10px', borderBottom:'1px solid #f1f5f9' }}>
                        <div style={{ display:'flex', alignItems:'center', gap:10 }}>
                          <div style={{ width:32, height:32, borderRadius:9, background:'linear-gradient(135deg,#dc2626,#ef4444)', display:'flex', alignItems:'center', justifyContent:'center', fontSize:15, flexShrink:0 }}>ðŸ¥</div>
                          <div>
                            <div style={{ fontWeight:700, fontSize:13.5, color:'#0d1829', fontFamily:font, display:'flex', alignItems:'center', gap:7 }}>
                              {t.whoAlerts}
                              {isLive && (
                                <span style={{ display:'inline-flex', alignItems:'center', gap:3, fontSize:9, color:'#dc2626', fontWeight:700, background:'#fef2f2', padding:'2px 7px', borderRadius:20 }}>
                                  <span style={{ width:5, height:5, borderRadius:'50%', background:'#dc2626', display:'inline-block', animation:'pulse 2s infinite' }}/>
                                  LIVE
                                </span>
                              )}
                            </div>
                            {isLive && (
                              <div style={{ fontSize:10, color:'#94a3b8', marginTop:1 }}>
                                {t.feedWhoSource}
                              </div>
                            )}
                          </div>
                        </div>
                        {/* same countdown for WHO */}
                        {isLive && (
                          <div style={{ display:'flex', flexDirection:'column', alignItems:'center', gap:2 }}>
                            <div style={{ fontSize:9, color:'#94a3b8', whiteSpace:'nowrap' }}>
                              {t.feedNextIn} {mins}:{String(secs).padStart(2,'0')}
                            </div>
                            <div style={{ width:52, height:3, background:'#e2e8f0', borderRadius:10, overflow:'hidden' }}>
                              <div style={{ height:'100%', width:`${countdownPct}%`, background:'linear-gradient(90deg,#ef4444,#f87171)', borderRadius:10, transition:'width 1s linear' }}/>
                            </div>
                          </div>
                        )}
                      </div>

                      {/* WHO items */}
                      <div style={{ padding:'0 20px', flex:1 }}>
                        {feedLoading && !liveFeed ? (
                          [0,1].map(i => (
                            <div key={i} style={{ padding:'16px 0', borderBottom: i < 1 ? '1px solid #f1f5f9' : 'none' }}>
                              <div style={{ height:20, background:'#f1f5f9', borderRadius:4, width:70, marginBottom:8 }}/>
                              <div style={{ height:13, background:'#f1f5f9', borderRadius:4, marginBottom:7, width:'90%' }}/>
                              <div style={{ height:10, background:'#f1f5f9', borderRadius:4, marginBottom:9, width:'100%' }}/>
                              <div style={{ display:'flex', gap:5 }}>
                                {[60,55,70].map((w,j) => <div key={j} style={{ height:20, background:'#f1f5f9', borderRadius:6, width:w }}/>)}
                              </div>
                            </div>
                          ))
                        ) : (
                          displayWho.map((item, i) => (
                            <div key={i} style={{ padding:'13px 0', borderBottom: i < displayWho.length-1 ? '1px solid #f1f5f9' : 'none' }}>
                              <div style={{ display:'flex', alignItems:'center', justifyContent:'space-between', marginBottom:5 }}>
                                <span style={{ background: item.typeColor || '#dc2626', color:'white', borderRadius:6, padding:'2px 10px', fontSize:10.5, fontWeight:700, fontFamily:font }}>{item.type}</span>
                                {item.url && (
                                  <a href={item.url} target="_blank" rel="noopener noreferrer"
                                    style={{ fontSize:11, color:'#3b82f6', fontWeight:600, textDecoration:'none', display:'flex', alignItems:'center', gap:3 }}>
                                    {t.feedReadMore} â†—
                                  </a>
                                )}
                              </div>
                              <a href={item.url || '#'} target="_blank" rel="noopener noreferrer"
                                style={{ fontWeight:700, fontSize:13, color:'#0d1829', margin:'4px 0', fontFamily:font, display:'block', textDecoration:'none', lineHeight:1.4, cursor: item.url ? 'pointer' : 'default' }}
                                onMouseEnter={e => { if(item.url) e.currentTarget.style.color='#1d4ed8'; }}
                                onMouseLeave={e => e.currentTarget.style.color='#0d1829'}>
                                {item.title}
                              </a>
                              <div style={{ fontSize:11.5, color:'#64748b', marginBottom:7, fontFamily:font, lineHeight:1.55 }}>{item.summary}</div>
                              <div style={{ display:'flex', flexWrap:'wrap', gap:4, alignItems:'center' }}>
                                {(item.tags || []).map(tag => (
                                  <span key={tag} style={{ background:'#f1f5f9', color:'#475569', borderRadius:20, padding:'2px 9px', fontSize:10.5, fontFamily:font }}>{tag}</span>
                                ))}
                              </div>
                            </div>
                          ))
                        )}
                      </div>

                      {isLive && (
                        <div style={{ padding:'10px 20px', borderTop:'1px solid #f1f5f9' }}>
                          <span style={{ fontSize:10, color:'#cbd5e1', fontFamily:"'DM Sans',sans-serif" }}>
                            {t.feedWhoFooter}
                          </span>
                        </div>
                      )}
                    </div>
                  </div>
                  );
                })()}

                {/* Recent reports */}
                <div>
                  <div style={{ display:'flex', justifyContent:'space-between', alignItems:'center', marginBottom:14, direction:dir }}>
                    <div style={{ fontWeight:700, fontSize:17, color:'#0d1829', fontFamily:font }}>{t.recentReports}</div>
                    <button onClick={() => setPage('reports')} style={{ background:'none', border:'none', cursor:'pointer', color:'#3b82f6', fontSize:13, fontWeight:600, fontFamily:font }}>{t.viewAll}</button>
                  </div>
                  {reports.slice(0, 5).map(r => (
                    <div key={r.id} onClick={() => openDetail(r.id)}
                      style={{ background:'white', borderRadius:12, padding:'14px 18px', display:'flex', alignItems:'center', gap:14, border:'1px solid #edf2f7', cursor:'pointer', transition:'all 0.15s', marginBottom:10, direction:dir }}
                      onMouseEnter={e => { e.currentTarget.style.boxShadow='0 4px 14px rgba(0,0,0,0.09)'; e.currentTarget.style.transform=isAr?'translateX(-2px)':'translateX(2px)'; }}
                      onMouseLeave={e => { e.currentTarget.style.boxShadow='none'; e.currentTarget.style.transform='none'; }}>
                      <div style={{ width:38, height:38, borderRadius:9, background:'#eff6ff', display:'flex', alignItems:'center', justifyContent:'center', flexShrink:0, fontSize:18 }}>ðŸ“„</div>
                      <div style={{ flex:1, minWidth:0 }}>
                        <div style={{ fontWeight:600, fontSize:14, color:'#0d1829', marginBottom:3, fontFamily:font }}>{r.title || r.event_name}</div>
                        <div style={{ display:'flex', gap:14, fontSize:12, color:'#94a3b8', fontFamily:"'DM Sans',sans-serif" }}>
                          <span>ðŸ“ {r.city}, {translateCountry(r.country, lang)}</span>
                          <span>ðŸ“… {r.start_date}</span>
                          {r.ai_generated && <span style={{ color:'#3b82f6', fontWeight:600, fontSize:11 }}>{t.aiGenLabel}</span>}
                        </div>
                      </div>
                      <div style={{ display:'flex', gap:8, alignItems:'center', flexShrink:0 }}>
                        <span style={{ padding:'3px 11px', borderRadius:20, fontSize:11, fontWeight:700, background:r.status==='ready'?'#dcfce7':'#fef9c3', color:r.status==='ready'?'#15803d':'#a16207', fontFamily:font }}>
                          {r.status === 'ready' ? t.ready : t.draft}
                        </span>
                        <span style={{ color:'#94a3b8', fontSize:12 }}>{isAr ? 'â†' : 'â†’'}</span>
                      </div>
                    </div>
                  ))}
                  {reports.length === 0 && !loading && (
                    <div style={{ background:'white', borderRadius:12, padding:'36px 18px', textAlign:'center', color:'#94a3b8', border:'1px solid #edf2f7', fontSize:14, fontFamily:font }}>
                      {t.noReportsYet}
                      <button onClick={() => setPage('new')} style={{ display:'block', margin:'12px auto 0', padding:'8px 16px', background:'#0d1829', color:'white', border:'none', borderRadius:7, cursor:'pointer', fontSize:12, fontWeight:600, fontFamily:font }}>
                        {t.createFirstBtn}
                      </button>
                    </div>
                  )}
                </div>
              </>
            )}

            {/* â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â• REPORTS PAGE â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â• */}
            {page === 'reports' && (
              <ReportsPage
                reports={reports}
                lang={lang}
                t={t}
                isAr={isAr}
                dir={dir}
                setPage={setPage}
                openPreview={openPreview}
                handleDelete={handleDelete}
                openDetail={openDetail}
              />
            )}

            {/* â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â• REPORT DETAIL â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â• */}
            {page === 'detail' && selectedReport && (
              <ReportDetail
                lang={lang}
                reportId={selectedReport}
                onBack={() => setPage('reports')}
                onPDFOpen={(id, title, type) => openPreview(id, title, type)}
              />
            )}

            {/* â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â• NEW REPORT â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â• */}
            {page === 'new' && (
              <NewReportForm onSuccess={handleNewReport} onCancel={() => setPage('dashboard')} lang={lang} />
            )}

          </div>
        )}
      </div>
    </div>
  );
}
