import { useState, useEffect } from 'react';
import { getReport, getPDFUrl, getInlinePDFUrl, getPreviewUrl } from '../services/api';

const RISK_COLORS = { low:'#22c55e', medium:'#f59e0b', high:'#ef4444' };

function Section({ icon, title, children, defaultOpen = true }) {
  const [open, setOpen] = useState(defaultOpen);
  return (
    <div style={{ background:'white', borderRadius:12, border:'1px solid #e9eef5', marginBottom:14, overflow:'hidden' }}>
      <div
        onClick={() => setOpen(o => !o)}
        style={{ display:'flex', alignItems:'center', justifyContent:'space-between', padding:'14px 20px', cursor:'pointer', background:'#1c3370', userSelect:'none' }}>
        <div style={{ display:'flex', alignItems:'center', gap:10 }}>
          <span style={{ fontSize:16 }}>{icon}</span>
          <span style={{ fontWeight:700, fontSize:14.5, color:'white' }}>{title}</span>
        </div>
        <span style={{ color:'rgba(255,255,255,0.6)', fontSize:16, transform: open ? 'rotate(180deg)' : 'none', transition:'transform 0.2s' }}>â–¾</span>
      </div>
      {open && <div style={{ padding:'18px 20px' }}>{children}</div>}
    </div>
  );
}

function InfoRow({ label, value }) {
  return (
    <div style={{ display:'flex', justifyContent:'space-between', padding:'7px 0', borderBottom:'1px solid #f1f5f9', fontSize:13 }}>
      <span style={{ color:'#64748b', fontWeight:500 }}>{label}</span>
      <span style={{ color:'#0d1829', fontWeight:600, textAlign:'right', maxWidth:'60%' }}>{value || 'â€”'}</span>
    </div>
  );
}

function Tag({ children, color = '#eff6ff', text = '#3b82f6' }) {
  return (
    <span style={{ background:color, color:text, borderRadius:20, padding:'3px 12px', fontSize:11, fontWeight:700, display:'inline-block' }}>
      {children}
    </span>
  );
}

export default function ReportDetail({ reportId, onBack, onPDFOpen }) {
  const [report, setReport]   = useState(null);
  const [loading, setLoading] = useState(true);
  const [activeTab, setActiveTab] = useState('overview');

  useEffect(() => {
    getReport(reportId).then(r => { setReport(r.data); setLoading(false); }).catch(() => setLoading(false));
  }, [reportId]);

  if (loading) return (
    <div style={{ display:'flex', alignItems:'center', justifyContent:'center', height:300, gap:12, color:'#64748b' }}>
      <span style={{ animation:'spin 1s linear infinite', display:'inline-block', fontSize:20 }}>â³</span> Loading report...
      <style>{`@keyframes spin{to{transform:rotate(360deg)}}`}</style>
    </div>
  );

  if (!report) return <div style={{ padding:40, textAlign:'center', color:'#ef4444' }}>Report not found.</div>;

  const tabs = [
    { id:'overview',  label:'Overview',    icon:'ðŸ“‹' },
    { id:'agenda',    label:'Agenda',      icon:'ðŸ“…' },
    { id:'speakers',  label:'Speakers',    icon:'ðŸŽ¤' },
    { id:'meetings',  label:'Meetings',    icon:'ðŸ¤' },
    { id:'logistics', label:'Logistics',   icon:'ðŸŒ¤ï¸' },
  ];

  const tabBtn = id => ({
    padding:'8px 16px', borderRadius:8, border:'none', cursor:'pointer', fontFamily:'inherit',
    fontSize:12.5, fontWeight:activeTab === id ? 700 : 500,
    background: activeTab === id ? '#1c3370' : 'transparent',
    color: activeTab === id ? 'white' : '#64748b',
    display:'flex', alignItems:'center', gap:6, transition:'all 0.15s',
  });

  return (
    <div style={{ maxWidth:900, margin:'0 auto' }}>

      {/* Breadcrumb */}
      <div style={{ display:'flex', alignItems:'center', gap:8, marginBottom:18, fontSize:13, color:'#94a3b8' }}>
        <button onClick={onBack} style={{ background:'none', border:'none', cursor:'pointer', color:'#3b82f6', fontSize:13, fontWeight:600, padding:0 }}>
          â† Reports
        </button>
        <span>/</span>
        <span style={{ color:'#0d1829', fontWeight:600 }}>{report.title || report.event_name}</span>
      </div>

      {/* Header card */}
      <div style={{ background:'linear-gradient(135deg, #1c3370 0%, #0f2558 100%)', borderRadius:16, padding:'24px 28px', marginBottom:20, color:'white', position:'relative', overflow:'hidden' }}>
        <div style={{ position:'absolute', top:-40, right:-40, width:160, height:160, borderRadius:'50%', background:'rgba(255,255,255,0.05)' }}/>
        <div style={{ position:'absolute', bottom:-60, left:-30, width:200, height:200, borderRadius:'50%', background:'rgba(255,255,255,0.04)' }}/>

        <div style={{ display:'flex', justifyContent:'space-between', alignItems:'flex-start', position:'relative' }}>
          <div style={{ flex:1 }}>
            <div style={{ display:'flex', alignItems:'center', gap:10, marginBottom:10 }}>
              <Tag color='rgba(255,255,255,0.15)' text='white'>
                {report.status?.toUpperCase()}
              </Tag>
              <Tag color={RISK_COLORS[report.risk_level || 'low'] + '33'} text={RISK_COLORS[report.risk_level || 'low']}>
                {(report.risk_level || 'low').toUpperCase()} RISK
              </Tag>
              {report.ai_generated && <Tag color='rgba(59,130,246,0.25)' text='#93c5fd'>âœ¦ AI Generated</Tag>}
            </div>
            <h1 style={{ margin:'0 0 6px', fontSize:26, fontWeight:800, lineHeight:1.2 }}>{report.title || report.event_name}</h1>
            <div style={{ fontSize:14, color:'rgba(255,255,255,0.75)', marginBottom:14 }}>{report.report_subtitle}</div>
            <div style={{ display:'flex', flexWrap:'wrap', gap:20, fontSize:13, color:'rgba(255,255,255,0.8)' }}>
              <span>ðŸ“ {report.venue ? `${report.venue}, ` : ''}{report.city}, {report.country}</span>
              <span>ðŸ“… {report.start_date}{report.end_date ? ` â€“ ${report.end_date}` : ''}</span>
              <span>ðŸ‘¤ {report.traveler_name}</span>
            </div>
            {report.event_website && (
              <a href={report.event_website} target="_blank" rel="noreferrer"
                style={{ display:'inline-flex', alignItems:'center', gap:5, marginTop:10, color:'#93c5fd', fontSize:12.5, textDecoration:'none' }}>
                ðŸ”— Conference Website â†—
              </a>
            )}
          </div>

          {/* Action buttons */}
          <div style={{ display:'flex', gap:8, flexShrink:0, marginLeft:20 }}>
            <button
              onClick={() => onPDFOpen(reportId, report.title || report.event_name, 'html')}
              style={{ padding:'10px 16px', background:'rgba(255,255,255,0.12)', color:'white', border:'1px solid rgba(255,255,255,0.2)', borderRadius:9, fontSize:12.5, fontWeight:700, cursor:'pointer', display:'flex', alignItems:'center', gap:6 }}>
              ðŸ‘ï¸ Preview
            </button>
            <a href={getPDFUrl(reportId)} download
              style={{ padding:'10px 18px', background:'white', color:'#1c3370', borderRadius:9, fontSize:12.5, fontWeight:800, textDecoration:'none', display:'flex', alignItems:'center', gap:6, boxShadow:'0 4px 14px rgba(0,0,0,0.2)' }}>
              â¬‡ Export PDF
            </a>
          </div>
        </div>
      </div>

      {/* Tabs */}
      <div style={{ display:'flex', gap:4, marginBottom:18, background:'white', padding:'6px', borderRadius:12, border:'1px solid #e9eef5', overflowX:'auto' }}>
        {tabs.map(t => (
          <button key={t.id} onClick={() => setActiveTab(t.id)} style={tabBtn(t.id)}>
            <span>{t.icon}</span>{t.label}
          </button>
        ))}
      </div>

      {/* â”€â”€ OVERVIEW TAB â”€â”€ */}
      {activeTab === 'overview' && (
        <>
          {/* Conference data cards */}
          {report.conference_data && Object.keys(report.conference_data).length > 0 && (
            <div style={{ display:'grid', gridTemplateColumns:'1fr 1fr 1fr', gap:12, marginBottom:16 }}>
              {[
                { label:'EVENT LEADER', value:report.traveler_name, sub:report.traveler_title },
                { label:'EVENT', value:report.event_name, sub:`${report.start_date} - ${report.end_date || ''}` },
                { label:'ORGANIZATION', value:report.conference_data.organizer, icon:'ðŸ›ï¸' },
              ].map((card, i) => (
                <div key={i} style={{ background:i===0?'#1c3370':'white', borderRadius:12, padding:'18px 16px', border:'1px solid #e9eef5', textAlign:'center' }}>
                  <div style={{ fontSize:10, fontWeight:700, color:i===0?'rgba(255,255,255,0.6)':'#94a3b8', letterSpacing:'0.1em', marginBottom:10 }}>{card.label}</div>
                  {card.icon && <div style={{ fontSize:28, marginBottom:8 }}>{card.icon}</div>}
                  <div style={{ fontSize:15, fontWeight:700, color:i===0?'white':'#0d1829', lineHeight:1.3 }}>{card.value || 'â€”'}</div>
                  {card.sub && <div style={{ fontSize:11, color:i===0?'rgba(255,255,255,0.6)':'#94a3b8', marginTop:4 }}>{card.sub}</div>}
                </div>
              ))}
            </div>
          )}

          {/* Visit objectives */}
          {report.visit_objectives?.length > 0 && (
            <Section icon="ðŸŽ¯" title="Visit Objectives">
              {report.visit_objectives.map((obj, i) => (
                <div key={i} style={{ display:'flex', gap:12, alignItems:'flex-start', padding:'8px 0', borderBottom:'1px solid #f1f5f9' }}>
                  <div style={{ width:26, height:26, borderRadius:'50%', background:'#1c3370', color:'white', display:'flex', alignItems:'center', justifyContent:'center', fontSize:11, fontWeight:700, flexShrink:0 }}>{i+1}</div>
                  <span style={{ fontSize:13.5, color:'#1e293b', lineHeight:1.5, paddingTop:3 }}>{obj}</span>
                </div>
              ))}
            </Section>
          )}

          {/* Country info */}
          {report.country_info && Object.keys(report.country_info).length > 0 && (
            <Section icon="ðŸŒ" title={`About ${report.country}`}>
              {report.country_info.overview && (
                <div style={{ background:'#f8fafc', borderRadius:8, padding:'12px 15px', marginBottom:14, fontSize:13.5, color:'#374151', lineHeight:1.7, borderRight:'4px solid #1c3370' }}>
                  {report.country_info.overview}
                </div>
              )}
              <div style={{ display:'grid', gridTemplateColumns:'1fr 1fr', gap:'0 20px' }}>
                <InfoRow label="Capital"          value={report.country_info.capital} />
                <InfoRow label="Head of State"    value={`${report.country_info.head_of_state_title || ''} ${report.country_info.head_of_state || ''}`} />
                <InfoRow label="Population"       value={report.country_info.population} />
                <InfoRow label="Area"             value={report.country_info.area} />
                <InfoRow label="GDP"              value={report.country_info.gdp} />
                <InfoRow label="Currency"         value={report.country_info.currency} />
                <InfoRow label="Official Language" value={report.country_info.official_language} />
              </div>
            </Section>
          )}

          {/* Delegation */}
          {report.delegation?.length > 0 && (
            <Section icon="ðŸ‘¥" title="Official Delegation">
              <table style={{ width:'100%', borderCollapse:'collapse', fontSize:13 }}>
                <thead>
                  <tr style={{ background:'#f8fafc' }}>
                    {['#','Name','Title','Department'].map(h => (
                      <th key={h} style={{ padding:'10px 12px', textAlign:'left', fontSize:10.5, fontWeight:700, color:'#94a3b8', textTransform:'uppercase', letterSpacing:'0.07em' }}>{h}</th>
                    ))}
                  </tr>
                </thead>
                <tbody>
                  {report.delegation.map((m, i) => (
                    <tr key={i} style={{ borderBottom:'1px solid #f1f5f9' }}>
                      <td style={{ padding:'10px 12px', color:'#94a3b8', fontWeight:700 }}>{i+1}</td>
                      <td style={{ padding:'10px 12px', fontWeight:700, color:'#0d1829' }}>{m.name}</td>
                      <td style={{ padding:'10px 12px', color:'#374151' }}>{m.title}</td>
                      <td style={{ padding:'10px 12px', color:'#64748b' }}>{m.department}</td>
                    </tr>
                  ))}
                </tbody>
              </table>
            </Section>
          )}

          {/* Conference tracks */}
          {report.conference_tracks?.length > 0 && (
            <Section icon="ðŸ”·" title="Conference Tracks">
